INSERT INTO `tbfnxnarchivostxt` (`codigo`, `descripcion`, `estado`, `ejecuta`, `tipo`) VALUES
('ARCIESS', 'EJEMPLO ARCHIVO IESS', 0, 'sGsTextoinicial is string=[\r\n	dsFnxconsulta is Data Source\r\n	sConsulta is string= <-[\r\n	SELECT 1,tbfnxninit.ruc AS emp_ruc ,\r\n	tbfnxninit.sucursal AS emp_sucursal,\r\n	tbfnxnhisempleado.empcedula,\r\n	tbfnxnhisempleado.nombres, \r\n	sum(ROUND(valor,2)) as extra 	\r\n	FROM  tbfnxndetallerol, tbfnxnhisempleado,tbfnxnrubros,tbfnxninit\r\nWHERE TRIM(tbfnxnrubros.rubcodigo)=TRIM( tbfnxndetallerol.rubcodigo) AND rubiess=1 \r\nAND  tbfnxnhisempleado.empcedula= tbfnxndetallerol.empcedula \r\nAND  tbfnxndetallerol.encrolcodigo= \'%1\'\r\nAND  tbfnxnhisempleado.encrolcodigo= \'%1\'\r\nAND  tbfnxnhisempleado.emptipoemple =\'TEF\'\r\nAND valor>0 AND tbfnxninit.codigo<>\'-1\'\r\n	GROUP BY  tbfnxndetallerol.empcedula \r\n	ORDER BY  tbfnxndetallerol.empcedula\r\n<-]\r\n	sConsultaExec is string=StringBuild(sConsulta,FnxNRolCont)\r\n	IF HExecuteSQLQuery(dsFnxconsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec) THEN\r\n	FileToMemoryTable(TABLE_FnxDatos,dsFnxconsulta)\r\n	END\r\n]\r\n\r\nsGsTextoImprimir is string=[\r\n	sLcFecha is string=DateToString (gdFecha,\"AAAAMMDD\")	\r\n	FilName is string = \"C:\\fenix\\Docs\\\"+\"IESS_\"+FnxNRolCont+\"_\"+sLcFecha+\".txt\"	\r\n	\r\n	FileNum is int=FnxNCrearArchivo(FilName)\r\n	\r\n	IF FileNum <> -1 THEN		\r\n	\r\n	FOR i =TableCount(TABLE_FnxDatos) to 1 not -1			\r\n	            IF TABLE_FnxDatos[i].COL_Check=1 THEN\r\n			fWrite(FileNum,TABLE_FnxDatos[i].COL_Emp_Ruc+\";\"+TABLE_FnxDatos[i].COL_Emp_Sucursal+\";\"+Middle(sLcFecha,1,4)+\";\"+Middle(sLcFecha,5,2)+\";INS;\"...\r\n			+TABLE_FnxDatos[i].COL_Cedula+\";\"+TABLE_FnxDatos[i].COL_Extra+\";O\"+CR)			\r\n                            END			\r\n	END	\r\n	\r\n	IF ErrorOccurred THEN\r\n	Error(StringBuild(\"\",FilName),ErrorInfo())\r\n	ELSE\r\n	fClose(FileNum)		\r\n	Info(\"Proceso concluido. Se genero el archivo plano en: \"+CR+FilName)		\r\n	END\r\n	END\r\n]\r\ndfecha is Date=EDT_Fecha\r\nOpen(\"WIN_FnxNTotalIess\",FnxNRolCont,dfecha,Replace(Replace(sGsTextoinicial,\"<-[\",\"[\"),\"<-]\",\"]\"),sGsTextoImprimir)', 'I'),
('BPACIFICO', 'EJEMPLO BANCO PACIFICO', 0, 'sGsRutaArchivoTxt is string=CompleteDir(fExeDir())\r\nsLcFecha is string=DateToString (EDT_Fecha,\"AAAAMMDD\")\r\n//FilName is string = gsRutaArchivoTxt+\"INTER\"+sLcFecha+\".txt\"	\r\nFilName is string = \"C:\\fenix\\Docs\\ROLMENSUAL\\\"+\"PACIFICO_\"+FnxNRolCont+\"_\"+sLcFecha+\".txt\"	\r\nFilName2 is string = \"C:\\fenix\\Docs\\ROLMENSUAL\\\"+\"PACIFICO_\"+FnxNRolCont+\"_\"+sLcFecha+\".xls\"	\r\n\r\nFileNum is int=FnxNCrearArchivo(FilName)\r\ndsFnxconsulta is Data Source\r\n\r\nIF FileNum <> -1 THEN		\r\n	sConsulta is string=[\r\n		SELECT\r\n		\'5\'  AS tipo,\r\n		\'OCP\'  AS contrapartida,\r\n		CASE WHEN emptipocta = \'CTAVIR\' THEN \'PP\'  ELSE  \'RP\' END  AS mon,\r\n		RIGHT(CONCAT(REPLACE(SPACE(10),SPACE(1),0),TRIM(COALESCE(empctaban,0))),10)  AS pempcta,\r\n		RIGHT(CONCAT(REPLACE(SPACE(15),SPACE(1),0),FLOOR(ABS(empliquido)*100)),15)  AS valor,\r\n		RIGHT(CONCAT(REPLACE(SPACE(15),SPACE(1),0),TRIM(COALESCE(empcedula,0))),15)  AS empcedula,\r\n                                SPACE(20) AS secuencia,\r\n		\'CU\' AS cta,	\r\n		\'USD\' AS moneda\r\n		FROM\r\n		tbfnxnhisempleado\r\n		WHERE\r\n		(encrolcodigo = \'%1\')  AND LENGTH(TRIM(COALESCE(empctaban,\'\'))) <>0 AND EMPNOMBAN= \'029\' AND empliquido > 0 AND (emptipocta = \'CTAAHO\' OR emptipocta = \'CTACOR\')\r\n		\r\n	]\r\n	//AND LENGTH(TRIM(COALESCE(empctaban,\'\'))) <>0 AND EMPNOMBAN= \'029\' AND empliquido > 0 AND (emptipocta = \'CTAAHO\' OR emptipocta = \'CTACOR\')\r\n	sConsultaExec is string=StringBuild(sConsulta,FnxNRolCont)\r\n	IF HExecuteSQLQuery(dsFnxconsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec) THEN\r\n		FnxNExportarExcel(dsFnxconsulta,FilName2)\r\n		FOR EACH dsFnxconsulta			\r\n			fWrite(FileNum,dsFnxconsulta.tipo+dsFnxconsulta.contrapartida+dsFnxconsulta.mon+dsFnxconsulta.pempcta+dsFnxconsulta.valor+...\r\n			+dsFnxconsulta.empcedula+dsFnxconsulta.secuencia+dsFnxconsulta.cta+dsFnxconsulta.moneda+CR)			\r\n		END	\r\n	END\r\n	\r\n	IF ErrorOccurred THEN\r\n		Error(StringBuild(\"\",FilName),ErrorInfo())\r\n	ELSE\r\n		fClose(FileNum)		\r\n		Info(\"Proceso concluido. Se genero el archivo plano en: \"+CR+FilName)		\r\n	END\r\nEND\r\n\r\nFilName2= \"C:\\fenix\\Docs\\ROLMENSUAL\\\"+\"PACIFICO_\"+FnxNRolCont+\"_\"+sLcFecha+\".xls\"\r\n	\r\n	sConsulta is string=[\r\n		SELECT CASE WHEN EmpFormaPago= \"E\" THEN \"Efectivo\" ELSE \"Bancario\" END	AS Pago, \r\n		CASE WHEN emptipocta=\"E\" THEN SPACE(20) ELSE emptipocta END	AS Tipo_cta, \r\n		nombres												AS Nombres, \r\n		DATE_FORMAT(empfecing,\'%d/%m/%Y\')										AS fecha, \r\n		\"5\"													AS Localidad, \r\n		\"OCP\"												AS Transaccion, \r\n		CASE WHEN emptipocta = \"CTAVIR\" THEN \"PP\" ELSE \"RP\" END				AS CodServ, \r\n		CASE WHEN emptipocta = \"CTACOR\" THEN \"00\" ELSE \"10\" END				AS TipCta, \r\n		RIGHT(CONCAT(REPLACE(SPACE(10),SPACE(1),0),TRIM(COALESCE(empctaban,0))),10) AS NroCta,\r\n		empliquido											AS Valor, \r\n		empcedula											AS Identi, \r\n		SPACE(20) AS secuencia,\r\n		\"CU\"												AS FormaPago, \r\n		\"USD\"												AS Moneda \r\n                                 FROM tbfnxnhisempleado \r\n                                WHERE encrolcodigo = \'%1\' AND EMPNOMBAN= \"029\" \r\n                               ORDER BY empformapago,emptipocta, nombres		\r\n	]\r\n	\r\n	sConsultaExec is string =StringBuild(sConsulta,FnxNRolCont)\r\n	IF HExecuteSQLQuery(dsFnxconsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec) THEN\r\n		FnxNExportarExcel(dsFnxconsulta,FilName2)\r\n		\r\n	END\r\n	\r\n	IF ErrorOccurred THEN\r\n		Error(StringBuild(\"\",\"\"),ErrorInfo())\r\n	ELSE		\r\n		Info(\"Proceso concluido. Se genero el excel en: \"+CR+FilName2)		\r\n	END\r\n\r\n\r\n', 'B'),
