n	END\r\n	\r\n	IF ErrorOccurred THEN\r\n		Error(StringBuild(\"\",FilName),ErrorInfo())\r\n	ELSE\r\n		fClose(FileNum)		\r\n		Info(\"Proceso concluido. Se genero el archivo plano en: \"+CR+FilName)		\r\n	END\r\nEND\r\n\r\n\r\nFilName2= \"C:\\fenix\\Docs\\ArchivosUtilidad\\\"+\"Utilidad_PACIFICO_\"+FAnio+\"_\"+sLcFecha+\".xls\"\r\n	\r\n	sConsulta is string=[\r\n			SELECT CASE WHEN EmpFormaPago= \"E\" THEN \"Efectivo\" ELSE \"Bancario\" END	AS Pago, \r\n		CASE WHEN emptipocta=\"E\" THEN SPACE(20) ELSE emptipocta END	AS Tipo_cta, \r\n		nombres												AS Nombres, \r\n		DATE_FORMAT(empfecing,\'%d/%m/%Y\')										AS fecha, \r\n		\"5\"													AS Localidad, \r\n		\"OCP\"												AS Transaccion, \r\n		CASE WHEN emptipocta = \"CTAVIR\" THEN \"PP\" ELSE \"RP\" END				AS CodServ, \r\n		CASE WHEN emptipocta = \"CTACOR\" THEN \"00\" ELSE \"10\" END				AS TipCta, \r\n		RIGHT(CONCAT(REPLACE(SPACE(10),SPACE(1),0),TRIM(COALESCE(empctaban,0))),10) AS NroCta,\r\n		total											AS Valor, \r\n		empcedula											AS Identi, \r\n		SPACE(20) AS secuencia,\r\n		\"CU\"												AS FormaPago, \r\n		\"USD\"												AS Moneda \r\n		 FROM tbfnxnutilidad \r\n		WHERE anio = \'%1\' AND EMPNOMBAN= \"029\" \r\n	 ORDER BY empformapago,emptipocta, nombres		\r\n	]\r\n	\r\n	sConsultaExec is string =StringBuild(sConsulta,FAnio)\r\n	IF HExecuteSQLQuery(dsFnxconsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec) THEN\r\n		FnxNExportarExcel(dsFnxconsulta,FilName2)\r\n		\r\n	END\r\n	\r\n	IF ErrorOccurred THEN\r\n		Error(StringBuild(\"\",\"\"),ErrorInfo())\r\n	ELSE		\r\n		Info(\"Proceso concluido. Se genero el excel en: \"+CR+FilName2)		\r\n	END\r\n\r\n\r\n', 'B'),
('UTLIBPICH', 'EJEMPLO BANCO PICHINCHA UTILIDAD', 0, 'sGsRutaArchivoTxt is string=CompleteDir(fExeDir())\r\nsLcFecha is string=DateToString (EDT_Fecha,\"AAAAMMDD\")\r\nFAnio is string=\"2017\"\r\n//FilName is string = gsRutaArchivoTxt+\"INTER\"+sLcFecha+\".txt\"	\r\nFilName is string = \"C:\\fenix\\Docs\\ArchivosUtilidad\\\"+\"Utilidad_Pichincha_\"+FAnio+\"_\"+sLcFecha+\".txt\"	\r\nFilName2 is string = \"C:\\fenix\\Docs\\ArchivosUtilidad\\\"+\"Utilidad_Pichincha_\"+FAnio+\"_\"+sLcFecha+\".xls\"	\r\n	\r\nFileNum is int=FnxNCrearArchivo(FilName)\r\ndsFnxconsulta is Data Source\r\n\r\nIF FileNum <> -1 THEN		\r\n	sConsulta is string=[\r\n		SELECT\r\n		\'PA\'  AS tipo,\r\n		REPLACE(SPACE(20),SPACE(1),0) AS secuencia,\r\n		\'USD\'  AS moneda,\r\n		RIGHT(CONCAT(REPLACE(SPACE(13),SPACE(1),0),FLOOR(ABS(total)*100)),13) AS valor,\r\n		REPLACE(TRIM(emptipocta),\'CTACOR\',\'CTACTE\')  AS cuenta,	\r\n		RIGHT(CONCAT(REPLACE(SPACE(20),SPACE(1),0),RIGHT(TRIM(COALESCE(empctaban,0)),10)),20) AS cuentaproveedor,		\r\n		LEFT(CONCAT(\'PAGO UTILIDAD\',SPACE(40)),40) AS tipop,\r\n		\'C\'  AS tipoc,\r\n		LEFT(CONCAT(TRIM(empcedula),SPACE(14)),14) AS cedula,\r\n		LEFT(CONCAT(nombres,SPACE(41)),41) AS nombres\r\n		FROM\r\n		tbfnxnutilidad\r\n		WHERE\r\n		(anio = \'%1\' AND empnomban = \'10\' AND total > 0) \r\n		AND (emptipocta = \'CTAAHO\' OR emptipocta = \'CTACOR\'  OR emptipocta = \'CTAVIR\' )\r\n		ORDER BY\r\n		emptipocta, nombres \r\n	]\r\n	sConsultaExec is string=StringBuild(sConsulta,FAnio)\r\n	IF HExecuteSQLQuery(dsFnxconsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec) THEN\r\n//FnxNExportarExcel(dsFnxconsulta,sFilName2)\r\n		nSecuen is int=1		\r\n		FOR EACH dsFnxconsulta			\r\n			fWrite(FileNum,dsFnxconsulta.tipo+Right(dsFnxconsulta.secuencia+nSecuen,20)+dsFnxconsulta.moneda+dsFnxconsulta.valor+dsFnxconsulta.cuenta+dsFnxconsulta.cuentaproveedor...\r\n			+dsFnxconsulta.tipop+dsFnxconsulta.tipoc+dsFnxconsulta.cedula+dsFnxconsulta.nombres+CR)\r\n			nSecuen++\r\n		END\r\n					\r\n	END\r\n		\r\n	IF ErrorOccurred THEN\r\n		Error(StringBuild(\"\",FilName),ErrorInfo())\r\n	ELSE\r\n		fClose(FileNum)		\r\n	Info(\"Proceso concluido. Se genero el archivo plano en: \"+CR+FilName)		\r\n	END\r\nEND\r\n\r\n\r\n\r\n//FilName2= \"C:\\fenix\\Docs\\ROLMENSUAL\\\"+\"PACIFICO_\"+FnxNRolCont+\"_\"+sLcFecha+\".xls\"\r\n	\r\n	sConsulta is string=[\r\n		SELECT CASE WHEN EmpFormaPago= \"E\" THEN \"Efectivo\" ELSE \"Bancario\" END	AS Pago, \r\n		CASE WHEN emptipocta=\"E\" THEN SPACE(20) ELSE emptipocta END	AS Tipo_cta, \r\n		nombres												AS Nombres, \r\n		DATE_FORMAT(empfecing,\'%d/%m/%Y\')										AS fecha, \r\n		\"5\"													AS Localidad, \r\n		\"OCP\"												AS Transaccion, \r\n		CASE WHEN emptipocta = \"CTAVIR\" THEN \"PP\" ELSE \"RP\" END				AS CodServ, \r\n		CASE WHEN emptipocta = \"CTACOR\" THEN \"00\" ELSE \"10\" END				AS TipCta, \r\n		RIGHT(CONCAT(REPLACE(SPACE(10),SPACE(1),0),TRIM(COALESCE(empctaban,0))),10) AS NroCta,\r\n		total											AS Valor, \r\n		empcedula											AS Identi, \r\n		SPACE(20) AS secuencia,\r\n		\"CU\"												AS FormaPago, \r\n		\"USD\"												AS Moneda \r\n   FROM tbfnxnutilidad \r\n    WHERE\r\n		(anio = \'%1\' AND empnomban = \'10\' AND total> 0) \r\n		AND (emptipocta = \'CTAAHO\' OR emptipocta = \'CTACOR\'  OR emptipocta = \'CTAVIR\' )\r\n    ORDER BY empformapago,emptipocta, nombres		\r\n	]\r\n	\r\n	sConsultaExec is string =StringBuild(sConsulta,FAnio)\r\n	IF HExecuteSQLQuery(dsFnxconsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec) THEN\r\n		FnxNExportarExcel(dsFnxconsulta,FilName2)\r\n		\r\n	END\r\n	\r\n	IF ErrorOccurred THEN\r\n		Error(StringBuild(\"\",\"\"),ErrorInfo())\r\n	ELSE		\r\n		Info(\"Proceso concluido. Se genero el excel en: \"+CR+FilName2)		\r\n	END\r\n\r\n\r\n\r\n', 'B');
