('RPEMAIL', 'ROL_EMAIL', 'REPORTE ENVIO EMAIL', '', 'bCorreto is boolean=False\r\nsEmpeadosnoenviado is string = \"\"\r\n_spdatosempresa()\r\nCc is string =_spEnviodecopias()\r\nlsEmisior is string = [\r\n	SELECT\r\n	tbfnxrcuentamail.id,\r\n	tbfnxrcuentamail.usuario,\r\n	tbfnxrcuentamail.correo,\r\n	tbfnxrcuentamail.`password`,\r\n	tbfnxrcuentamail.puerto,\r\n	tbfnxrcuentamail.smtp,\r\n	tbfnxrcuentamail.opcion,\r\n	tbfnxrcuentamail.firma,\r\n	tbfnxrcuentamail.etiqueta,\r\n	tbfnxrcuentamail.imagen,\r\n	tbfnxrcuentamail.dir\r\n	FROM tbfnxrcuentamail\r\n	WHERE tbfnxrcuentamail.activo=1\r\n]\r\nlqEmisor is Data Source \r\nloTablaCEsmisor is SQLTable(\"\",\"\",MySqlNomina..Name,1)\r\nIF not loTablaCEsmisor.runString(lsEmisior,lqEmisor) THEN\r\n	pgUti_Mess(\"SE HA PRODUCIDO UN ERROR AL CONSULTAR LOS DATOS\", \"Error\",Detener)\r\nELSE\r\n	HReadFirst(lqEmisor)\r\n	FOR EACH ROW OF grdEmail\r\n		IF grdEmail.COL_marca = True THEN\r\n			IF NoSpace(grdEmail.COL_correo) <> \"\" THEN\r\n			sLsalis is string = gsNombreempresa\r\n			sLsusuario is string = lqEmisor.correo\r\n			sLspassword is string = lqEmisor.password\r\n			sLssmtp is string =lqEmisor.smtp\r\n			sLspuerto is string =lqEmisor.puerto\r\n			sLscorreo is string = grdEmail.COL_correo+ \";\" + Cc\r\n			sLsalias is string	= grdEmail.COL_nombre+ \";\"\r\n			sLsasunto is string =STC_Text\r\n			lsbody is string =lqEmisor.etiqueta\r\n				arrArraybody is array of strings\r\n				sLsCuerpo is string \r\n				StringToArray(lsbody, arrArraybody , CR)\r\n				FOR EACH x OF arrArraybody\r\n					sLsCuerpo = sLsCuerpo + x + \"<br>\"\r\n				END\r\n				\r\n			sLsfirma is string =lqEmisor.dir\r\n			arrArrayFirma is array of strings\r\n			sLsMessage is string \r\n			StringToArray(sLsfirma, arrArrayFirma , CR)\r\n			FOR EACH x OF arrArrayFirma\r\n				sLsMessage = sLsMessage + x + \"<br>\"\r\n			END\r\n		\r\n			lsMensaje  is string = [\r\n					<html>\r\n					<h1>%1</h1>\r\n					<p>%2 </p>\r\n					<br>\r\n					<p>%3</p>\r\n					</html>\r\n				]\r\n			lsMensaje = StringBuild(lsMensaje,lqEmisor.firma,sLsCuerpo,sLsMessage)\r\n			sLsbody1 is string = lsMensaje\r\n			//Archivos Adjuntos\r\n				MyUniqueFile is string = fDataDir() + \"\\\" + DateSys() + TimeSys() + \".pdf\"\r\n				lsQryint is string =[\r\n					SELECT \r\n						ctbldataemail.empfecing AS empfecing1,	\r\n						ctbldataemail.empfecsal AS enmpfecsal1,	\r\n						ctbldataemail.empsalario AS empsalario1,	\r\n						ctbldataemail.empdiastrab AS empdiastrab1,	\r\n						ctbldataemail.tipo_remuneracion  AS tipo_remuneracio1,	\r\n						ctbldataemail.encroldescrip AS encroldescrip1,	\r\n						ctbldataemail.encrolcodigo AS rol1,	\r\n						ctbldataemail.orden AS Orden1,	\r\n						ctbldataemail.empapeuno AS empapeuno1,	\r\n						ctbldataemail.empapedos AS empapedos1,	\r\n						ctbldataemail.empnomuno AS empnomuno1,	\r\n						ctbldataemail.empnomdos AS empnomdos1,	\r\n						ctbldataemail.empcedula AS empcedula1,	\r\n						ctbldataemail.rubdescrip AS rubdescrip1,	\r\n						ctbldataemail.empliquido AS empliquido1,	\r\n						ctbldataemail.emptotaling AS emprtotaling1,	\r\n						ctbldataemail.emptotalegr AS emptotalegr1,	\r\n						ctbldataemail.valor AS valor1,	\r\n						ctbldataemail.tipo AS tipo1,	\r\n						ctbldataemail.rubcodigo AS rubcodigo1,	\r\n						ctbldataemail.fecha AS fecha1,	\r\n						ctbldataemail.empcodigo AS empcodigo1,	\r\n						ctbldataemail.encrolcodigo AS emprolcodigo1,	\r\n						ctbldataemail.grupo AS grupo1,	\r\n						ctbldataemail.Nombres AS Nombres1,	\r\n						ctbldataemail.rubtipo AS rubtipo1,	\r\n						ctbldataemail.ruborden AS ruborden1,	\r\n						ctbldataemail.empmail AS email1\r\n					FROM \r\n						ctbldataemail\r\n					WHERE \r\n						ctbldataemail.valor <> 0\r\n						AND	ctbldataemail.empcedula =\'%1\'\r\n					ORDER by \r\n						rol1 Asc,	\r\n						grupo1 Asc,	\r\n						Orden1 Asc,	\r\n						tipo1 DESC,	\r\n						empcedula1 Asc\r\n					\r\n				]\r\n				lsQryint= StringBuild(lsQryint,grdEmail.COL_cedula)\r\n				iDestination(iPDF, MyUniqueFile)\r\n				iPrintReport(\"RPT_rolindividuales\",grdEmail.COL_cedula,gsNombreempresa,gsRucid,gsDireccion,gsTelefo,gsImagen,lsQryint)\r\n				sFile is string\r\n				sFile = MyUniqueFile+ \";\"\r\n				FnxCorreo.envioemail(sLsalis,sLsusuario,sLspassword,sLssmtp,sLspuerto,sLscorreo,sLsalias,sLsasunto,sFile,sLsbody1)\r\n				fDelete(MyUniqueFile)\r\n			END\r\n		END \r\n	END		\r\nEND\r\nRESULT bCorreto', '', '1', 'VFP', 'PUBLIC gsNombreempresa,gsDirecciongsRucid,gsTelefo,gsImagen,Lsalis,Lsusuario,Lspassword,Lssmtp,Lspuerto,Lscorreo,Lsalias,Lsasunto\r\nlsCopia = \"\"\r\nLsCuerpo = \"\"\r\nLsMessage = \"\"\r\nLsbody1 = \"\"\r\nMyUniqueFile = \"\" \r\n\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT tbfnxnhisempleado.empfecing,\r\n	empfecsal,\r\n	empdepar,\r\n	empsalario,\r\n	empdiastrab,\r\n	emptiporemun as tipo_remuneracion,\r\n	encroldescrip,\r\n	tbfnxndetallerol.encrolcodigo as rol,\r\n	empcodigo as orden,\r\n	empapeuno,\r\n	empapedos,\r\n	empnomuno,\r\n	empnomdos,\r\n	tbfnxnhisempleado.empcedula,\r\n	rubdescrip,\r\n	empliquido,\r\n	emptotaling,\r\n	emptotalegr,\r\n	valor,\r\n	tbfnxnhisrubros.rubtipo as tipo,\r\n	tbfnxnhisrubros.rubcodigo,\r\n	tbfnxnencabezadorol.fecha,\r\n	tbfnxnhisempleado.empcodigo,\r\n	tbfnxndetallerol.encrolcodigo,\r\n	depnombre as grupo,\r\n	tbfnxnhisempleado.nombres,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.ruborden,\r\n	tbfnxnhisempleado.empmail\r\n	FROM tbfnxncurselemp, tbfnxndetallerol\r\n	INNER JOIN tbfnxnencabezadorol\r\n	on tbfnxndetallerol.encrolcodigo = tbfnxnencabezadorol.encrolcodigo \r\n	LEFT JOIN tbfnxnhisrubros\r\n	ON tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo\r\n	left join tbfnxnhisempleado \r\n	on tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\r\n	LEFT JOIN tbfnxndepartamento \r\n	ON tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\r\n	WHERE \r\n	tbfnxnhisrubros.rubprovision=false\r\n	AND tbfnxndetallerol.encrolcodigo=\'<<oAppPrm.frmprm.Codigorol>>\'\r\n	AND tbfnxnhisrubros.encrolcodigo=\'<<oAppPrm.frmprm.Codigorol>>\'\r\n	AND tbfnxnhisempleado.encrolcodigo=\'<<oAppPrm.frmprm.Codigorol>>\'\r\n	and tbfnxnencabezadorol.encrolcodigo=\'<<oAppPrm.frmprm.Codigorol>>\'\r\n	and tbfnxnhisempleado.empcedula = tbfnxncurselemp.empcedula\r\n	ORDER BY tbfnxndetallerol.encrolcodigo,depnombre,empcodigo ,tbfnxnhisrubros.rubtipo desc,tbfnxnhisrubros.ruborden\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"ctbldataemail\")\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT  tbfnxninit.nombre,\r\n		tbfnxninit.direccion,\r\n		tbfnxninit.ruc,\r\n		tbfnxninit.telefono,\r\n		tbfnxninit.logo\r\n	FROM tbfnxninit\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"vQRY\")\r\n\r\ngsNombreempresa=vQRY.nombre\r\ngsDireccion=vQRY.direccion\r\ngsRucid=vQRY.ruc\r\ngsTelefo=vQRY.telefono\r\ngsImagen=vQRY.logo\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT\r\n		tbfnxrcuentamail.id,\r\n		tbfnxrcuentamail.usuario,\r\n		tbfnxrcuentamail.correo,\r\n		tbfnxrcuentamail.`password`,\r\n		tbfnxrcuentamail.puerto,\r\n		tbfnxrcuentamail.smtp,\r\n		tbfnxrcuentamail.opcion,\r\n		cast(tbfnxrcuentamail.firma as CHAR(250)) as firma,\r\n		cast(tbfnxrcuentamail.etiqueta as CHAR(250)) as etiqueta,\r\n		tbfnxrcuentamail.imagen,\r\n		cast(	tbfnxrcuentamail.dir as CHAR(250)) as dir\r\n	FROM tbfnxrcuentamail\r\n	WHERE tbfnxrcuentamail.activo=1\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"lqEmisor\")\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT\r\n	tbfnxrcopiaemail.id,\r\n	cast(	tbfnxrcopiaemail.nombre as CHAR(250) ) as nombre,\r\n	cast(tbfnxrcopiaemail.correo as CHAR(250) ) as correo,\r\n	tbfnxrcopiaemail.envio\r\n	FROM tbfnxrcopiaemail\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"lqCorreoCopia\")\r\n\r\nSELECT  lqCorreoCopia\r\nSCAN\r\n	IF lqCorreoCopia.envio = 1 THEN \r\n		lstrue= .T.\r\n	ELSE \r\n		lstrue= .F.\r\n	ENDIF \r\n	IF lstrue = .T. THEN \r\n			lsCopia = lsCopia+ alltrim(lqCorreoCopia.correo) +\";\"\r\n	ENDIF \r\nENDSCAN \r\n\r\nSELECT empcedula,Nombres,empmail, emptotaling, emptotalegr,empliquido,encrolcodigo,empcodigo  FROM ctbldataemail ;\r\nGROUP BY  Nombres,empmail, emptotaling, emptotalegr,empliquido,encrolcodigo,empcodigo,empcedula INTO CURSOR curdata\r\n\r\nSELECT curdata\r\nSCAN\r\n	\r\n	IF !EMPTY(curdata.empmail) THEN \r\n			Lsalis 			= gsNombreempresa\r\n			Lsusuario 	 = lqEmisor.correo\r\n			Lspassword 	= lqEmisor.password\r\n			Lssmtp 			=lqEmisor.smtp\r\n			Lspuerto  	=lqEmisor.puerto\r\n			Lscorreo	 	= curdata.empmail+ \";\" + lsCopia\r\n			Lsalias 		= curdata.nombres+ \";\"\r\n			Lsasunto 		= oAppPrm.frmprm.asunto\r\n			lsbody  		= lqEmisor.etiqueta\r\n			ALINES(arrArraybody,lsbody,CHR(13))\r\n			lnbody = ALEN(arrArraybody)\r\n			FOR i=1 TO lnbody\r\n				LsCuerpo = LsCuerpo + arrArraybody[i] + \"<br>\"\r\n			ENDFOR\r\n			Lsfirma 		= lqEmisor.dir\r\n			ALINES(arrArrayFirma,Lsfirma,CHR(13))\r\n			lnfirma = ALEN(arrArrayFirma)\r\n			FOR i=1 TO lnfirma\r\n				LsMessage = LsMessage + arrArrayFirma[i] + \"<br>\"\r\n			ENDFOR\r\n			TEXT TO DocumentoXML  NOSHOW TEXT PRETEXT 7\r\n					<html>\r\n					<h1> <<lqEmisor.firma>></h1>\r\n					<p><<LsCuerpo>></p>\r\n					<br>\r\n					<p><<LsMessage>></p>\r\n					</html>\r\n			ENDTEXT\r\n					Lsbody1 = DocumentoXML\r\n**************************************************************************\r\n**************************************************************************\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n					SELECT DISTINCT\r\n					tbfnxnhisempleado.encrolcodigo as rol,\r\n					tbfnxnhisempleado.empfecing,\r\n					tbfnxnhisempleado.empfecsal,\r\n					tbfnxnhisempleado.empdepar,\r\n					tbfnxnhisempleado.empsalario,\r\n					tbfnxnhisempleado.empdiastrab,\r\n					tbfnxnhisempleado.emptiporemun AS tipo_remuneracion,\r\n					tbfnxnencabezadorol.encroldescrip,\r\n					empcodigo AS orden,\r\n					tbfnxnhisempleado.empapeuno,\r\n					tbfnxnhisempleado.empapedos,\r\n					tbfnxnhisempleado.empnomuno,\r\n					tbfnxnhisempleado.empnomdos,\r\n					tbfnxnhisempleado.empcedula,\r\n					tbfnxnhisempleado.empliquido,\r\n					tbfnxnhisempleado.emptotaling,\r\n					tbfnxnhisempleado.emptotalegr,\r\n					tbfnxnencabezadorol.fecha,\r\n					tbfnxnhisempleado.empcodigo,\r\n					tbfnxndepartamento.depnombre AS grupo,\r\n					tbfnxnhisempleado.nombres,\r\n					tbfnxnhisempleado.empmail,\r\n					tbfnxnhisempleado.empcargo,\r\n					tbfnxncargo.cardescrip,\r\n					tbfnxnhisempleado.emptipopersonal\r\n				FROM\r\n					tbfnxnhisempleado ,\r\n					tbfnxndepartamento ,\r\n					tbfnxnencabezadorol ,\r\n					tbfnxncurselemp ,\r\n					tbfnxncargo,\r\n					tbfnxnpersonal\r\n				WHERE\r\n					tbfnxnhisempleado.empdepar 				= tbfnxndepartamento.depcodigo AND\r\n					tbfnxncargo.carcodigo 						= tbfnxnhisempleado.empcargo AND\r\n					tbfnxncargo.percodigo			 				=  tbfnxnhisempleado.emptipopersonal  AND \r\n					tbfnxnhisempleado.encrolcodigo 		= \'<<oAppPrm.frmprm.Codigorol>>\' AND\r\n					tbfnxnencabezadorol.encrolcodigo	= \'<<oAppPrm.frmprm.Codigorol>>\' AND\r\n					tbfnxnhisempleado.empcedula 			= tbfnxncurselemp.Empcedula AND \r\n					tbfnxnhisempleado.empdepar				=tbfnxndepartamento.depcodigo\r\n				ORDER BY\r\n					tbfnxndepartamento.depnombre ASC,\r\n					empcodigo ASC\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"qResultnrolHorizontal\")\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT DISTINCT \r\n				tbfnxndetallerol.encrolcodigo,tbfnxndetallerol.empcedula,\r\n				tbfnxnhisrubros.rubtipo AS tipo,\r\n				CASE WHEN  tbfnxnhisrubros.rubtipo= \'I\' THEN rubdescrip ELSE \'\' END  AS rubdescrip,\r\n				CASE WHEN  tbfnxnhisrubros.rubtipo= \'I\' THEN tbfnxndetallerol.rubcodigo ELSE \'\' END AS rubcodigo,\r\n				CASE WHEN  tbfnxnhisrubros.rubtipo= \'I\' THEN ROUND(valor,2) ELSE 0 END AS valor,\r\n				tbfnxnhisrubros.rubtipo,tbfnxnhisrubros.ruborden\r\n				FROM  tbfnxndetallerol,tbfnxnhisrubros,tbfnxncurselemp\r\n				WHERE  tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo\r\n				AND tbfnxnhisrubros.rubprovision=0\r\n				AND tbfnxndetallerol.encrolcodigo=\'<<oAppPrm.frmprm.Codigorol>>\'\r\n				AND tbfnxnhisrubros.encrolcodigo= \'<<oAppPrm.frmprm.Codigorol>>\'\r\n				AND tbfnxndetallerol.empcedula = tbfnxncurselemp.Empcedula\r\n				AND valor <> 0\r\n				AND tbfnxndetallerol.rubcodigo <> \'SALARIO\'\r\n				AND tbfnxnhisrubros.rubtipo=\'I\'\r\n				ORDER BY tbfnxndetallerol.encrolcodigo,tbfnxndetallerol.empcedula ,rubtipo DESC,ruborden \r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"qResultningreso\")\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT DISTINCT \r\n				tbfnxndetallerol.encrolcodigo,tbfnxndetallerol.empcedula,\r\n				tbfnxnhisrubros.rubtipo AS tipo,\r\n				CASE WHEN  tbfnxnhisrubros.rubtipo  = \'E\' THEN rubdescrip ELSE \'\' END  AS rubdescrip,\r\n				CASE WHEN  tbfnxnhisrubros.rubtipo  = \'E\' THEN tbfnxndetallerol.rubcodigo ELSE \'\' END AS rubcodigo,\r\n				CASE WHEN  tbfnxnhisrubros.rubtipo  = \'E\' THEN ROUND(valor,2) ELSE 0 END AS valor,\r\n				tbfnxnhisrubros.rubtipo,tbfnxnhisrubros.ruborden\r\n				FROM  tbfnxndetallerol,tbfnxnhisrubros,tbfnxncurselemp\r\n				WHERE  tbfnxndetallerol.rubcodigo	  =tbfnxnhisrubros.rubcodigo\r\n				AND tbfnxnhisrubros.rubprovision	  =0\r\n				AND tbfnxndetallerol.encrolcodigo	  =\'<<oAppPrm.frmprm.Codigorol>>\'\r\n				AND tbfnxnhisrubros.encrolcodigo		= \'<<oAppPrm.frmprm.Codigorol>>\'\r\n				AND tbfnxndetallerol.empcedula 			= tbfnxncurselemp.Empcedula\r\n				AND tbfnxndetallerol.valor<>0\r\n				AND tbfnxnhisrubros.rubtipo					=\'E\'\r\n				ORDER BY tbfnxndetallerol.encrolcodigo,tbfnxndetallerol.empcedula ,rubtipo DESC,ruborden \r\nENDTEXT\r\n\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"qResultnegresos\")\r\n\r\n* Programacion para los ingresos y egresos\r\n\r\nSET ENGINEBEHAVIOR 70\r\n\r\nUseTable(\"tbfnxninit\") \r\nSELECT nombre, direccion, telefono, ruc, logo from tbfnxninit   INTO CURSOR qemp\r\n\r\nSELECT * from qResultnrolHorizontal INTO CURSOR qCurRol READWRITE \r\n\r\nCREATE CURSOR cur1(cedula1 c(10),rubcodigoi c(10),rubdescripi c(30),valori n(20,4),rubcodigoe c(10),rubdescripe c(30),valore n(20,4),ordenrub n(5))\r\ncampo=0\r\nj=1\r\n\r\nSELECT qCurRol\r\nSCAN\r\nSELECT * from qResultningreso WHERE ALLTRIM(empcedula) = ALLTRIM(qCurRol.empcedula)  INTO CURSOR CIng\r\n	camposing=reccount(\"CIng\")\r\n	SELECT * from qResultnegresos  WHERE ALLTRIM(empcedula) = ALLTRIM(qCurRol.empcedula) INTO CURSOR CEgr	\r\n	camposegr=reccount(\"CEgr\")\r\n	campo=j\r\n	*************************************************\r\n	IF camposing>camposegr\r\n		SELECT Cing\r\n		SCAN \r\n			SELECT cur1\r\n			APPEND BLANK \r\n			REPLACE;\r\n			 rubcodigoi		WITH Cing.rubcodigo,;\r\n			 rubdescripi	WITH Cing.rubdescrip,;\r\n			 cedula1			WITH Cing.empcedula,;\r\n			 ordenrub			WITH j,;\r\n			 valori				WITH Cing.valor IN cur1			 \r\n			 j=j+1\r\n			 SELECT Cing\r\n		ENDSCAN\r\n		SELECT cur1		\r\n		IF j>campo		\r\n			GO campo				\r\n		ELSE\r\n			campo=j\r\n		ENDIF\r\n		lcCont=1\r\n		SELECT CEgr\r\n		SCAN \r\n			SELECT cur1			\r\n			REPLACE;\r\n			 rubcodigoe		WITH CEgr.rubcodigo,;\r\n			 rubdescripe	WITH CEgr.rubdescrip,;\r\n			 ordenrub			WITH lcCont,;\r\n			 valore				WITH CEgr.valor IN cur1			 \r\n			 SKIP 1\r\n			 lcCont=lcCont+1\r\n			 SELECT CEgr\r\n		ENDSCAN				\r\n	ELSE \r\n	*/////////////////////si egresos es mayor a ingresos\r\n		lcCont=1\r\n		SELECT CEgr\r\n		SCAN \r\n			SELECT cur1\r\n			APPEND BLANK \r\n			REPLACE;\r\n			 rubcodigoe		WITH CEgr.rubcodigo,;\r\n			 rubdescripe	WITH CEgr.rubdescrip,;\r\n			 cedula1			WITH CEgr.empcedula,;\r\n			 ordenrub			WITH lcCont,;\r\n			 valore				WITH CEgr.valor IN cur1\r\n			 j=j+1\r\n			 lcCont=lcCont+1\r\n			 SELECT CEgr			 \r\n		ENDSCAN				\r\n		SELECT cur1\r\n		IF j>campo		\r\n			GO campo				\r\n		ELSE\r\n			campo=j\r\n		ENDIF\r\n 		lcCont=1\r\n		SELECT Cing\r\n		SCAN \r\n			SELECT cur1		\r\n			REPLACE;\r\n			 rubcodigoi		WITH Cing.rubcodigo,;\r\n			 rubdescripi	WITH Cing.rubdescrip,;\r\n			 ordenrub			WITH lcCont,;\r\n