string=\"RPT_FnxRptRolGeneral\"\r\n			iPreview(ipvZoom100)\r\n			IF _DirectorioReportes(sReportCustom) THEN\r\n				iPrintReport(sReportCustom,sCODIGOROL)\r\n			ELSE\r\n				iPrintReport(sReportNormal,sCODIGOROL)\r\n			END\r\n			sListaI=\"\"\r\n			sListaE=\"\"\r\n			sCode =\"\"\r\n			sCodeE=\"\"\r\nEND\r\n\r\n\r\nRESULT bCorreto', NULL, '1', 'VFP', 'lvalorI			= oAppPrm.frmprm.Code\r\nlvalorE			= oAppPrm.frmprm.CodeE\r\nlEtiquetaI	= oAppPrm.frmprm.gsLetiquetasI\r\nlEtiquetaE	= oAppPrm.frmprm.gsLetiquetasE\r\n\r\nlcCadenaRI 	= STRTRAN(lvalorI,\"*\",\",\")\r\nlcCadenaRE 	= STRTRAN(lvalorE,\"*\",\",\")\r\nlcCadenaEI 	= STRTRAN(lEtiquetaI,\"*\",\",\")\r\nlcCadenaEE 	= STRTRAN(lEtiquetaE,\"*\",\",\")\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT\r\n	tbfnxnencabezadorol.encroldescrip ,\r\n	tbfnxndetallerol.encrolcodigo as rol,\r\n	<<oAppPrm.frmprm.Grupo>> AS grupo,\r\n	<<oAppPrm.frmprm.Orden>> AS orden, \r\n	tbfnxnhisempleado.empapeuno as empapeuno,\r\n	tbfnxnhisempleado.empapedos as empapedos,\r\n	tbfnxnhisempleado.empnomuno as empnomuno,\r\n	tbfnxnhisempleado.empnomdos as empnomdos,\r\n	tbfnxnhisempleado.empcodigo as empcodigo,\r\n	tbfnxnhisempleado.empcedula as empcedula,\r\n	<<lcCadenaRI>>\r\n	<<lcCadenaRE>>\r\n	<<lcCadenaEI>>\r\n	<<lcCadenaEE>>\r\n	tbfnxnhisempleado.emptotaling as campoi0,\r\n	tbfnxnhisempleado.emptotalegr as campoe0,\r\n	tbfnxnhisempleado.empliquido as campol0\r\n	FROM\r\n	tbfnxndetallerol,tbfnxnencabezadorol,tbfnxnhisempleado, tbfnxncurselemp , <<oAppPrm.frmprm.Tabla>>,tbfnxnrubros\r\n	where 	tbfnxndetallerol.encrolcodigo=\'<<oAppPrm.frmprm.Codigorol>>\'\r\n	and 	tbfnxndetallerol.encrolcodigo=tbfnxnencabezadorol.encrolcodigo\r\n	and 	tbfnxnencabezadorol.encrolcodigo=tbfnxnhisempleado.encrolcodigo\r\n	and  	tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\r\n 	and tbfnxndetallerol.rubcodigo=tbfnxnrubros.rubcodigo\r\n \r\n <<oAppPrm.frmprm.ReGrupo>>\r\n\r\n	and tbfnxnhisempleado.empcedula = tbfnxncurselemp.empcedula\r\n	GROUP   BY tbfnxnhisempleado.empcedula\r\n	order by <<oAppPrm.frmprm.Orden>>;\r\n\r\nENDTEXT\r\n\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"nrolGeneral\")\r\n\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n\r\n	SELECT  DISTINCT\r\n	tbfnxndetallerol.rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.rubnomcor\r\n	from \r\n	tbfnxndetallerol ,tbfnxnhisrubros\r\n	where  tbfnxndetallerol.encrolcodigo = \'<<oAppPrm.frmprm.Codigorol>>\' \r\n	and tbfnxnhisrubros.rubcodigo=tbfnxndetallerol.rubcodigo\r\n	and tbfnxnhisrubros.encrolcodigo= tbfnxndetallerol.encrolcodigo\r\n	AND  tbfnxnhisrubros.rubtipo=\'E\'\r\n	AND tbfnxnhisrubros.rubprovision <>\'1\'\r\n	and tbfnxndetallerol.valor <>\'0\'\r\nENDTEXT\r\n\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"netiquetaegreso\")\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n SELECT  DISTINCT\r\n	tbfnxndetallerol.rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.rubnomcor\r\n	from \r\n	tbfnxndetallerol ,tbfnxnhisrubros\r\n	where  tbfnxndetallerol.encrolcodigo = \'<<oAppPrm.frmprm.Codigorol>>\' \r\n	and tbfnxnhisrubros.rubcodigo=tbfnxndetallerol.rubcodigo\r\n	and tbfnxnhisrubros.encrolcodigo= tbfnxndetallerol.encrolcodigo\r\n	AND  tbfnxnhisrubros.rubtipo=\'I\'\r\n	and tbfnxndetallerol.valor <>\'0\'\r\n\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"netiquetaingreso\")\r\n\r\nUseTable(\"tbfnxninit\") \r\n\r\nSELECT nombre, direccion, telefono, ruc, logo from tbfnxninit INTO CURSOR qemp\r\n\r\nSELECT * FROM netiquetaingreso INTO CURSOR qetiqueta\r\nLOCAL i ,lsnom\r\ni=0 \r\n!	etiqueta de ingresos\r\n\r\nSELECT qetiqueta\r\nSCAN\r\n	i=i+1\r\n	IF EMPTY( ALLTRIM(qetiqueta.rubnomcor))\r\n	lsnom= \'0\'\r\n	ELSE\r\n	lsnom = ALLTRIM(qetiqueta.rubnomcor)\r\n	ENDIF \r\n	campo=\'vartli\'+ALLTRIM(STR(i))+\" = \'\"+ALLTRIM(lsnom)+\"\'\"\r\n	&campo\r\n	SELECT qetiqueta\r\nENDSCAN\r\n\r\nSELECT * FROM netiquetaegreso INTO CURSOR qetiqueta\r\n\r\nLOCAL i ,lsnom\r\n*,fields1,fields2,fields3,fields4,fields5,fields6,fields7,fields8,fields9\r\ni=0\r\n!	etiqueta de ingresos\r\nSELECT qetiqueta\r\nSCAN\r\n	i=i+1\r\n	IF EMPTY( ALLTRIM(qetiqueta.rubnomcor))\r\n	lsnom= \'0\'\r\n	ELSE\r\n	lsnom = ALLTRIM(qetiqueta.rubnomcor)\r\n	ENDIF \r\n	campo=\'vartle\'+ALLTRIM(STR(i))+\" = \'\"+ALLTRIM(lsnom)+\"\'\"\r\n	&campo\r\n	SELECT qetiqueta\r\nENDSCAN\r\n\r\nSELECT * FROM nrolGeneral ORDER BY rol,grupo, orden INTO CURSOR qingreso\r\n\r\nPrintViewReport(\"C:\\Fenix\\AdsFenix\\Modulos\\MenuWD\\ReportesVFP\\RolGeneral.frx\")', '{ \"frmprm\":{ \"Codigorol\":\"0000000004\", \"Orden\":\"tbfnxnhisempleado.nombres\", \"Grupo\":\"tbfnxncostos.cosnombre\", \"ReGrupo\":\" and tbfnxnhisempleado.coscodigo=tbfnxncostos.coscodigo\", \"Tabla\":\"tbfnxncostos\", \"Code\":\" MAX(IF(tbfnxndetallerol.rubcodigo = \'SF\'*valor* NULL)) AS campoi1* MAX(IF(tbfnxndetallerol.rubcodigo = \'HEE\'*valor* NULL)) AS campoi2* MAX(IF(tbfnxndetallerol.rubcodigo = \'BONOV\'*valor* NULL)) AS campoi3* MAX(IF(tbfnxndetallerol.rubcodigo = \'COMISION\'*valor* NULL)) AS campoi4* MAX(IF(tbfnxndetallerol.rubcodigo = \'FRING\'*valor* NULL)) AS campoi5* MAX(IF(tbfnxndetallerol.rubcodigo = \'DECIMEN3\'*valor* NULL)) AS campoi6* MAX(IF(tbfnxndetallerol.rubcodigo = \'DECIMOMEN4\'*valor* NULL)) AS campoi7* MAX(IF(tbfnxndetallerol.rubcodigo = \'001\'*valor* NULL)) AS campoi8* MAX(IF(tbfnxndetallerol.rubcodigo = \'XTI\'*valor* NULL)) AS campoi9*\", \"gsLetiquetasI\":\"\'SUELDO FIJO\' AS vartli1*\'HORAS EXTRAS\' AS vartli2*\'BONO\' AS vartli3*\'COMISION\' AS vartli4*\'F RESER ING\' AS vartli5*\'DECIMEN3\' AS vartli6*\'DECIMOMEN4\' AS vartli7*\'BONO ALIMENTAC\' AS vartli8*\'TOTALES\' AS vartli9*\", \"gsLetiquetasE\":\"\'IEES PER\' AS vartle1*\'PRESTAMOS EMPR\' AS vartle2*\'PRESTAMO IESS\' AS vartle3*\'ANT SUE.\' AS vartle4*\'ALIMENTACION\' AS vartle5*\'DESCUENTOS\' AS vartle6*\'PARQUEADERO\' AS vartle7*\'LENTES\' AS vartle8*\'RENTA\' AS vartle9*\'EXAMENES\' AS vartle10*\", \"CodeE\":\" MAX(IF(t