igo ELSE \'\' END AS rubcodigo,\r\n				CASE WHEN  tbfnxnhisrubros.rubtipo  = \'E\' THEN ROUND(valor,2) ELSE 0 END AS valor,\r\n				tbfnxnhisrubros.rubtipo,tbfnxnhisrubros.ruborden\r\n				FROM  tbfnxndetallerol,tbfnxnhisrubros,tbfnxncurselemp\r\n				WHERE  tbfnxndetallerol.rubcodigo	  =tbfnxnhisrubros.rubcodigo\r\n				AND tbfnxnhisrubros.rubprovision	  =0\r\n				AND tbfnxndetallerol.encrolcodigo	  =\'<<oAppPrm.frmprm.Codigorol>>\'\r\n				AND tbfnxnhisrubros.encrolcodigo		= \'<<oAppPrm.frmprm.Codigorol>>\'\r\n				AND tbfnxndetallerol.empcedula 			= tbfnxncurselemp.Empcedula\r\n				AND tbfnxndetallerol.valor<>0\r\n				AND tbfnxnhisrubros.rubtipo					=\'E\'\r\n				ORDER BY tbfnxndetallerol.encrolcodigo,tbfnxndetallerol.empcedula ,rubtipo DESC,ruborden \r\nENDTEXT\r\n\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"qResultnegresos\")\r\n\r\n* Programacion para los ingresos y egresos\r\n\r\nSET ENGINEBEHAVIOR 70\r\n\r\nUseTable(\"tbfnxninit\") \r\nSELECT nombre, direccion, telefono, ruc, logo from tbfnxninit   INTO CURSOR qemp\r\n\r\nSELECT * from qResultnrolHorizontal INTO CURSOR qCurRol READWRITE \r\n\r\nCREATE CURSOR cur1(cedula1 c(10),rubcodigoi c(10),rubdescripi c(30),valori n(20,4),rubcodigoe c(10),rubdescripe c(30),valore n(20,4),ordenrub n(5))\r\ncampo=0\r\nj=1\r\n\r\nSELECT qCurRol\r\nSCAN\r\nSELECT * from qResultningreso WHERE ALLTRIM(empcedula) = ALLTRIM(qCurRol.empcedula)  INTO CURSOR CIng\r\n	camposing=reccount(\"CIng\")\r\n	SELECT * from qResultnegresos  WHERE ALLTRIM(empcedula) = ALLTRIM(qCurRol.empcedula) INTO CURSOR CEgr	\r\n	camposegr=reccount(\"CEgr\")\r\n	campo=j\r\n	*************************************************\r\n	IF camposing>camposegr\r\n		SELECT Cing\r\n		SCAN \r\n			SELECT cur1\r\n			APPEND BLANK \r\n			REPLACE;\r\n			 rubcodigoi		WITH Cing.rubcodigo,;\r\n			 rubdescripi	WITH Cing.rubdescrip,;\r\n			 cedula1			WITH Cing.empcedula,;\r\n			 ordenrub			WITH j,;\r\n			 valori				WITH Cing.valor IN cur1			 \r\n			 j=j+1\r\n			 SELECT Cing\r\n		ENDSCAN\r\n		SELECT cur1		\r\n		IF j>campo		\r\n			GO campo				\r\n		ELSE\r\n			campo=j\r\n		ENDIF\r\n		lcCont=1\r\n		SELECT CEgr\r\n		SCAN \r\n			SELECT cur1			\r\n			REPLACE;\r\n			 rubcodigoe		WITH CEgr.rubcodigo,;\r\n			 rubdescripe	WITH CEgr.rubdescrip,;\r\n			 ordenrub			WITH lcCont,;\r\n			 valore				WITH CEgr.valor IN cur1			 \r\n			 SKIP 1\r\n			 lcCont=lcCont+1\r\n			 SELECT CEgr\r\n		ENDSCAN				\r\n	ELSE \r\n	*/////////////////////si egresos es mayor a ingresos\r\n		lcCont=1\r\n		SELECT CEgr\r\n		SCAN \r\n			SELECT cur1\r\n			APPEND BLANK \r\n			REPLACE;\r\n			 rubcodigoe		WITH CEgr.rubcodigo,;\r\n			 rubdescripe	WITH CEgr.rubdescrip,;\r\n			 cedula1			WITH CEgr.empcedula,;\r\n			 ordenrub			WITH lcCont,;\r\n			 valore				WITH CEgr.valor IN cur1\r\n			 j=j+1\r\n			 lcCont=lcCont+1\r\n			 SELECT CEgr			 \r\n		ENDSCAN				\r\n		SELECT cur1\r\n		IF j>campo		\r\n			GO campo				\r\n		ELSE\r\n			campo=j\r\n		ENDIF\r\n 		lcCont=1\r\n		SELECT Cing\r\n		SCAN \r\n			SELECT cur1		\r\n			REPLACE;\r\n			 rubcodigoi		WITH Cing.rubcodigo,;\r\n			 rubdescripi	WITH Cing.rubdescrip,;\r\n			 ordenrub			WITH lcCont,;\r\n			 valori				WITH Cing.valor IN cur1						 \r\n			 SKIP 1\r\n			 lcCont=lcCont+1\r\n\r\n			 SELECT Cing\r\n		ENDSCAN		\r\n	ENDIF\r\n			SELECT cur1	\r\n	SELECT qCurRol\r\nENDSCAN \r\n\r\nSELECT * FROM qCurRol, cur1 WHERE  ALLTRIM(qcurrol.empcedula) = ALLTRIM(cur1.cedula1)  INTO CURSOR qCurRol \r\n\r\nPrintViewReport(\"C:\\Fenix\\AdsFenix\\Modulos\\MenuWD\\ReportesVFP\\rolhorizontal.frx\")', '{ \"frmprm\":{ \"Codigorol\":\"0000000004\", \"Orden\":\"tbfnxnhisempleado.nombres\", \"Grupo\":\"tbfnxnhisempleado.coscodigo\", \"ReGrupo\":\" and tbfnxnhisempleado.coscodigo=tbfnxncostos.coscodigo\", \"Tabla\":\" tbfnxncostos\", \"CentroCosto\":\"\" } }'),
('RPTPROVIS', 'ROL_PAGOS', 'REPORTE PROVICIONES', NULL, 'bCorreto is boolean=False\r\n\r\nsTxtinser is string = [\r\n	INSERT INTO tbfnxnrolparametrizable (encroldescrip ,rol ,grupo ,orden ,empapeuno ,empapedos ,empnomuno ,empnomdos ,empcodigo ,empcedula ,\r\n	%1	%2 campoi0,campoe0 ,campol0)\r\n]\r\nsMyresulinisert is string = StringBuild(sTxtinser,sListaI,sListaE)\r\nlsTxtvista is string = [\r\n	%6\r\n	SELECT\r\n	tbfnxnencabezadorol.encroldescrip ,\r\n	tbfnxndetallerol.encrolcodigo as rol,\r\n	%1 as grupo,\r\n	%2 as orden, \r\n	tbfnxnhisempleado.empapeuno as empapeuno,\r\n	tbfnxnhisempleado.empapedos as empapedos,\r\n	tbfnxnhisempleado.empnomuno as empnomuno,\r\n	tbfnxnhisempleado.empnomdos as empnomdos,\r\n	tbfnxnhisempleado.empcodigo as empcodigo,\r\n	tbfnxnhisempleado.empcedula as empcedula,\r\n	%7\r\n	%8\r\n	tbfnxnhisempleado.emptotaling as campoi0,\r\n	tbfnxnhisempleado.emptotalegr as campoe0,\r\n	tbfnxnhisempleado.empliquido as campol0\r\n	FROM\r\n	tbfnxndetallerol,tbfnxnencabezadorol,tbfnxnhisempleado, tbfnxncurselemp %3\r\n	where 	tbfnxndetallerol.encrolcodigo=\'\'%4\'\'\r\n	and 	tbfnxndetallerol.encrolcodigo=tbfnxnencabezadorol.encrolcodigo\r\n	and 	tbfnxnencabezadorol.encrolcodigo=tbfnxnhisempleado.encrolcodigo\r\n	and  	tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\r\n	%5\r\n	and tbfnxnhisempleado.empcedula = tbfnxncurselemp.empcedula\r\n	GROUP   BY tbfnxnhisempleado.empcedula\r\n	order by %2;\r\n]\r\nSWITCH NoSpace(gsRptvfp)\r\nCASE \"VFP\"\r\n///////////////////////////////////////////////////////////////////////////////////////////////////\r\n// Codigo de visualizacion de reporte en VFP																										 //\r\n//////////////////////////////////////////////////////////////////////////////////////////////////\r\nlsTxtvista= StringBuild(lsTxtvista,sLcGrupoCam,sLcOrden,gsTablas,sCODIGOROL,sLcGrupoRel,sCode , sCodeE )\r\nLsTxtvista  = replace(LsTxtvista,\"\'\'\",\"\'\")\r\n\r\n	lsetiquetas is string = [\r\n	SELECT  DISTINCT\r\n	tbfnxndetallerol.rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.rubnomcor\r\n	from \r\n	tbfnxndetallerol ,tbfnxnhisrubros\r\n	where  tbfnxndetallerol.encrolcodigo = \'%1\' \r\n	and tbfnxnhisrubros.rubcodigo=tbfnxndetallerol.rubcodigo\r\n	and tbfnxnhisrubros.encrolcodigo= tbfnxndetallerol.encrolcodigo\r\n	and tbfnxndetallerol.valor <>\'0\'\r\n	AND tbfnxnhisrubros.rubprovision  <>\'0\'\r\n	]\r\n	lsetiquetas = StringBuild(lsetiquetas,sCODIGOROL,\"E\")\r\n			FnxCursorVFP(lsetiquetas,\"netiquetaProvision\") \r\n\r\n			FnxCursorVFP(lsTxtvista,\"nrolprovisiones\") \r\n			FnxPreview()\r\n			FnxReportVFP(*,\"RolPagosProvisiones.frx\",\"nrolprovisiones.dbf\")\r\n\r\nOTHER CASE\r\n		lsTxtvista= StringBuild(lsTxtvista,sLcGrupoCam,sLcOrden,gsTablas,sCODIGOROL,sLcGrupoRel,sMyresulinisert,sCode , sCodeE )\r\n		IF HExecuteSQLQuery(\"dsData\",MySqlNomina,hQueryWithoutCorrection,\"call Fnxsp_NReporteParametrizable (\'\"+lsTxtvista+\"\')\") = False THEN\r\n			pgUti_Mess(Error(ErrorInfo()),\"ADS Informa\",Detener)\r\n			sListaI=\"\"\r\n			sListaE=\"\"\r\n			sCode =\"\"\r\n			sCodeE=\"\"\r\n	ELSE\r\n\r\n			sRuta is string = fExeDir() +\"\\Informes_Consulta\"\r\n			sReportCustom is string=\"RPT_FnxRptProvisionaRolPagos\"//+sRuta\r\n			sReportNormal is string=\"RPT_FnxRptProvisionaRolPagos\"\r\n			iPreview(ipvZoom100)\r\n			IF _DirectorioReportes(\"RPT_FnxRptProvisionaRolPagos\") THEN\r\n				iPrintReport(sReportCustom)\r\n			ELSE\r\n				iPrintReport(sReportNormal,sCODIGOROL)\r\n			END\r\n			sListaI=\"\"\r\n			sListaE=\"\"\r\n			sCode =\"\"\r\n			sCodeE=\"\"\r\n		END\r\nEND\r\n\r\n\r\nRESULT bCorreto', NULL, '1', 'VFP', 'lvalorI			= oAppPrm.frmprm.Code\r\nlvalorE			= oAppPrm.frmprm.CodeE\r\nlEtiquetaI	= oAppPrm.frmprm.gsLetiquetasI\r\nlEtiquetaE	= oAppPrm.frmprm.gsLetiquetasE\r\n\r\nlcCadenaRI 	= STRTRAN(lvalorI,\"*\",\",\")\r\nlcCadenaRE 	= STRTRAN(lvalorE,\"*\",\",\")\r\nlcCadenaEI 	= STRTRAN(lEtiquetaI,\"*\",\",\")\r\nlcCadenaEE 	= STRTRAN(lEtiquetaE,\"*\",\",\")\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n	SELECT\r\n	tbfnxnencabezadorol.encroldescrip ,\r\n	tbfnxndetallerol.encrolcodigo as rol,\r\n	<<oAppPrm.frmprm.Grupo>> AS grupo,\r\n	<<oAppPrm.frmprm.Orden>> AS orden,  \r\n	tbfnxnhisempleado.empapeuno as empapeuno,\r\n	tbfnxnhisempleado.empapedos as empapedos,\r\n	tbfnxnhisempleado.empnomuno as empnomuno,\r\n	tbfnxnhisempleado.empnomdos as empnomdos,\r\n	tbfnxnhisempleado.empcodigo as empcodigo,\r\n	tbfnxnhisempleado.empcedula as empcedula,\r\n	<<lcCadenaRI>>\r\n	<<lcCadenaRE>>\r\n	<<lcCadenaEI>>\r\n	<<lcCadenaEE>>\r\n	tbfnxnhisempleado.emptotaling as campoi0,\r\n	tbfnxnhisempleado.emptotalegr as campoe0,\r\n	tbfnxnhisempleado.empliquido as campol0\r\n	FROM\r\n	tbfnxndetallerol,tbfnxnencabezadorol,tbfnxnhisempleado, tbfnxncurselemp , <<oAppPrm.frmprm.Tabla>>,tbfnxnrubros\r\n	where 	tbfnxndetallerol.encrolcodigo=\'<<oAppPrm.frmprm.Codigorol>>\'\r\n	and 	tbfnxndetallerol.encrolcodigo=tbfnxnencabezadorol.encrolcodigo\r\n	and 	tbfnxnencabezadorol.encrolcodigo=tbfnxnhisempleado.encrolcodigo\r\n	and  	tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\r\n 	and tbfnxndetallerol.rubcodigo=tbfnxnrubros.rubcodigo\r\n \r\n	 <<oAppPrm.frmprm.ReGrupo>>\r\n\r\n	and tbfnxnhisempleado.empcedula = tbfnxncurselemp.empcedula\r\n	GROUP   BY tbfnxnhisempleado.empcedula\r\n	order by <<oAppPrm.frmprm.Orden>>;\r\n\r\nENDTEXT\r\n\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"nrolprovisiones\")\r\n\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n\r\n	SELECT  DISTINCT\r\n	tbfnxndetallerol.rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.rubnomcor\r\n	from \r\n	tbfnxndetallerol ,tbfnxnhisrubros\r\n	where  tbfnxndetallerol.encrolcodigo = \'<<oAppPrm.frmprm.Codigorol>>\' \r\n	and tbfnxnhisrubros.rubcodigo=tbfnxndetallerol.rubcodigo\r\n	and tbfnxnhisrubros.encrolcodigo= tbfnxndetallerol.encrolcodigo\r\n	AND  tbfnxnhisrubros.rubtipo=\'E\'\r\n	AND tbfnxnhisrubros.rubprovision <>\'1\'\r\n	and tbfnxndetallerol.valor <>\'0\'\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"netiquetaProvision\")\r\nUseTable(\"tbfnxninit\") \r\nSELECT nombre, direccion, telefono, ruc, logo from tbfnxninit INTO CURSOR qemp\r\nSELECT * FROM netiquetaProvision INTO CURSOR qetiqueta\r\n\r\nLOCAL i ,lsnom\r\n*,fields1,fields2,fields3,fields4,fields5,fields6,fields7,fields8,fields9\r\ni=0\r\n*!*	etiqueta de ingresos\r\nSELECT qetiqueta\r\nSCAN\r\n	i=i+1\r\n	IF EMPTY( ALLTRIM(qetiqueta.rubnomcor))\r\n	lsnom= \'0\'\r\n	ELSE\r\n	lsnom = ALLTRIM(qetiqueta.rubnomcor)\r\n	ENDIF \r\n	campo=\'fields\'+ALLTRIM(STR(i))+\" = \'\"+ALLTRIM(lsnom)+\"\'\"\r\n	&campo\r\n	SELECT qetiqueta\r\nENDSCAN\r\n\r\nSELECT * FROM nrolprovisiones ORDER BY rol,grupo, orden INTO CURSOR qprovis\r\n*!*brow\r\n*!*	a=\"SUELDO BASICO\"\r\n\r\nPrintViewReport(\"C:\\Fenix\\AdsFenix\\Modulos\\MenuWD\\ReportesVFP\\RolPagosProvisiones.frx\")', NULL),
