('RPTRICC', 'ROL_PLAN', 'REPORTE DEPLANILLA CON COPIA', NULL, 'bCorreto is boolean=False\r\nlsTxt is string = [\r\n	SELECT DISTINCT  tbfnxnhisempleado.empfecing,empfecsal,empdepar,empsalario,empdiastrab,emptiporemun as tipo_remuneracion,\r\n	encroldescrip,tbfnxndetallerol.encrolcodigo as rol,%2 as orden,\r\n	empapeuno,empapedos,empnomuno,empnomdos,tbfnxnhisempleado.empcedula,rubdescrip,empliquido,\r\n	emptotaling,emptotalegr,valor,tbfnxnhisrubros.rubtipo as tipo,tbfnxnhisrubros.rubcodigo,tbfnxnencabezadorol.fecha,tbfnxnhisempleado.empcodigo,\r\n	tbfnxndetallerol.encrolcodigo,%3 as grupo,tbfnxnhisempleado.nombres,tbfnxnhisrubros.rubtipo,tbfnxnhisrubros.ruborden,tbfnxnhisempleado.empmail\r\n	FROM  tbfnxndetallerol,tbfnxnhisrubros,tbfnxnhisempleado,tbfnxndepartamento,tbfnxnencabezadorol,tbfnxncurselemp %5\r\n	WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\r\n	and tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\r\n	AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo\r\n	AND tbfnxnhisrubros.rubprovision=FALSE\r\n	AND tbfnxndetallerol.encrolcodigo= \'%1\'\r\n	AND tbfnxnhisrubros.encrolcodigo= \'%1\'\r\n	AND tbfnxnhisempleado.encrolcodigo= \'%1\'	\r\n	and tbfnxnencabezadorol.encrolcodigo= \'%1\'\r\n	AND valor <> 0\r\n	AND tbfnxndetallerol.rubcodigo <> \'SALARIO\'\r\n	%6\r\n	and tbfnxnhisempleado.empcedula = tbfnxncurselemp.empcedula\r\n	%4\r\n	ORDER BY tbfnxndetallerol.encrolcodigo,%3,%2 ,rubtipo desc,ruborden;\r\n]\r\nlsTxt = StringBuild(lsTxt,sCODIGOROL,sLcOrden,sLcGrupoCam,sLcGrupoRel,gsTablas,sValorextra)\r\ndsData is Data Source\r\n\r\nSWITCH NoSpace(gsRptvfp)\r\nCASE \"VFP\"\r\n\r\n	///////////////////////////////////////////////////////////////////////////////////////////////////\r\n	// Codigo de visualizacion de reporte en VFP																										 //\r\n	///////////////////////////////////////////////////////////////////////////////////////////////////\r\n	FnxCursorVFP(lsTxt,\"nrolindividualconcopia\") \r\n	FnxPreview()\r\n	FnxReport