nrolRubrosAgruEgre\")\r\n\r\n\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n		SELECT \r\n				tbfnxndetallerol.encrolcodigo AS encrolcodigo,	\r\n				tbfnxndetallerol.rubcodigo AS rubcodigo,	\r\n				v_fnxhistoricosrubros.rubdescrip AS rubdescrip,	\r\n				v_fnxhistoricosrubros.rubtipo AS rubtipo,	\r\n				v_fnxhistoricosrubros.rubnomcor AS rubnomcor,	\r\n				v_fnxhistoricosrubros.rubprovision AS rubprovision,	\r\n				SUM(tbfnxndetallerol.valor) AS valor\r\n			FROM \r\n				tbfnxndetallerol,	\r\n				v_fnxhistoricosrubros\r\n			WHERE \r\n				tbfnxndetallerol.encrolcodigo = v_fnxhistoricosrubros.encrolcodigo\r\n				AND	tbfnxndetallerol.rubcodigo = v_fnxhistoricosrubros.rubcodigo\r\n				AND\r\n				(\r\n					tbfnxndetallerol.valor <> 0\r\n					AND	v_fnxhistoricosrubros.rubprovision  = 1\r\n					AND	tbfnxndetallerol.encrolcodigo = \'<<oAppPrm.frmprm.Codigorol>>\'\r\n				)\r\n			GROUP BY \r\n				tbfnxndetallerol.encrolcodigo,	\r\n				tbfnxndetallerol.rubcodigo,	\r\n				v_fnxhistoricosrubros.rubdescrip,	\r\n				v_fnxhistoricosrubros.rubtipo,	\r\n				v_fnxhistoricosrubros.rubnomcor,	\r\n				v_fnxhistoricosrubros.rubprovision\r\n\r\nENDTEXT\r\n\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"nrolRubrosAgruProv\")\r\n	SET ENGINEBEHAVIOR 70\r\n\r\nUseTable(\"tbfnxninit\") \r\nSELECT nombre, direccion, telefono, ruc, logo from tbfnxninit   INTO CURSOR qemp\r\n\r\nSELECT * from qResultnrolRubrosAgrupados  INTO CURSOR qCurRol READWRITE \r\n\r\nCREATE CURSOR cur1(encrolcodi c(10),rubcodigoi c(10),rubdescripi c(30),valori n(20,4),rubcodigoe c(10),rubdescripe c(30),valore n(20,4),ordenrub n(5))\r\nCREATE CURSOR curol(encrolcodi c(10),rubcodigoi c(10),rubdescripi c(30),valori n(20,4),rubcodigoe c(10),rubdescripe c(30),valore n(20,4),rubcodigop c(10),rubdescripp c(30),valorp n(20,4),ordenrub n(5))\r\ncampo=0\r\nj=1\r\n	SELECT * from nrolRubrosAgruIng  INTO CURSOR CIng\r\n	camposing=reccount(\"CIng\")\r\n	SELECT * from nrolRubrosAgruEgre INTO CURSOR CEgr	\r\n	camposegr=reccount(\"CEgr\")\r\n