('RTCC', 'RTCCOSTOS', 'REPORTE TIEMPO CENTRO DE COSTOS', NULL, '', NULL, '1', 'VFP', 'SET CENTURY ON\r\nSET DATE DMY\r\nSET HOURS TO 24\r\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\r\n    SELECT\r\n			tbfnxntiempocc.coscodigo,\r\n			tbfnxncostos.cosnombre as nombrecc, 			\r\n			tbfnxntiempocc.empcedula,\r\n			tbfnxnempleado.nombres,\r\n			CAST(tbfnxntiempocc.valor AS CHARACTER(6)) AS valor,\r\n			CAST((tbfnxntiempocc.valor * 160)/100 AS CHARACTER(6)) as tHoras,\r\n			CAST(SEC_TO_TIME((tbfnxntiempocc.valor * 160)/100 * 3600) AS CHARACTER(8)) as horas\r\n			FROM \r\n				tbfnxntiempocc\r\n			LEFT JOIN tbfnxncostos ON (tbfnxntiempocc.coscodigo = tbfnxncostos.coscodigo)\r\n			RIGHT JOIN  tbfnxnempleado ON (tbfnxnempleado.empcedula = tbfnxntiempocc.empcedula)\r\n			WHERE  tbfnxnempleado.empactivo = \'<<oAppPrm.frmprm.Activo>>\'\r\n			GROUP BY  tbfnxntiempocc.coscodigo, tbfnxntiempocc.empcedula\r\n			ORDER BY tbfnxnempleado.nombres\r\nENDTEXT\r\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\"qResultCC\")\r\n\r\nSELECT empcedula+\' - \'+nombres AS Empleado ,coscodigo+\' - \'+nombrecc as cc , valor+\' - \'+thoras+\' - \'+horas as formato fROM qResultCC into cursor qResultCC\r\n\r\nVFPXTAB(\"qResultCC1\")\r\n*browse\r\n\r\nUseTable(\"tbfnxninit\") \r\nSELECT nombre, direccion, telefono, ruc, logo from tbfnxninit   INTO CURSOR qemp\r\n\r\nSELECT * FROM qResultCC1 INTO CURSOR qResultCC1\r\n\r\nPrintViewReport(\"C:\\Fenix\\AdsFenix\\Modulos\\MenuWD\\ReportesVFP\\ReporteTiemposCC.frx\")\r\nreturn \r\n	\r\n', '{ \"frmprm\":{ \"CentroC\":\"0000000001\", \"NombreCC\":\"ADMINISTRATIVO\", \"CodEmp\":\"1003647524\", \"NombreEmp\":\"GOMEZ MENDEZ ANA YAJAIRA\", \"Activo\":\"Activo\", \"CentroCD\":\"\", \"Cantidad\":0 } }');
