(1, '2017-03-27', 'Pantalla Bonificaciones', '\r\nCALL addField (\'tbfnxncargo\',\'codigoare\',\'VARCHAR\',\'10\',\'DEFAULT \\\'\\\'\');\r\n\r\nCREATE  TABLE IF NOT EXISTS `tbfnxnarea` (\r\n  `codigoare` varchar(10) NOT NULL,\r\n  `descripcion` varchar(50) DEFAULT NULL,\r\n  `observacion` text,\r\n  PRIMARY KEY (`codigoare`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\r\n\r\nCREATE TABLE IF NOT EXISTS `tbfnxnencbono` (\r\n  `codigobo` varchar(10) NOT NULL,\r\n  `descripcion` varchar(255) DEFAULT NULL,\r\n  `cumplimientoe` decimal(23,8) DEFAULT NULL,\r\n  `cuarta` decimal(23,8) DEFAULT NULL,\r\n  `cumplimientoc` decimal(23,8) DEFAULT NULL,\r\n  `roturames` decimal(23,8) DEFAULT NULL,\r\n  `valorrotura` decimal(23,8) DEFAULT NULL,\r\n  `multa` decimal(23,8) DEFAULT NULL,\r\n  `encrolcodigo` varchar(10) DEFAULT NULL,\r\n  `rubcodigoi` varchar(10) DEFAULT NULL,\r\n  `aprobado` int(1) DEFAULT NULL,\r\n  `roturamol` decimal(23,8) DEFAULT NULL,\r\n  `roturaemp` decimal(23,8) DEFAULT NULL,\r\n  `cuarta2` decimal(23,8) DEFAULT NULL,\r\n  `cumplimientoc2` decimal(23,8) DEFAULT NULL,\r\n  `roturames2` decimal(23,8) DEFAULT NULL,\r\n  `valorrotura2` decimal(23,8) DEFAULT NULL,\r\n  `multa2` decimal(23,8) DEFAULT NULL,\r\n  `roturamol2` decimal(23,8) DEFAULT NULL,\r\n  `roturaemp2` decimal(23,8) DEFAULT NULL,\r\n  `cumplimientoc3` decimal(23,8) DEFAULT NULL,\r\n  `cumplimientoc4` decimal(23,8) DEFAULT NULL,\r\n  PRIMARY KEY (`codigobo`),\r\n  KEY `encrolcodigo` (`encrolcodigo`),\r\n  KEY `rubcodigoi` (`rubcodigoi`),\r\n  KEY `rubcodigoe` (`roturamol`),\r\n  CONSTRAINT `tbfnxnencbono_ibfk_1` FOREIGN KEY (`encrolcodigo`) REFERENCES `tbfnxnencabezadorol` (`encrolcodigo`),\r\n  CONSTRAINT `tbfnxnencbono_ibfk_2` FOREIGN KEY (`rubcodigoi`) REFERENCES `tbfnxnrubros` (`rubcodigo`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\r\n\r\n\r\nCREATE TABLE IF NOT EXISTS `tbfnxndetbono` (\r\n  `codigobonod` int(255) NOT NULL AUTO_INCREMENT,\r\n  `codigobo` varchar(10) DEFAULT NULL,\r\n  `empcedula` varchar(10) DEFAULT NULL,\r\n  `nombres` varchar(80) DEFAULT NULL,\r\n  `area` varchar(10) DEFAULT NULL,\r\n  `personal` varchar(10) DEFAULT NULL,\r\n  `cargo` varchar(10) DEFAULT NULL,\r\n  `actsec` varchar(10) DEFAULT NULL,\r\n  `estadodescuen` int(1) DEFAULT \'0\',\r\n  `bono` decimal(23,8) DEFAULT NULL,\r\n  `calbono` decimal(23,8) DEFAULT NULL,\r\n  `descuento` decimal(23,8) DEFAULT NULL,\r\n  `total` decimal(23,8) DEFAULT NULL,\r\n  `tipo` int(255) DEFAULT NULL,\r\n  PRIMARY KEY (`codigobonod`),\r\n  KEY `codigobo` (`codigobo`),\r\n  KEY `empcedula` (`empcedula`),\r\n  KEY `personal` (`personal`),\r\n  KEY `actsec` (`actsec`),\r\n  CONSTRAINT `tbfnxndetbono_ibfk_1` FOREIGN KEY (`codigobo`) REFERENCES `tbfnxnencbono` (`codigobo`),\r\n  CONSTRAINT `tbfnxndetbono_ibfk_2` FOREIGN KEY (`empcedula`) REFERENCES `tbfnxnempleado` (`empcedula`),\r\n  CONSTRAINT `tbfnxndetbono_ibfk_3` FOREIGN KEY (`personal`) REFERENCES `tbfnxnpersonal` (`percodigo`),\r\n  CONSTRAINT `tbfnxndetbono_ibfk_4` FOREIGN KEY (`actsec`) REFERENCES `tbfnxiessectorial` (`cscodigo1`)\r\n) ENGINE=InnoDB AUTO_INCREMENT=456 DEFAULT CHARSET=utf8;\r\n\r\n-- ----------------------------\r\n-- Procedure structure for Fnxsp_NCargarBonosRol\r\n-- ----------------------------\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NCargarBonosRol`;\r\n\r\nCREATE DEFINER=`edoc`@`%` PROCEDURE `Fnxsp_NCargarBonosRol`(IN RolEncForm VARCHAR(10))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n\r\nDECLARE pcedula VARCHAR (10);\r\nDECLARE pvalor NUMERIC (23, 8);\r\nDECLARE d INT DEFAULT 0;\r\nDECLARE prubro VARCHAR (10);\r\nDECLARE paprobado INT DEFAULT 0;\r\n\r\nDECLARE curbono CURSOR FOR \r\nSELECT D.empcedula,D.total\r\nFROM tbfnxndetbono AS D\r\nLEFT JOIN tbfnxnencbono AS E ON D.codigobo=E.codigobo \r\nWHERE E.aprobado=\'1\' AND D.total>0\r\nAND E.encrolcodigo= RolEncForm;\r\n\r\n\r\n\r\nDECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\'\r\nSET d = 1;\r\nSET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\nSTART TRANSACTION;\r\n\r\nSELECT tbfnxnencbono.rubcodigoi INTO prubro FROM tbfnxnencbono WHERE tbfnxnencbono.encrolcodigo=RolEncForm;\r\nSELECT tbfnxnencbono.aprobado INTO paprobado FROM tbfnxnencbono WHERE tbfnxnencbono.encrolcodigo=RolEncForm;\r\n\r\n\r\nIF paprobado=1 THEN\r\n\r\nOPEN curbono;\r\n\r\n	FETCH curbono INTO pcedula, pvalor;\r\n		WHILE NOT d DO\r\n			UPDATE tbFnxNDetalleRol\r\n			SET valor = pvalor\r\n			WHERE\r\n				encrolcodigo = RolEncForm \r\n			AND empcedula = pcedula\r\n			AND Rubcodigo = prubro;\r\n\r\n			FETCH curbono INTO pcedula, pvalor;\r\n		END\r\n	WHILE;\r\n\r\n	CLOSE curbono;\r\nEND IF;\r\n\r\nCOMMIT;\r\n\r\nEND\r\n;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NGenerar`;\r\n\r\nCREATE DEFINER=``@`%` PROCEDURE `Fnxsp_NGenerar`(IN RolEncForm VARCHAR(10), IN Metodo INT)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n\r\n	-- \'0000000148\'\r\n	 DROP VIEW IF EXISTS `v_fnxncurcont`;\r\n\r\n	IF Metodo=1 THEN\r\n		CREATE SQL SECURITY  INVOKER VIEW v_fnxncurcont AS\r\n		SELECT tbfnxnhisempleado.encrolcodigo,tbfnxnhisempleado.empcedula,COALESCE(tbfnxnhisempleado.empcodcon,\'\') AS empcodcon,\r\n			COALESCE(empdepar,\'\') AS empdepar,COALESCE(depcodcon,\'\') AS depcodcon,COALESCE(depcodcon2,\'\') AS depcodcon2,COALESCE(tbfnxnhisempleado.coscodigo,\'\') AS coscodigo, \r\n			COALESCE(tbfnxnhisrubros.rubcodigo,\'\') AS rubcodigo,COALESCE(tbfnxnhisrubros.rubextension,\'\') AS rubextension, \r\n			COALESCE(tbfnxndetallerol.valor,0) AS valor,COALESCE(codcta1d,\'\') AS codctad,COALESCE(rubcodcta1d,\'\') AS rubcodctad,COALESCE(codcta1h,\'\') AS codctah,COALESCE(rubcodcta1h,\'\') AS rubcodctah\r\n			FROM tbfnxnhisempleado,tbfnxnhisrubros,tbfnxndetallerol,tbfnxndepartamento\r\n			WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula \r\n			AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo \r\n			AND tbfnxndetallerol.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisrubros.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\r\n			AND valor<>0 \r\n			ORDER BY tbfnxnhisempleado.empcedula,tbfnxnhisrubros.rubtipo desc,tbfnxnhisrubros.ruborden ;\r\n\r\n	END IF;\r\n\r\nIF Metodo=2 THEN\r\n		CREATE SQL SECURITY  INVOKER VIEW v_fnxncurcont AS\r\n		SELECT tbfnxnhisempleado.encrolcodigo,tbfnxnhisempleado.empcedula,COALESCE(tbfnxnhisempleado.empcodcon,\'\') AS empcodcon,\r\n			COALESCE(empdepar,\'\') AS empdepar,COALESCE(depcodcon,\'\') AS depcodcon,COALESCE(depcodcon2,\'\') AS depcodcon2,COALESCE(tbfnxnhisempleado.coscodigo,\'\') AS coscodigo, \r\n			COALESCE(tbfnxnhisrubros.rubcodigo,\'\') AS rubcodigo,COALESCE(tbfnxnhisrubros.rubextension,\'\') AS rubextension, \r\n			COALESCE(tbfnxndetallerol.valor,0) AS valor,COALESCE(codcta2d,\'\') AS codctad,COALESCE(rubcodcta2d,\'\') AS rubcodctad,COALESCE(codcta2h,\'\') AS codctah,COALESCE(rubcodcta2h,\'\') AS rubcodctah\r\n			FROM tbfnxnhisempleado,tbfnxnhisrubros,tbfnxndetallerol,tbfnxndepartamento\r\n			WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula \r\n			AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo \r\n			AND tbfnxndetallerol.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisrubros.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\r\n			AND valor<>0 \r\n			ORDER BY tbfnxnhisempleado.empcedula,tbfnxnhisrubros.rubtipo desc,tbfnxnhisrubros.ruborden ;\r\n\r\n	END IF;\r\n\r\n\r\n CALL Fnxsp_NCrearCurCont(RolEncForm );\r\n	\r\n\r\nEND\r\n;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NGenerarAju`;\r\n\r\nCREATE DEFINER=``@`%` PROCEDURE `Fnxsp_NGenerarAju`(IN PAnio VARCHAR(10),IN Metodo INT,IN PRegimen VARCHAR(10))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	DECLARE texto text;\r\n\r\n	-- \'0000000148\'\r\n	 DROP VIEW IF EXISTS `v_fnxncurcontaju`;\r\n\r\n	IF Metodo=1 THEN\r\n		SET texto=CONCAT( \' CREATE  SQL SECURITY  INVOKER VIEW v_fnxncurcontaju AS SELECT tbfnxnrubrosiv.anio AS encrolcodigo,tbfnxnempleado.empcedula,COALESCE(tbfnxnempleado.empcodcon,\'\'\'\') AS empcodcon,\r\n			COALESCE(empdepar,\'\'\'\') AS empdepar,COALESCE(depcodcon,\'\'\'\') AS depcodcon,COALESCE(depcodcon2,\'\'\'\') AS depcodcon2,COALESCE(tbfnxnempleado.coscodigo,\'\'\'\') AS coscodigo, \r\n			COALESCE(tbfnxnrubros.rubcodigo,\'\'\'\') AS rubcodigo,COALESCE(tbfnxnrubros.rubextension,\'\'\'\') AS rubextension, \r\n			COALESCE(tbfnxnrubrosiv.saldo,0) AS valor,COALESCE(codcta1d,\'\'\'\') AS codctad,COALESCE(rubcodcta1d,\'\'\'\') AS rubcodctad,COALESCE(codcta1h,\'\'\'\') AS codctah,COALESCE(rubcodcta1h,\'\'\'\') AS rubcodctah\r\n			FROM tbfnxnempleado,tbfnxnrubros,tbfnxnrubrosiv,tbfnxndepartamento\r\n			WHERE tbfnxnrubrosiv.empcedula=tbfnxnempleado.empcedula \r\n			AND tbfnxnrubrosiv.rubcodigo=tbfnxnrubros.rubcodigo \r\n			AND tbfnxnrubrosiv.anio=\'\'\',PAnio ,\'\'\'\r\n			AND tbfnxnrubrosiv.regimen=\'\'\',PRegimen ,\'\'\'\r\n			AND tbfnxnempleado.empdepar=tbfnxndepartamento.depcodigo\r\n			AND saldo<>0 \r\n			ORDER BY tbfnxnempleado.empcedula,tbfnxnrubros.rubtipo desc,tbfnxnrubros.ruborden\');\r\n\r\n	END IF;\r\n\r\nIF Metodo=2 THEN\r\n		SET texto=CONCAT( \' CREATE  SQL SECURITY  INVOKER VIEW v_fnxncurcontaju AS SELECT tbfnxnrubrosiv.anio AS encrolcodigo,tbfnxnempleado.empcedula,COALESCE(tbfnxnempleado.empcodcon,\'\'\'\') AS empcodcon,\r\n			COALESCE(empdepar,\'\'\'\') AS empdepar,COALESCE(depcodcon,\'\'\'\') AS depcodcon,COALESCE(depcodcon2,\'\'\'\') AS depcodcon2,COALESCE(tbfnxnempleado.coscodigo,\'\'\'\') AS coscodigo, \r\n			COALESCE(tbfnxnrubros.rubcodigo,\'\'\'\') AS rubcodigo,COALESCE(tbfnxnrubros.rubextension,\'\'\'\') AS rubextension, \r\n			COALESCE(tbfnxnrubrosiv.saldo,0) AS valor,COALESCE(codcta2d,\'\'\'\') AS codctad,COALESCE(rubcodcta2d,\'\'\'\') AS rubcodctad,COALESCE(codcta2h,\'\'\'\') AS codctah,COALESCE(rubcodcta2h,\'\'\'\') AS rubcodctah\r\n			FROM tbfnxnempleado,tbfnxnrubros,tbfnxnrubrosiv,tbfnxndepartamento\r\n			WHERE tbfnxnrubrosiv.empcedula=tbfnxnempleado.empcedula \r\n			AND tbfnxnrubrosiv.rubcodigo=tbfnxnrubros.rubcodigo \r\n			AND tbfnxnrubrosiv.anio=\'\'\',PAnio ,\'\'\'\r\n			AND tbfnxnrubrosiv.regimen=\'\'\',PRegimen ,\'\'\'\r\n			AND tbfnxnempleado.empdepar=tbfnxndepartamento.depcodigo\r\n			AND saldo<>0 \r\n			ORDER BY tbfnxnempleado.empcedula,tbfnxnrubros.rubtipo desc,tbfnxnrubros.ruborden \');\r\n\r\n	END IF;\r\n\r\n		SET @sqlt=texto;\r\n		PREPARE stmt FROM @sqlt;\r\n		EXECUTE stmt ;\r\n		DEALLOCATE PREPARE stmt;	\r\n\r\n		CALL Fnxsp_NCrearCurContAju(\'\');\r\n	\r\nEND\r\n;\r\n\r\n\r\n\r\n\r\n\r\n', '1) Subir el programa .\r\n- Titulo: Bonificaciones\r\n- Clase: WIN_tbfnxNENCBONO. \r\n', 1, NULL, 'Silvia', NULL, NULL, 'Nómina'),
(2, '2017-04-19', 'ACTUALIZACIÓN  PARA PRESTAMOS Y SRI 107', '/*\r\nRequired Proecdures\r\n*/\r\nDROP PROCEDURE IF EXISTS `addField`;\r\n\r\nCREATE  PROCEDURE `addField`(IN  tableName varchar(50), IN fieldName varchar(50), IN dataType varchar(50), IN size varchar(90), IN detail varchar(90))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	#Routine body goes here.\r\n	DECLARE existField INT(2);	\r\n	SET @qryAddField = \"\";\r\n	SELECT COUNT(column_name) INTO existField FROM information_schema.COLUMNS WHERE table_name = tableName AND column_name = fieldName AND  TABLE_SCHEMA=SCHEMA();\r\n	IF existField = 0 THEN\r\n		IF TRIM(size) <> \'\' THEN\r\n			SET size = CONCAT(\'(\',size,\')\');\r\n		END IF;\r\n\r\n		SET @qryAddField = CONCAT(\'ALTER TABLE \',tableName,\' ADD \', fieldName, \' \', dataType,  size ,\' \', detail, \";\");\r\n		\r\n		-- SELECT @qryAddField;\r\n		PREPARE stmt1 FROM @qryAddField;\r\n		EXECUTE stmt1;\r\n		DEALLOCATE PREPARE stmt1;\r\n	END IF; \r\nEND;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `rmConstraint`;\r\n\r\nCREATE PROCEDURE `rmConstraint`(IN constraintName varchar(180), IN tableName varchar(180), IN type varchar(180))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	SET @qryRmForeign = \'\';\r\n	IF EXISTS(\r\n		SELECT \r\n			constraint_name \r\n		FROM \r\n			information_schema.table_constraints\r\n		WHERE \r\n			table_schema    = DATABASE()     AND\r\n			table_name      = tableName       AND\r\n			constraint_name = constraintName AND\r\n			constraint_type = type)\r\n	THEN\r\n		SET @qryRmForeign = CONCAT(\'ALTER TABLE \', tableName, \' DROP \',type,\' \', constraintName, \';\');\r\n		SELECT @qryRmForeign;\r\n		PREPARE stmt FROM @qryRmForeign; \r\n		EXECUTE stmt; \r\n		DEALLOCATE PREPARE stmt;\r\n	END IF;\r\nEND;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `addConstraint`;\r\n\r\nCREATE PROCEDURE `addConstraint`(IN `fkName` varchar(180),IN `destTable` varchar(180),IN `destCampo` varchar(180),IN `oriTable` varchar(180),IN `oriCampo` varchar(180))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	#Routine body goes here.\r\n	SET @qryAddForeign = \'\';\r\n	IF NOT EXISTS(\r\n		SELECT \r\n			constraint_name \r\n		FROM \r\n			information_schema.table_constraints\r\n		WHERE \r\n			table_schema    = DATABASE()     AND\r\n			table_name      = destTable       AND\r\n			constraint_name = fkName AND\r\n			constraint_type = \'FOREIGN KEY\')\r\n	THEN\r\n		SET @qryAddForeign = CONCAT(\'ALTER TABLE \', destTable , \' ADD CONSTRAINT \',fkName,\' FOREIGN KEY(\', destCampo ,\') REFERENCES \', oriTable ,\'(\',oriCampo,\') ON DELETE RESTRICT ON UPDATE RESTRICT;\');\r\n		\r\n		SELECT @qryAddForeign;\r\n		PREPARE stmt FROM @qryAddForeign; \r\n		EXECUTE stmt; \r\n		DEALLOCATE PREPARE stmt;\r\n	END IF;\r\nEND;\r\n\r\n\r\n\r\n/*\r\n*	Nuevos Campos\r\n*/\r\nCALL addField(\'tbfnxnsri107\',\'sriA\',\'INT\',\'1\', \'\');\r\n/*\r\n*	Scripts para vistas\r\n*/\r\nDROP VIEW IF EXISTS `v_fnxn_totalesrubro`;\r\n\r\nDROP VIEW IF EXISTS `v_fnxntotales_sri2`;\r\n\r\nDROP VIEW IF EXISTS `v_fnxrubrossrii107`;\r\n\r\nDROP VIEW IF EXISTS `v_fnxn_sri107valor`;\r\n\r\nDROP VIEW IF EXISTS `v_fnxnrubrosperiodo2`;\r\n\r\nDROP VIEW IF EXISTS `v_fnxn_rubrosperiodo`;\r\n\r\nDROP VIEW IF EXISTS `v_fnx_numroles`;\r\n\r\nDROP VIEW IF EXISTS `v_fnx_totalessri`;\r\n\r\nDROP VIEW IF EXISTS `v_fnx_reporte`;\r\n\r\n/*Modificaciones de estructura*/\r\nCALL addField(\'tbfnxnsri107\',\'id\',\'INT\',\'11\',\'NOT NULL AUTO_INCREMENT FIRST ,DROP PRIMARY KEY,ADD PRIMARY KEY (`id`)\');\r\nCALL addField(\'tbfnxnhisrubros\',\'sriCode\',\'INT\',\'11\',\'\');\r\n\r\nCALL addConstraint(\'fk_sri_rubro\',\'tbfnxnhisrubros\', \'sriCode\', \'tbfnxnsri107\', \'id\');\r\n\r\nCALL rmConstraint(\'tbfnxnsri107xempleado_ibfk_4\', \'tbfnxnsri107xempleado\', \'FOREIGN KEY\');\r\n\r\nCALL addField(\'tbfnxnsri107xempleado\',\'sriCode\',\'INT\',\'11\',\'\');\r\nCALL addConstraint(\'fk_sri_sriempleado\', \'tbfnxnsri107xempleado\', \'sriCode\', \'tbfnxnsri107\', \'id\' );\r\n\r\n/*Queries de vistas*/\r\n\r\nCREATE \r\nSQL SECURITY INVOKER \r\nVIEW v_fnx_reporte AS \r\nSELECT\r\n	ER.encrolcodigo,\r\n	ER.encroldescrip,\r\n	EM.nombres,\r\n	DR.valor,\r\n	DR.empcedula,\r\n	RU.rubcodigo,\r\n	RU.rubdescrip,\r\n	SRI.nsridescrip,\r\n	SRI.nsricodigo,\r\n	SRI.nsrieditable\r\nFROM\r\n	tbfnxndetallerol DR\r\nINNER JOIN tbfnxnrubros RU ON DR.rubcodigo = RU.rubcodigo\r\nINNER JOIN tbfnxnsri107 SRI ON RU.rubsri = SRI.nsricodigo\r\nINNER JOIN tbfnxnencabezadorol ER ON DR.encrolcodigo = ER.encrolcodigo\r\nINNER JOIN tbfnxnempleado EM ON DR.empcedula = EM.empcedula\r\nWHERE\r\n	YEAR (DR.fecha) = \'2016\'\r\nAND ((ER.encroltipo1 = \'MENSUAL\')OR(ER.encroltipo1 = \'PPROVICION\' AND DR.rubcodigo = (SELECT HOM.homologado FROM tbfnxnhomologarubro HOM WHERE HOM.codigo = \'FNXD4\')) ) ;\r\n\r\n\r\nDROP VIEW IF EXISTS `vfnxconteonominas`;\r\nCREATE \r\nSQL SECURITY INVOKER \r\nVIEW `vfnxconteonominas` AS \r\n/*\r\n*Aqui se obtiene los empleados y el conteo de los RUBROS\r\n*/ \r\nSELECT DISTINCT\r\n	DR.empcedula,\r\n	COUNT(DR.ndetalle_id) AS nominas\r\nFROM\r\n	tbfnxndetallerol DR\r\nINNER JOIN tbfnxnencabezadorol ER ON DR.encrolcodigo = ER.encrolcodigo\r\nWHERE\r\n	YEAR (DR.fecha) = (SELECT periodo FROM tbfnxnglobal WHERE tbfnxnglobal.Codigo=1)\r\nAND (\r\n	ER.encroltipo1 = \'MENSUAL\'\r\n	OR (\r\n		ER.encroltipo1 = \'PPROVICION\'\r\n		AND DR.rubcodigo = (\r\n			SELECT\r\n				HOM.homologado\r\n			FROM\r\n				tbfnxnhomologarubro HOM\r\n			WHERE\r\n				HOM.codigo = \'FNXD4\'\r\n		)\r\n	)\r\n)\r\nGROUP BY\r\n	DR.empcedula,\r\n	DR.rubcodigo\r\nORDER BY\r\n	DR.empcedula,\r\n	COUNT(DR.ndetalle_id) DESC ;\r\n\r\n\r\n\r\nDROP VIEW IF EXISTS `vfnxnominaempleado`;\r\nCREATE \r\nSQL SECURITY INVOKER \r\nVIEW `vfnxnominaempleado` AS \r\n/*\r\n* Se obtiene el numero de nominas por empleado \r\n*/\r\nSELECT\r\n	N.empcedula,\r\n	MAX(N.nominas) AS nominas\r\nFROM\r\n	vfnxconteonominas N\r\nGROUP BY\r\n	N.empcedula ;\r\n\r\n\r\n\r\nDROP VIEW IF EXISTS `vfnxrubroshiscodigos`;\r\nCREATE\r\nSQL SECURITY INVOKER \r\nVIEW `vfnxrubroshiscodigos` AS \r\n/*\r\n* Rubros configurados en historicos \r\n*/\r\nSELECT\r\n	HR.encrolcodigo,\r\n	HR.rubcodigo,\r\n	SRI.id,\r\n	SRI.nsricodigo\r\nFROM\r\n	tbfnxnhisrubros HR\r\nINNER JOIN tbfnxnencabezadorol ER ON HR.encrolcodigo = ER.encrolcodigo\r\nINNER JOIN tbfnxnsri107 SRI ON HR.sriCode = SRI.id\r\nWHERE\r\n	YEAR (ER.fecha) = (SELECT periodo FROM tbfnxnglobal WHERE tbfnxnglobal.Codigo=1)\r\n\r\nAND SRI.nsriactivo = 1\r\nAND (\r\n	ER.encroltipo1 = \'MENSUAL\'\r\n	OR (\r\n		ER.encroltipo1 = \'PPROVICION\'\r\n		AND HR.rubcodigo = (\r\n			SELECT\r\n				HOM.homologado\r\n			FROM\r\n				tbfnxnhomologarubro HOM\r\n			WHERE\r\n				HOM.codigo = \'FNXD4\'\r\n		)\r\n	)\r\n)\r\nGROUP BY\r\n	HR.rubcodigo,\r\n	HR.sriCode ;\r\n\r\n\r\n\r\nDROP VIEW IF EXISTS `vfnxtotempleadorubro`;\r\nCREATE\r\nSQL SECURITY INVOKER \r\nVIEW `vfnxtotempleadorubro` AS \r\n/*\r\n* El total de rubros por empleado en un periodo\r\n*/\r\nSELECT\r\n	DR.fecha,\r\n	DR.empcedula,\r\n	DR.encrolcodigo,\r\n	DR.rubcodigo,\r\n	SUM(DR.valor) AS valRubro\r\nFROM\r\n	tbfnxndetallerol DR\r\nINNER JOIN tbfnxnencabezadorol ER ON DR.encrolcodigo = ER.encrolcodigo\r\nWHERE\r\n	YEAR (DR.fecha) = (\r\n		SELECT\r\n			periodo\r\n		FROM\r\n			tbfnxnglobal\r\n		WHERE\r\n			tbfnxnglobal.Codigo = 1\r\n	)\r\nAND (\r\n	ER.encroltipo1 = \'MENSUAL\'\r\n	OR (\r\n		ER.encroltipo1 = \'PPROVICION\'\r\n		AND DR.rubcodigo = (\r\n			SELECT\r\n				HOM.homologado\r\n			FROM\r\n				tbfnxnhomologarubro HOM\r\n			WHERE\r\n				HOM.codigo = \'FNXD4\'\r\n		)\r\n	)\r\n)\r\nGROUP BY\r\n	DR.empcedula,\r\n	DR.rubcodigo  ;\r\n\r\n\r\n\r\nDROP VIEW IF EXISTS `vfnxempleados107`;\r\nCREATE \r\nSQL SECURITY INVOKER \r\nVIEW `vfnxempleados107` AS \r\n/*\r\n* Es la sumatoria de emplados por codigo de sri\r\n*/\r\nSELECT\r\n	EMR.fecha,\r\n	EMR.encrolcodigo,\r\n	EMR.empcedula,\r\n	HC.id,\r\n	HC.nsricodigo,\r\n	NE.nominas,\r\n	SUM(EMR.valRubro) AS totCode\r\nFROM\r\n	vfnxtotempleadorubro EMR\r\nINNER JOIN vfnxrubroshiscodigos HC ON EMR.rubcodigo = HC.rubcodigo\r\nINNER JOIN vfnxnominaempleado NE ON EMR.empcedula = NE.empcedula\r\n GROUP BY EMR.empcedula, HC.id ;\r\n\r\n\r\n\r\nDROP VIEW IF EXISTS vfnxsriactual;\r\nCREATE\r\nSQL SECURITY INVOKER \r\nVIEW vfnxsriactual AS \r\n/*\r\n* SRI Actual considerando los confisurados con sus totales y \r\n* los no configurados con valor 0 para insertar\r\n*/\r\nSELECT\r\n	SRI.periodo,\r\n	NE.empcedula,\r\n	NE.nominas AS nominas,\r\n	0.00 AS valor,\r\n	SRI.id\r\nFROM \r\nvfnxnominaempleado NE, tbfnxnsri107 SRI\r\nWHERE \r\n	SRI.nsriactivo = 1\r\nAND SRI.id NOT IN(\r\n			SELECT RH.id FROM vfnxrubroshiscodigos RH GROUP BY RH.id\r\n		)\r\nAND \r\n	SRI.periodo = (SELECT periodo FROM tbfnxnglobal WHERE tbfnxnglobal.Codigo=1)\r\nUNION ALL\r\n	SELECT\r\n		YEAR (EM.fecha),\r\n		EM.empcedula,		\r\n		EM.nominas,\r\n		EM.totCode,\r\n		EM.id\r\n	FROM\r\n		vfnxempleados107 EM ;\r\n\r\n\r\nDROP FUNCTION IF EXISTS `FnxNDatos`;\r\n\r\nCREATE FUNCTION `FnxNDatos`(`tipo` int)\r\n RETURNS varchar(10)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	\r\n	DECLARE fnxDATO VARCHAR(10);\r\nCASE tipo\r\n	WHEN  0 THEN\r\n		SELECT (empcedula)  INTO fnxDATO FROM tbfnxnglobal WHERE tbfnxnglobal.Codigo=1;\r\n	WHEN 1 THEN\r\n		SELECT (periodo) INTO fnxDATO FROM tbfnxnglobal WHERE tbfnxnglobal.Codigo=1;\r\n	ELSE\r\n	SET fnxDATO=\'\'; \r\nEND CASE;\r\n	RETURN (fnxDATO);\r\nEND;\r\n\r\nDROP FUNCTION IF EXISTS `FnxNQuitaEspeciales`;\r\n\r\n\r\nCREATE  FUNCTION `FnxNQuitaEspeciales`(`Cadena` text)\r\n RETURNS text\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	\r\n	declare resultado TEXT;\r\n	SET resultado=Cadena;\r\n\r\n	set	resultado = REPLACE(resultado,\'├æ\',\'N\');\r\n	set	resultado = REPLACE(resultado,\'├▒\',\'n\');\r\n	set	resultado = REPLACE(resultado,\'├í\',\'a\');\r\n	set	resultado = REPLACE(resultado,\'├ü\',\'A\');\r\n	set	resultado = REPLACE(resultado,\'├®\',\'e\');\r\n	set	resultado = REPLACE(resultado,\'├ë\',\'E\');\r\n	set	resultado = REPLACE(resultado,\'├¡\',\'i\');\r\n	set	resultado = REPLACE(resultado,\'├ì\',\'I\');\r\n	set	resultado = REPLACE(resultado,\'├│\',\'o\');\r\n	set	resultado = REPLACE(resultado,\'├ô\',\'O\');\r\n	set	resultado = REPLACE(resultado,\'├║\',\'u\');\r\n	set	resultado = REPLACE(resultado,\'├Ü\',\'U\');\r\n	return resultado;\r\nEND;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxp_NDescargarPrestamoRol`;\r\n\r\nCREATE  PROCEDURE `Fnxp_NDescargarPrestamoRol`(IN `PENCROLCODIGO` VARCHAR(10))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	\r\n	DECLARE PEMPCEDULA VARCHAR(25);\r\n	DECLARE	PRUBCODIGO VARCHAR(25);\r\n	DECLARE	PSUMA Numeric(23,8);\r\n	DECLARE	PSALDO NUMERIC(23,8);\r\n	DECLARE	PENCPCODIGO INT;\r\n	DECLARE	PNCUOTA  INT ;\r\n	DECLARE D INT DEFAULT 0;\r\n\r\n	DECLARE CURPRESTAMO CURSOR FOR\r\n	SELECT empcedula,rubcodigo,SUM(ppimporte*-1) AS Suma\r\n	FROM tbfnxnpagoprestamo	WHERE encrolcodigo=PENCROLCODIGO group by empcedula, rubcodigo;\r\n\r\n		DECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\' SET D = 1;\r\n			SET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\n			 START TRANSACTION;\r\n\r\n				OPEN CURPRESTAMO;\r\n					FETCH CURPRESTAMO INTO  PEMPCEDULA,PRUBCODIGO,PSUMA;\r\n						 WHILE NOT D DO\r\n\r\n								UPDATE tbFnxNDetalleRol\r\n								SET valor=PSUMA\r\n								WHERE encrolcodigo=PENCROLCODIGO AND empcedula=PEMPCEDULA AND Rubcodigo=PRUBCODIGO;\r\n								\r\n								UPDATE tbfnxnpagoprestamo \r\n								SET  psalactualizado = 1\r\n								WHERE Encrolcodigo=PENCROLCODIGO AND empcedula=PEMPCEDULA AND Rubcodigo=PRUBCODIGO;\r\n								FETCH CURPRESTAMO INTO   PEMPCEDULA, PRUBCODIGO, PSUMA;\r\n\r\n						 END WHILE;\r\n								\r\n				CLOSE CURPRESTAMO;\r\n	COMMIT;	\r\nEND;\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizaPrestamoempleado`;\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NDesbloquearCobrosPrestamoRol`;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizarCodigoSri107HisRubros`;\r\n\r\nCREATE PROCEDURE `Fnxsp_NActualizarCodigoSri107HisRubros`(IN  `PPERIODO` varchar(10),IN `PRUBCODIGO` varchar(10))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\nDECLARE PENCROLCODIGO VARCHAR(10);\r\nDECLARE PRUBCOD VARCHAR(10);\r\nDECLARE PRUBSRI1 VARCHAR (10);\r\nDECLARE PRUBSRI VARCHAR (10);\r\nDECLARE D INT DEFAULT 0;\r\nDECLARE CURHISRUB CURSOR FOR\r\nSELECT encrolcodigo,rubcodigo,rubsri FROM tbfnxnhisrubros WHERE rubsri>0\r\n AND encrolcodigo IN(SELECT encrolcodigo FROM tbfnxnencabezadorol WHERE YEAR(fecha)=PPERIODO)\r\nAND rubcodigo =PRUBCODIGO GROUP BY encrolcodigo;\r\n\r\nDECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\' SET D = 1;\r\n			SET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\n			 START TRANSACTION;\r\n\r\n			OPEN CURHISRUB;\r\n						FETCH CURHISRUB INTO  PENCROLCODIGO,PRUBCOD,PRUBSRI1;\r\n								WHILE NOT D DO\r\n									SELECT nsricodigo INTO PRUBSRI FROM tbfnxnsri107 WHERE nsricodigo2= PRUBSRI1 AND periodo=PPERIODO;\r\n									UPDATE tbfnxnhisrubros SET rubsri2=PRUBSRI1 WHERE encrolcodigo=PENCROLCODIGO AND rubcodigo=PRUBCOD;\r\n									UPDATE tbfnxnhisrubros SET rubsri=PRUBSRI WHERE encrolcodigo=PENCROLCODIGO AND rubcodigo=PRUBCOD;\r\n									UPDATE tbfnxnsri107xempleado set sri107codigo =PRUBSRI where sri107periodo =PPERIODO and tbfnxnsri107xempleado.sri107codigo=PRUBSRI1;\r\n							FETCH CURHISRUB INTO   PENCROLCODIGO,PRUBCOD,PRUBSRI1;\r\n\r\n						END WHILE;\r\n								\r\n			CLOSE CURHISRUB;\r\nEND;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizarCodigosSri107Rubros`;\r\n\r\nCREATE PROCEDURE `Fnxsp_NActualizarCodigosSri107Rubros`(IN `PPERIODO` varchar(10))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n\r\nDECLARE PCODIGO1 VARCHAR(10);\r\nDECLARE PCODIGO2 VARCHAR(10);\r\nDECLARE PCODRUBRO VARCHAR (10);\r\nDECLARE PCODIGOHIST VARCHAR(10);\r\nDECLARE D INT DEFAULT 0;\r\nDECLARE CURRUBROS CURSOR FOR \r\nSELECT rubcodigo,rubsri\r\nFROM tbfnxnrubros \r\nWHERE rubsri IN (SELECT nsricodigo2 FROM tbfnxnsri107 WHERE  NSRICODIGO2 >0 AND PERIODO =PPERIODO);\r\n\r\nDECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\' SET D = 1;\r\n			SET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\n			 START TRANSACTION;\r\n\r\n			OPEN CURRUBROS;\r\n				FETCH CURRUBROS INTO  PCODRUBRO,PCODIGO1;\r\n						WHILE NOT D DO\r\n						\r\n							UPDATE tbfnxnrubros SET rubsri2 =PCODIGO1 where rubcodigo=PCODRUBRO;\r\n							SELECT nsricodigo into PCODIGO2 FROM tbfnxnsri107 WHERE PERIODO =PPERIODO and nsricodigo2=PCODIGO1;\r\n							UPDATE tbfnxnrubros SET rubsri =PCODIGO2 where rubcodigo=PCODRUBRO ;								\r\n							CALL Fnxsp_NActualizarCodigoSri107HisRubros(PPERIODO,PCODRUBRO );\r\n						FETCH CURRUBROS INTO   PCODRUBRO,PCODIGO1;\r\n\r\n				END WHILE;\r\n								\r\n			CLOSE CURRUBROS;\r\nEND;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizarDetallePrestamos`;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizarprestamos`;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizarSRI107`;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizaSaldosPrestamo`;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NCargarPrestamos`;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NActualizaUtilidades107`;\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NCalcularImpuesto`;\r\n\r\nCREATE PROCEDURE `Fnxsp_NCalcularImpuesto`(IN `periodo` varchar(4))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	\r\ndeclare fBasica decimal(23,8);\r\ndeclare rIMPFRACBASICA DECIMAL(23,8);\r\nDECLARE rIMPFRACXCEDENTE decimal(23,8);\r\nDECLARE rCALFRACEXE decimal(23,8);\r\nDECLARE rIMPUESTXCENTAJE decimal(23,8);\r\ndeclare imtotal decimal(23,8);\r\nDECLARE IFB decimal(23,8);\r\ndeclare codigo varchar(10);\r\nDECLARE FE decimal(23,8);\r\ndeclare IMMENSUAL DECIMAL(23,8);\r\ndeclare valor DECIMAL(23,8);\r\ndeclare cedula varchar(10);\r\nDECLARE d INT DEFAULT 0;\r\n\r\nDECLARE C_MAXIMPUESTO CURSOR FOR\r\nSELECT tbfnxnsri107xempleado.sri107empcedula,tbfnxnsri107xempleado.sri107valor\r\nFROM tbfnxnsri107xempleado LEFT JOIN tbfnxnsri107 ON tbfnxnsri107xempleado.sri107periodo = tbfnxnsri107.periodo and tbfnxnsri107xempleado.sri107codigo=tbfnxnsri107.nsricodigo\r\nwhere tbfnxnsri107xempleado.sri107periodo= periodo and  tbfnxnsri107.nsricodhml=\'basImp\' ;\r\n\r\n	DECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\' SET d =1;\r\n	SET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\n	START TRANSACTION;\r\n\r\n	SELECT tbfnxnsri107.nsricodigo into codigo FROM  tbfnxnsri107 where  tbfnxnsri107.nsricodhml=\'impRentCaus\' and tbfnxnsri107.periodo=periodo;\r\n\r\nOPEN C_MAXIMPUESTO;\r\n		FETCH C_MAXIMPUESTO INTO cedula,valor;\r\n		WHILE NOT d	DO\r\n			SELECT irdesde into fBasica  from tbfnxnimpuestorenta \r\n			WHERE irdesde<=valor and irhasta>= valor and irperiodo = periodo;\r\n\r\n			select  irvalor1 into rIMPFRACBASICA   from tbfnxnimpuestorenta \r\n			where irdesde<=valor and irhasta>= valor and irperiodo = periodo;\r\n\r\n			select irvalor2 into rIMPFRACXCEDENTE  from tbfnxnimpuestorenta \r\n			where irdesde<=valor and irhasta>= valor and irperiodo = periodo; \r\n\r\n			SET rCALFRACEXE =valor-fBasica;\r\n			SET rIMPUESTXCENTAJE=rCALFRACEXE*(rIMPFRACXCEDENTE/100);\r\n			SET imtotal=rIMPUESTXCENTAJE+rIMPFRACBASICA;\r\n			SELECT imtotal;\r\n			select CEDULA;\r\n			update tbfnxnsri107xempleado \r\n			SET tbfnxnsri107xempleado.sri107valor=imtotal\r\n			WHERE tbfnxnsri107xempleado.sri107empcedula= CEDULA AND tbfnxnsri107xempleado.sri107codigo=codigo and  tbfnxnsri107xempleado.sri107periodo=periodo;\r\n		FETCH C_MAXIMPUESTO INTO cedula,valor;\r\n		END WHILE;\r\n	CLOSE C_MAXIMPUESTO;\r\nEND;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NObtenerSri107`;\r\n\r\nDROP PROCEDURE IF EXISTS `fnxsp_NSRI107Calculoformulas`;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NTransferirImpuestoSRI107`;\r\n\r\nCREATE PROCEDURE `Fnxsp_NTransferirImpuestoSRI107`(IN `PPERIODO` VARCHAR(10))\r\nBEGIN\r\n\r\nDECLARE PEMPCEDULA VARCHAR (10);\r\nDECLARE apoPerIess NUMERIC (23, 8);\r\nDECLARE deducVivienda NUMERIC (23, 8);\r\nDECLARE deducSalud NUMERIC (23, 8);\r\nDECLARE deducEduca NUMERIC (23, 8);\r\nDECLARE deducAliement NUMERIC (23, 8);\r\nDECLARE deducVestim NUMERIC (23, 8);\r\nDECLARE exoDiscap NUMERIC (23, 8);\r\nDECLARE exoTerEd NUMERIC (23, 8);\r\nDECLARE basImp NUMERIC (23, 8);\r\nDECLARE impRentCaus NUMERIC (23, 8);\r\nDECLARE D INT DEFAULT 0;\r\n\r\nDECLARE CURIMPUESTO CURSOR FOR SELECT\r\n	empcedula,\r\n	AportePerIESS,\r\n	ConceptoGastoMViviendaDi,\r\n	ConceptoGastoMSaludDi,\r\n	ConceptoGastoMEduDi,\r\n	ConceptoGastoMAlimDi,\r\n	ConceptoGastoMVestDi,\r\n	DescDiscap,\r\n	DescTercedad,\r\n	BaseImpDesGast,\r\n	ImpuestoTotal\r\nFROM\r\n	tbfnxncalimpuestorenta\r\nWHERE\r\n	periodo = PPERIODO;\r\n\r\nDECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\'\r\nSET D = 1;\r\n\r\nSET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\nSTART TRANSACTION;\r\n\r\nOPEN CURIMPUESTO;\r\nFETCH CURIMPUESTO INTO PEMPCEDULA,\r\n apoPerIess,\r\n deducVivienda,\r\n deducSalud,\r\n deducEduca,\r\n deducAliement,\r\n deducVestim,\r\n exoDiscap,\r\n exoTerEd,\r\n basImp,\r\n impRentCaus;\r\n\r\n\r\nWHILE NOT D DO\r\n	UPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = apoPerIess\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'apoPerIess\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = deducVivienda\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'deducVivienda\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = deducSalud\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'deducSalud\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = deducEduca\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'deducEduca\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = deducAliement\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'deducAliement\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = deducVestim\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'deducVestim\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = exoDiscap\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'exoDiscap\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = exoTerEd\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'exoTerEd\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = basImp\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'basImp\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nUPDATE tbfnxnsri107xempleado\r\nSET tbfnxnsri107xempleado.sri107valor = impRentCaus\r\nWHERE\r\n	sri107codigo IN (\r\n		SELECT\r\n			nsricodigo\r\n		FROM\r\n			tbfnxnsri107\r\n		WHERE\r\n			tbfnxnsri107.nsricodhml = \'impRentCaus\'\r\n	)\r\nAND sri107empcedula = PEMPCEDULA\r\nAND tbfnxnsri107xempleado.sri107periodo = PPERIODO;\r\n\r\nFETCH CURIMPUESTO INTO PEMPCEDULA,\r\n apoPerIess,\r\n deducVivienda,\r\n deducSalud,\r\n deducEduca,\r\n deducAliement,\r\n deducVestim,\r\n exoDiscap,\r\n exoTerEd,\r\n basImp,\r\n impRentCaus;\r\n\r\n\r\nEND\r\nWHILE;\r\n\r\nCLOSE CURIMPUESTO;\r\n\r\nCOMMIT;\r\n\r\n\r\nEND;\r\n\r\n\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NValidaCampos`;\r\n\r\nCREATE PROCEDURE `Fnxsp_NValidaCampos`(`Ptabla` varchar(100),`Pcampo` varchar(100),`Pvalor` varchar(100),`Pcampo2` varchar(100),`Pvalor2`varchar(100),`PCODIGO` TEXT)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\nDECLARE validacion int ;\r\nDECLARE TES TEXT;\r\nDROP VIEW IF EXISTS `v_fnxn_validadatos`;\r\nSET @mystate = Pvalor;\r\nSET @sql1 = CONCAT(\'CREATE VIEW v_fnxn_validadatos as SELECT count(\',pcampo,\')as total FROM \',ptabla,\' where \',Pcampo,\' =\'\'\',Pvalor,\'\'\'   and \',pcampo2,\'=\'\'\',pvalor2,\'\'\'\');   \r\n\r\nPREPARE stmt FROM @sql1;\r\nEXECUTE stmt ;\r\nDEALLOCATE PREPARE stmt;\r\n\r\nSELECT total into validacion FROM v_fnxn_validadatos ;\r\nIF validacion >0 THEN\r\n SELECT \'Ya existen registros\';\r\nELSE\r\n	SET TES =PCODIGO;\r\n	SET @sql=TES;\r\n	PREPARE stmt FROM @sql ;\r\n	EXECUTE stmt ;\r\n	DEALLOCATE PREPARE stmt;\r\nSELECT \'Insercion correcta\';\r\n\r\nEND IF ;\r\nEND;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `pivotCodigosEmpleado`;\r\n\r\nCREATE PROCEDURE `pivotCodigosEmpleado`(anio INT(6))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	#Proceso para pivotear la columna codigos para calcular formulas \r\n	SET @@group_concat_max_len = 15000;\r\n	SET @sql = NULL;\r\n	SELECT\r\n		GROUP_CONCAT(DISTINCT\r\n			CONCAT(\r\n				\'MAX(IF(SRI.id = \'\'\',\r\n				SRI.id,\r\n				\'\'\', XEM.sri107valor, 0)) AS \\\'_\',\r\n				SRI.nsricodigo,IF(SRI.sriA,\'A\',\'\'),\'\\\'\'\r\n			)\r\n		) INTO @sql\r\n	FROM tbfnxnsri107xempleado XEM INNER JOIN tbfnxnsri107 SRI ON XEM.sriCode = SRI.id;\r\n\r\n	SET @sql = CONCAT(\'SELECT XEM.sri107periodo, XEM.sri107empcedula, \', \r\n			@sql, \r\n			\' FROM tbfnxnsri107xempleado XEM INNER JOIN tbfnxnsri107 SRI ON XEM.sriCode = SRI.id WHERE XEM.sri107periodo = \\\'\',\r\n			anio,\r\n			\'\\\' GROUP BY XEM.sri107empcedula\');\r\n	-- SELECT @sql;\r\n	PREPARE stmt1 FROM @sql;\r\n	EXECUTE stmt1;\r\n	DEALLOCATE PREPARE stmt1;\r\n	\r\nEND;', 'Para este proceso es necesario actualizar el FenixApps y el FnxNominaPrestamos.wdl(Libreria)', 1, NULL, 'wacurio', NULL, NULL, 'Nómina');
