(3, '2017-07-04', 'UTILIDAD, ROL ACUMULADO, HOMOLOGACION', '\r\nINSERT INTO `tbfnxnhomologarubro` (`codigo`, `descripcion`, `estado`, `homologado`, `tipo`) \r\nSELECT \'BONOI\',\'PAGO NOMINA BONO\',1,\'\',\'\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `codigo` FROM `tbfnxnhomologarubro` WHERE `tbfnxnhomologarubro`.`codigo` = \'BONOI\' );\r\n\r\nINSERT INTO `tbfnxnhomologarubro` (`codigo`, `descripcion`, `estado`, `homologado`, `tipo`) \r\nSELECT \'D3PP\',\'DECIMO III PROVISION\',1,\'\',\'\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `codigo` FROM `tbfnxnhomologarubro` WHERE `tbfnxnhomologarubro`.`codigo` = \'D3PP\' );\r\n\r\nINSERT INTO `tbfnxnhomologarubro` (`codigo`, `descripcion`, `estado`, `homologado`, `tipo`) \r\nSELECT \'D4PP\',\'DECIMO IV PROVISION\',1,\'\',\'\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `codigo` FROM `tbfnxnhomologarubro` WHERE `tbfnxnhomologarubro`.`codigo` = \'D4PP\' );\r\n\r\nINSERT INTO `tbfnxnhomologarubro` (`codigo`, `descripcion`, `estado`, `homologado`, `tipo`) \r\nSELECT \'DECIMO4\',\'DECIMO CUARTO\',1,\'\',\'\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `codigo` FROM `tbfnxnhomologarubro` WHERE `tbfnxnhomologarubro`.`codigo` = \'DECIMO4\' );\r\n\r\nINSERT INTO `tbfnxnhomologarubro` (`codigo`, `descripcion`, `estado`, `homologado`, `tipo`) \r\nSELECT \'VAC\',\'VACACIONES\',1,\'\',\'\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `codigo` FROM `tbfnxnhomologarubro` WHERE `tbfnxnhomologarubro`.`codigo` = \'VAC\' );\r\n\r\nCALL addField (\'tbfnxnutilidad\',\'decimoiii\',\'decimal\',\'23,8\',\'DEFAULT 0\');\r\nCALL addField (\'tbfnxnutilidad\',\'decimoiv\',\'decimal\',\'23,8\',\'DEFAULT 0\');\r\nCALL addField (\'tbfnxnutilidad\',\'participauti\',\'decimal\',\'23,8\',\'DEFAULT 0\');\r\nCALL addField (\'tbfnxnutilidad\',\'salario\',\'decimal\',\'23,8\',\'DEFAULT 0\');\r\nCALL addField (\'tbfnxnutilidad\',\'fondore\',\'decimal\',\'23,8\',\'DEFAULT 0\');\r\nDELETE FROM `tbfnxntablastraspaso` WHERE (`tabla`=\'tbfnxncalimpuestorenta\');\r\n\r\n-- --------------------