	tbfnxnhisempleadotemp.fnxempdiscapacidad,tbfnxnhisempleadotemp.fnxempdiscapacidadgrado,\r\n	tbfnxnhisempleadotemp.fnxjefe,tbfnxnhisempleadotemp.fnxempcodigojefe,\r\n	tbfnxnhisempleadotemp.regimen,tbfnxnhisempleadotemp.empcondias,tbfnxnhisempleadotemp.empconfecha \r\nFROM tbfnxnhisempleadotemp;\r\n\r\n\r\n \r\nEND\r\n ;\r\n\r\n\r\n\r\n-- ----------------------------\r\n-- Procedure structure for Fnxsp_NCargarRolAcum\r\n-- ----------------------------\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NCargarRolAcum`;\r\n\r\nCREATE DEFINER=`root`@`localhost` PROCEDURE `Fnxsp_NCargarRolAcum`(IN RolEncForm VARCHAR(10), IN RolFechaForm DATE)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	\r\n\r\n\r\n		DECLARE empcedulaV VARCHAR(10);\r\n		DECLARE lbEntro INT;\r\n		DECLARE ExisteRol INT(2) DEFAULT 0;\r\n		DECLARE ExisteCambio INT(2) DEFAULT 0;\r\n\r\n		DECLARE RubCodigo VARCHAR(10);\r\n		DECLARE RubValorV NUMERIC(23,8);\r\n		DECLARE rubtipoicV  VARCHAR(1);\r\n		DECLARE RubTipo VARCHAR(10);\r\n		DECLARE Ruborden NUMERIC(23,8);\r\n		DECLARE Rubformula TEXT;\r\n		DECLARE	rubprovisionV INT(1);\r\n		DECLARE	rubfnxgravableV INT(1);\r\n\r\n		DECLARE rubcodigoV VARCHAR(10);\r\n		DECLARE hechoCurAct INT DEFAULT 0;\r\n		DECLARE cuantoCurAct INT DEFAULT 0;\r\n		DECLARE hechoCurRubro INT DEFAULT 0;\r\n		DECLARE CurRubro INT DEFAULT FALSE;\r\n\r\n		\r\n		DECLARE CurAct CURSOR FOR \r\n				SELECT tbfnxnhisempleado.empcedula\r\n				FROM tbfnxnhisempleado\r\n				INNER JOIN tbfnxnrolesacum ON tbfnxnhisempleado.encrolcodigo=tbfnxnrolesacum.encrolacum		\r\n				WHERE tbfnxnrolesacum.encrolcodigo=RolEncForm \r\n				GROUP BY empcedula\r\n				ORDER By tbfnxnhisempleado.nombres;\r\n\r\n		\r\n		DECLARE CurRubro CURSOR FOR\r\n				SELECT tbfnxnhisrubros.rubcodigo,tbfnxnhisrubros.Rubvalor,tbfnxnhisrubros.RubTipoic,tbfnxnhisrubros.RubTipo,tbfnxnhisrubros.Ruborden,tbfnxnhisrubros.Rubformula,tbfnxnhisrubros.rubprovision,tbfnxnhisrubros.rubfnxgravable\r\n				FROM tbfnxnhisrubros\r\n				INNER JOIN tbfnxnrolesacum ON tbfnxnhisrubros.encrolcodigo=tbfnxnrolesacum.encrolacum\r\n				WHERE tbfnxnrolesacum.encrolcodigo=RolEncForm \r\n				GROUP BY tbfnxnhisrubros.rubcodigo\r\n				ORDER BY tbfnxnhisrubros.rubtipo DESC, tbfnxnhisrubros.ruborden;\r\n			\r\n		DECLARE CONTINUE HANDLER FOR NOT FOUND SET hechoCurRubro =1;\r\n\r\n		START TRANSACTION;\r\n\r\n\r\n		SELECT  COUNT(tbfnxnhisempleado.empcedula) INTO hechoCurAct FROM tbfnxnhisempleado INNER JOIN tbfnxnrolesacum ON tbfnxnhisempleado.encrolcodigo=tbfnxnrolesacum.encrolacum		\r\n		WHERE tbfnxnrolesacum.encrolcodigo=RolEncForm;\r\n		\r\n		SELECT COUNT(tbFnxNDetalleRol.Encrolcodigo) INTO ExisteRol FROM tbFnxNDetalleRol WHERE  tbFnxNDetalleRol.Encrolcodigo=RolEncForm;\r\n\r\n			\r\n			IF ExisteRol = 0	THEN				 							\r\n\r\n					\r\n					OPEN CurAct;\r\n\r\n						loopCurAct:LOOP\r\n							FETCH CurAct INTO empcedulaV;\r\n							SET cuantoCurAct=cuantoCurAct+1;\r\n							\r\n							IF cuantoCurAct>hechoCurAct THEN				\r\n								LEAVE loopCurAct;\r\n							END IF;		\r\n					\r\n									OPEN CurRubro;\r\n										loopCurRubro:LOOP\r\n											FETCH CurRubro INTO rubcodigoV,RubvalorV,RubTipoicV,RubTipo,Ruborden,Rubformula,rubprovisionV,rubfnxgravableV;\r\n											IF hechoCurRubro THEN										\r\n												LEAVE loopCurRubro;\r\n											END IF;		\r\n																					\r\n											INSERT INTO tbFnxNDetalleRol(tbFnxNDetalleRol.Encrolcodigo,tbFnxNDetalleRol.Empcedula,tbFnxNDetalleRol.RubCodigo,tbFnxNDetalleRol.Fecha,tbFnxNDetalleRol.valor,tbFnxNDetalleRol.RubTipoic,tbFnxNDetalleRol.RubTipo,tbFnxNDetalleRol.Ruborden,tbFnxNDetalleRol.Rubformula,rubprovision,tbFnxNDetalleRol.Rubfnxgravable) \r\n											VALUES(RolEncForm,empcedulaV,rubcodigoV,RolFechaForm,RubvalorV,RubTipoicV,RubTipo,Ruborden,Rubformula,rubprovisionV,rubfnxgravableV);\r\n										\r\n\r\n										END LOOP loopCurRubro;\r\n							\r\n									CLOSE CurRubro;\r\n									 SET hechoCurRubro=0;\r\n\r\n						END LOOP loopCurAct;\r\n\r\n					CLOSE CurAct;\r\n	\r\n			ELSE\r\n		\r\n				\r\n			 	CALL Fnxsp_NCargarRolAcumDatos(RolEncForm ,RolEncForm);\r\n\r\n			END IF;\r\n\r\n			\r\n			SELECT COUNT(rolcodigo) INTO ExisteCambio FROM tbFnxNCambiosRol WHERE  tbFnxNCambiosRol.rolcodigo=RolEncForm;\r\n			IF ExisteCambio = 0 THEN			\r\n					INSERT INTO tbFnxNCambiosRol (tbFnxNCambiosRol.rolcodigo,tbFnxNCambiosRol.Estado,Negativo) VALUES (RolEncForm,0,0); 				\r\n			ELSE			\r\n					UPDATE tbFnxNCambiosRol SET tbFnxNCambiosRol.Estado=0,negativo=0 WHERE tbFnxNCambiosRol.rolcodigo=RolEncForm;\r\n			END IF;\r\n			\r\n			UPDATE tbFnxninit SET tbFnxninit.rolcodigo=RolEncForm;\r\n	COMMIT;\r\nEND\r\n ;\r\n\r\n-- ----------------------------\r\n-- Procedure structure for Fnxsp_NCargarRolAcumDatos\r\n-- ----------------------------\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NCargarRolAcumDatos`;\r\n\r\nCREATE DEFINER=`root`@`localhost` PROCEDURE `Fnxsp_NCargarRolAcumDatos`(IN RolEncForm VARCHAR(10), IN RolFechaForm DATE)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n		\r\n		START TRANSACTION;\r\n\r\n		DELETE FROM tbFnxNDetalleRol WHERE tbFnxNDetalleRol.Encrolcodigo=RolEncForm  \r\n			AND tbFnxNDetalleRol.empcedula IN (\r\n					SELECT FnxDet.empcedula\r\n				FROM (SELECT DISTINCT empcedula FROM tbfnxndetallerol WHERE tbfnxndetallerol.encrolcodigo=RolEncForm ) AS FnxDet\r\n				LEFT JOIN \r\n				(SELECT tbfnxnhisempleado.empcedula FROM tbfnxnhisempleado\r\n						INNER JOIN tbfnxnrolesacum ON tbfnxnhisempleado.encrolcodigo=tbfnxnrolesacum.encrolacum		\r\n						WHERE tbfnxnrolesacum.encrolcodigo=RolEncForm GROUP BY empcedula) AS FnxHis\r\n				ON FnxDet.empcedula=FnxHis.empcedula\r\n				WHERE FnxHis.empcedula IS NULL\r\n				);\r\n\r\n\r\n		DELETE FROM tbFnxNDetalleRol WHERE tbFnxNDetalleRol.Encrolcodigo=RolEncForm  \r\n			AND tbFnxNDetalleRol.rubcodigo IN (SELECT FnxDet.rubcodigo\r\n		FROM (SELECT DISTINCT rubcodigo FROM tbfnxndetallerol WHERE tbfnxndetallerol.encrolcodigo=RolEncForm) AS FnxDet\r\n		LEFT JOIN \r\n		(SELECT tbfnxnhisrubros.rubcodigo FROM tbfnxnhisrubros\r\n					INNER JOIN tbfnxnrolesacum ON tbfnxnhisrubros.encrolcodigo=tbfnxnrolesacum.encrolacum\r\n					WHERE tbfnxnrolesacum.encrolcodigo=RolEncForm GROUP BY tbfnxnhisrubros.rubcodigo) AS FnxHis\r\n			ON FnxDet.rubcodigo=FnxHis.rubcodigo WHERE FnxHis.rubcodigo IS NULL\r\n		);\r\n\r\n\r\n\r\n 	call Fnxsp_NCargarRolAcumDatosEmpRub(RolEncForm ,RolFechaForm );\r\n	\r\n\r\n	COMMIT;\r\nEND\r\n ;\r\n\r\n-- ----------------------------\r\n-- Procedure structure for Fnxsp_NCargarRolAcumDatosEmpRub\r\n-- ----------------------------\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NCargarRolAcumDatosEmpRub`;\r\n\r\nCREATE DEFINER=`root`@`localhost` PROCEDURE `Fnxsp_NCargarRolAcumDatosEmpRub`(IN RolEncForm VARCHAR(10), IN RolFechaForm DATE)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	\r\n\r\n		DECLARE empcedulaV VARCHAR(10);\r\n		DECLARE Existe INT(2) DEFAULT 0;\r\n\r\n		DECLARE RubValorV NUMERIC(23,8);\r\n		DECLARE rubtipoicV  VARCHAR(1);\r\n		DECLARE RubTipo VARCHAR(10);\r\n		DECLARE Ruborden NUMERIC(23,8);\r\n		DECLARE Rubformula TEXT;\r\n		DECLARE	rubprovisionV INT(1);\r\n		DECLARE	rubfnxgravableV INT(1);\r\n\r\n		DECLARE rubcodigoV VARCHAR(10);\r\n		DECLARE hechoCurAct INT DEFAULT 0;\r\n		DECLARE cuantoCurAct INT DEFAULT 0;\r\n		DECLARE hechoCurRubro INT DEFAULT 0;\r\n		DECLARE CurRubro INT DEFAULT FALSE;\r\n		DECLARE d INT DEFAULT 0;\r\n		DECLARE cuantoCurRubro INT DEFAULT 0;\r\n		\r\n		DECLARE CurAct CURSOR FOR \r\n		SELECT tbfnxnhisempleado.empcedula FROM tbfnxnhisempleado,tbfnxnrolesacum\r\n		where tbfnxnhisempleado.encrolcodigo=tbfnxnrolesacum.encrolacum\r\n		AND tbfnxnrolesacum.encrolcodigo=RolEncForm\r\n		GROUP BY empcedula\r\n		order by tbfnxnhisempleado.nombres;\r\n\r\n\r\n		DECLARE CurRubro CURSOR FOR \r\n		SELECT tbfnxnhisrubros.rubcodigo,tbfnxnhisrubros.Rubvalor,tbfnxnhisrubros.RubTipoic,tbfnxnhisrubros.RubTipo,tbfnxnhisrubros.Ruborden,tbfnxnhisrubros.Rubformula,tbfnxnhisrubros.rubprovision,tbfnxnhisrubros.rubfnxgravable\r\n		FROM tbfnxnhisrubros\r\n		WHERE tbfnxnhisrubros.rubestado=1		\r\n		GROUP BY  tbfnxnhisrubros.rubcodigo;\r\n\r\n	\r\n		DECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\' SET d = 1;\r\n\r\n		START TRANSACTION;\r\n\r\n		SELECT COUNT(EmpC.empcodigo) INTO hechoCurAct  FROM\r\n		(SELECT tbfnxnhisempleado.empcodigo\r\n		FROM tbfnxnhisempleado,tbfnxnrolesacum\r\n		WHERE tbfnxnhisempleado.encrolcodigo=tbfnxnrolesacum.encrolacum\r\n		AND tbfnxnrolesacum.encrolcodigo=RolEncForm\r\n		GROUP BY empcedula) AS EmpC;\r\n		\r\n			\r\n		SELECT COUNT(RubC.RubCodigo) INTO hechoCurRubro  FROM\r\n		(SELECT tbfnxnhisrubros.RubCodigo\r\n		FROM tbfnxnhisrubros\r\n		WHERE tbfnxnhisrubros.rubestado=1 \r\n		GROUP BY  tbfnxnhisrubros.rubcodigo) RubC;\r\n\r\n			OPEN CurAct;\r\n\r\n				loopCurAct:LOOP\r\n					FETCH CurAct INTO empcedulaV;\r\n					\r\n					 \r\n					SET cuantoCurRubro=0;\r\n					IF cuantoCurAct>=hechoCurAct THEN				\r\n						LEAVE loopCurAct;\r\n					END IF;		\r\n			\r\n					\r\n						\r\n							OPEN CurRubro;\r\n								loopCurRubro:LOOP\r\n									FETCH CurRubro INTO rubcodigoV,RubvalorV,RubTipoicV,RubTipo,Ruborden,Rubformula,rubprovisionV,rubfnxgravableV;\r\n									\r\n									IF cuantoCurRubro>=hechoCurRubro THEN								\r\n										LEAVE loopCurRubro;\r\n									END IF;\r\n\r\n								SELECT COUNT(encrolcodigo) INTO Existe  FROM tbfnxndetallerol WHERE tbfnxndetallerol.empcedula=empcedulaV AND tbfnxndetallerol.rubcodigo=rubcodigoV AND tbfnxndetallerol.encrolcodigo=RolEncForm; \r\n								\r\n								IF Existe= 0	THEN\r\n											\r\n									INSERT INTO tbFnxNDetalleRol(tbFnxNDetalleRol.Encrolcodigo,tbFnxNDetalleRol.Empcedula,tbFnxNDetalleRol.RubCodigo,tbFnxNDetalleRol.Fecha,tbFnxNDetalleRol.valor,tbFnxNDetalleRol.RubTipoic,tbFnxNDetalleRol.RubTipo,tbFnxNDetalleRol.Ruborden,tbFnxNDetalleRol.Rubformula,rubprovision,tbFnxNDetalleRol.Rubfnxgravable) \r\n									VALUES(RolEncForm,empcedulaV,rubcodigoV,RolFechaForm,RubvalorV,RubTipoicV,RubTipo,Ruborden,Rubformula,rubprovisionV,rubfnxgravableV);\r\n								END IF;	\r\n\r\n								 SET cuantoCurRubro=cuantoCurRubro+1;\r\n\r\n								END LOOP loopCurRubro;\r\n							\r\n							CLOSE CurRubro;\r\n							 \r\n					SET cuantoCurAct=cuantoCurAct+1;\r\n				END LOOP loopCurAct;\r\n\r\n			CLOSE CurAct;\r\n	COMMIT;\r\nEND\r\n;\r\n', '', 1, NULL, 'stravez', NULL, NULL, 'Nómina');
INSERT INTO `tbsysnactualiza` (`FnxNumero`, `FnxFecha`, `FnxDescripcion`, `FnxScriptSQL`, `FnxManual`, `FnxEstado`, `FnxScriptSQLSistem`, `FnxUsuario`, `FnxFechaActualizacion`, `FnxUsuarioActualizacion`, `FnxProyecto`) VALUES
(4, '2017-08-04', 'BONOS, ROL ACUMULADO', '\r\n-- ----------------------------\r\n-- Procedure structure for Fnxsp_NCargarBonosRol\r\n-- ----------------------------\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NCargarBonosRol`;\r\n\r\nCREATE DEFINER=`edoc`@`%` PROCEDURE `Fnxsp_NCargarBonosRol`(IN RolEncForm VARCHAR(10))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n\r\nDECLARE pcedula VARCHAR (10);\r\nDECLARE pvalor NUMERIC (23, 8);\r\nDECLARE d INT DEFAULT 0;\r\nDECLARE prubro VARCHAR (10);\r\nDECLARE paprobado INT DEFAULT 0;\r\n\r\nDECLARE curbono CURSOR FOR \r\nSELECT D.empcedula,D.total\r\nFROM tbfnxndetbono AS D\r\nLEFT JOIN tbfnxnencbono AS E ON D.codigobo=E.codigobo \r\nWHERE E.aprobado=\'1\' AND D.total>0\r\nAND E.encrolcodigo= RolEncForm;\r\n\r\n\r\n\r\nDECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\'\r\nSET d = 1;\r\nSET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\nSTART TRANSACTION;\r\n\r\nSELECT tbfnxnencbono.rubcodigoi INTO prubro FROM tbfnxnencbono WHERE tbfnxnencbono.encrolcodigo=RolEncForm;\r\nSELECT tbfnxnencbono.aprobado INTO paprobado FROM tbfnxnencbono WHERE tbfnxnencbono.encrolcodigo=RolEncForm;\r\n\r\n\r\nIF paprobado=1 THEN\r\n\r\nOPEN curbono;\r\n\r\n	FETCH curbono INTO pcedula, pvalor;\r\n		WHILE NOT d DO\r\n			UPDATE tbFnxNDetalleRol\r\n			SET valor = pvalor\r\n			WHERE\r\n				encrolcodigo = RolEncForm \r\n			AND empcedula = pcedula\r\n			AND Rubcodigo = prubro;\r\n\r\n			FETCH curbono INTO pcedula, pvalor;\r\n		END\r\n	WHILE;\r\n\r\n	CLOSE curbono;\r\nEND IF;\r\n\r\nCOMMIT;\r\n\r\nEND\r\n;\r\n\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NGenerar`;\r\n\r\nCREATE DEFINER=``@`%` PROCEDURE `Fnxsp_NGenerar`(IN RolEncForm VARCHAR(10), IN Metodo INT)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n\r\n	-- \'0000000148\'\r\n	 DROP VIEW IF EXISTS `v_fnxncurcont`;\r\n\r\n	IF Metodo=1 THEN\r\n		CREATE SQL SECURITY  INVOKER VIEW v_fnxncurcont AS\r\n		SELECT tbfnxnhisempleado.encrolcodigo,tbfnxnhisempleado.empcedula,COALESCE(tbfnxnhisempleado.empcodcon,\'\') AS empcodcon,\r\n			COALESCE(empdepar,\'\') AS empdepar,COALESCE(depcodcon,\'\') AS depcodcon,COALESCE(depcodcon2,\'\') AS depcodcon2,COALESCE(tbfnxnhisempleado.coscodigo,\'\') AS coscodigo, \r\n			COALESCE(tbfnxnhisrubros.rubcodigo,\'\') AS rubcodigo,COALESCE(tbfnxnhisrubros.rubextension,\'\') AS rubextension, \r\n			COALESCE(tbfnxndetallerol.valor,0) AS valor,COALESCE(codcta1d,\'\') AS codctad,COALESCE(rubcodcta1d,\'\') AS rubcodctad,COALESCE(codcta1h,\'\') AS codctah,COALESCE(rubcodcta1h,\'\') AS rubcodctah\r\n			FROM tbfnxnhisempleado,tbfnxnhisrubros,tbfnxndetallerol,tbfnxndepartamento\r\n			WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula \r\n			AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo \r\n			AND tbfnxndetallerol.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisrubros.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\r\n			AND valor<>0 \r\n			ORDER BY tbfnxnhisempleado.empcedula,tbfnxnhisrubros.rubtipo desc,tbfnxnhisrubros.ruborden ;\r\n\r\n	END IF;\r\n\r\nIF Metodo=2 THEN\r\n		CREATE SQL SECURITY  INVOKER VIEW v_fnxncurcont AS\r\n		SELECT tbfnxnhisempleado.encrolcodigo,tbfnxnhisempleado.empcedula,COALESCE(tbfnxnhisempleado.empcodcon,\'\') AS empcodcon,\r\n			COALESCE(empdepar,\'\') AS empdepar,COALESCE(depcodcon,\'\') AS depcodcon,COALESCE(depcodcon2,\'\') AS depcodcon2,COALESCE(tbfnxnhisempleado.coscodigo,\'\') AS coscodigo, \r\n			COALESCE(tbfnxnhisrubros.rubcodigo,\'\') AS rubcodigo,COALESCE(tbfnxnhisrubros.rubextension,\'\') AS rubextension, \r\n			COALESCE(tbfnxndetallerol.valor,0) AS valor,COALESCE(codcta2d,\'\') AS codctad,COALESCE(rubcodcta2d,\'\') AS rubcodctad,COALESCE(codcta2h,\'\') AS codctah,COALESCE(rubcodcta2h,\'\') AS rubcodctah\r\n			FROM tbfnxnhisempleado,tbfnxnhisrubros,tbfnxndetallerol,tbfnxndepartamento\r\n			WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula \r\n			AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo \r\n			AND tbfnxndetallerol.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisrubros.encrolcodigo=FnxNRol()\r\n			AND tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\r\n			AND valor<>0 \r\n			ORDER BY tbfnxnhisempleado.empcedula,tbfnxnhisrubros.rubtipo desc,tbfnxnhisrubros.ruborden ;\r\n\r\n	END IF;\r\n\r\n\r\n CALL Fnxsp_NCrearCurCont(RolEncForm );\r\n	\r\n\r\nEND\r\n;\r\n\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NGenerarAju`;\r\n\r\nCREATE DEFINER=``@`%` PROCEDURE `Fnxsp_NGenerarAju`(IN PAnio VARCHAR(10),IN Metodo INT,IN PRegimen VARCHAR(10))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	DECLARE texto text;\r\n\r\n	-- \'0000000148\'\r\n	 DROP VIEW IF EXISTS `v_fnxncurcontaju`;\r\n\r\n	IF Metodo=1 THEN\r\n		SET texto=CONCAT( \' CREATE  SQL SECURITY  INVOKER VIEW v_fnxncurcontaju AS SELECT tbfnxnrubrosiv.anio AS encrolcodigo,tbfnxnempleado.empcedula,COALESCE(tbfnxnempleado.empcodcon,\'\'\'\') AS empcodcon,\r\n			COALESCE(empdepar,\'\'\'\') AS empdepar,COALESCE(depcodcon,\'\'\'\') AS depcodcon,COALESCE(depcodcon2,\'\'\'\') AS depcodcon2,COALESCE(tbfnxnempleado.coscodigo,\'\'\'\') AS coscodigo, \r\n			COALESCE(tbfnxnrubros.rubcodigo,\'\'\'\') AS rubcodigo,COALESCE(tbfnxnrubros.rubextension,\'\'\'\') AS rubextension, \r\n			COALESCE(tbfnxnrubrosiv.saldo,0) AS valor,COALESCE(codcta1d,\'\'\'\') AS codctad,COALESCE(rubcodcta1d,\'\'\'\') AS rubcodctad,COALESCE(codcta1h,\'\'\'\') AS codctah,COALESCE(rubcodcta1h,\'\'\'\') AS rubcodctah\r\n			FROM tbfnxnempleado,tbfnxnrubros,tbfnxnrubrosiv,tbfnxndepartamento\r\n			WHERE tbfnxnrubrosiv.empcedula=tbfnxnempleado.empcedula \r\n			AND tbfnxnrubrosiv.rubcodigo=tbfnxnrubros.rubcodigo \r\n			AND tbfnxnrubrosiv.anio=\'\'\',PAnio ,\'\'\'\r\n			AND tbfnxnrubrosiv.regimen=\'\'\',PRegimen ,\'\'\'\r\n			AND tbfnxnempleado.empdepar=tbfnxndepartamento.depcodigo\r\n			AND saldo<>0 \r\n			ORDER BY tbfnxnempleado.empcedula,tbfnxnrubros.rubtipo desc,tbfnxnrubros.ruborden\');\r\n\r\n	END IF;\r\n\r\nIF Metodo=2 THEN