xnempleado.empnomdos,\\r\\n				tbfnxnempleado.empcodigo,\\r\\n				tbfnxnempleado.empcedula,\\r\\n				%2\\r\\n				tbfnxnempleado.empdepar,\\r\\n				tbfnxndepartamento.depnombre\\r\\n				FROM\\r\\ntbfnxndetallerol\\r\\n,tbfnxnencabezadorol \\r\\n,tbfnxnhisempleado \\r\\n, tbfnxndepartamento\\r\\n,tbfnxncurselemp\\r\\n			where  tbfnxndetallerol.encrolcodigo = tbfnxnencabezadorol.encrolcodigo\\r\\nAND tbfnxndetallerol.rubprovision=False\\r\\nand tbfnxndetallerol.encrolcodigo = tbfnxnhisempleado.encrolcodigo \\r\\nAND tbfnxnencabezadorol.encrolcodigo = tbfnxnhisempleado.encrolcodigo \\r\\nAND tbfnxndetallerol.empcedula = tbfnxnhisempleado.empcedula\\r\\nand tbfnxnhisempleado.empdepar = tbfnxndepartamento.depcodigo\\r\\nand tbfnxndetallerol.empcedula = tbfnxncurselemp.Empcedula\\r\\nand  tbfnxnhisempleado.empcedula = tbfnxncurselemp.Empcedula\\r\\nGROUP BY tbfnxndetallerol.empcedula\\r\\n			]\\r\\n			\\r\\n			sMyresulvista is string = StringBuild(sTxtvista,sMyresulinisert,sCamposv)\\r\\nInfo(sMyresulvista		)\\r\\nToClipboard(	sMyresulvista		)\\r\\n			IF HExecuteSQLQuery(\\\"dsData\\\",MySqlNomina,hQueryWithoutCorrection,\\\"call Fnxsp_NReporteParametrizable (\\\'\\\"+sMyresulvista+\\\"\\\')\\\") = False THEN///IF HExecuteSQLQuery(\\\"dsData\\\",MySqlNomina,hQueryWithoutCorrection,\\\"call Fnxsp_NReporteParametrizable (\\\'\\\"+sMyresulvista+\\\"\\\')\\\") = False THEN\\r\\n				pgUti_Mess(Error(ErrorInfo()),\\\"ADS Informa\\\",Detener)\\r\\n			ELSE//IF HExecuteSQLQuery(\\\"dsData\\\",MySqlNomina,hQueryWithoutCorrection,\\\"call Fnxsp_NReporteParametrizable (\\\'\\\"+sMyresulvista+\\\"\\\')\\\") = False THEN\\r\\n				//iPreview(i100)\\r\\n				//iPrintReport(RPT_FnxRptCentrosCostoUnico)\\r\\niPreview(ipvZoom100)\\r\\n	IF _DirectorioReportes(sReportCustom) THEN\\r\\n		iPrintReport(sReportCustom)\\r\\n	ELSE\\r\\n		iPrintReport(sReportNormal)\\r\\n	END\\r\\n		\\r\\n		END//IF HExecuteSQLQuery(\\\"dsData\\\",MySqlNomina,hQueryWithoutCorrection,\\\"call Fnxsp_NReporteParametrizable (\\\'\\\"+sMyresulvista+\\\"\\\')\\\") = False THEN\\r\\n		\\r\\nRESULT bCorreto\\r\\n\\r\\n\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT init_cod FROM `tbfnxninit_varios` WHERE `tbfnxninit_varios`.`init_cod` = \'RPTCCTROL\' );\r\n', 'Descargar los reportes y guardar en la siguiente ruta C:\\Fenix\\AdsFenix\\Modulos\\MenuWD\\Informes_Consulta', 1, NULL, 'ctigse', NULL, NULL, 'Nómina');
