(8, '2018-04-25', 'FILTRO CONTABILIDAD. CASH BANCOS. RUBROS GERENTE', '\r\nALTER TABLE `tbfnxnutilidad`\r\nMODIFY COLUMN `empctaban`  varchar(15) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL AFTER `emptipocta`,\r\nMODIFY COLUMN `empnomban`  varchar(35) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL AFTER `empctaban`;\r\n\r\n\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\nif ($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\') {	\\r\\n		if (campo(\\\'empcargo\\\')==\\\'FNXGERENTE\\\') {	\\r\\n			$resultado=round(totalgravables()*0.1760,2);\\r\\n		}else{\\r\\n			$resultado=round(totalgravables()*0.0945,2);\\r\\n		}\\r\\n}\\r\\n\' WHERE (`rubcodigo`=\'APIESS\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\nif (($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\')) {	\\r\\n	if (campo(\\\'empcargo\\\')!=\\\'FNXGERENTE\\\') {\\r\\n		$resultado=round((totalgravables()*0.1115),2);	\\r\\n	}\\r\\n}\\r\\n\' WHERE (`rubcodigo`=\'APIESSP\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\nif (($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\') && (campo(\\\'emptipoemple\\\')!=\\\'TEE\\\')&&(campo(\\\'decimo3\\\')==0)) {\\r\\n	if (campo(\\\'empcargo\\\')!=\\\'FNXGERENTE\\\') {	\\r\\n		$resultado=round((totalgravables()/12),2);	\\r\\n	}\\r\\n}\\r\\n\' WHERE (`rubcodigo`=\'D3P\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\nif (($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\') && (campo(\\\'emptipoemple\\\')!=\\\'TEE\\\')&&(campo(\\\'decimo3\\\')==0)) {	\\r\\n	if (campo(\\\'empcargo\\\')!=\\\'FNXGERENTE\\\') {\\r\\n		$resultado=(valori(\\\'sbasico\\\')/valordf(\\\'DIAS\\\'))*campo(\\\'empdiastrab\\\');	\\r\\n	}\\r\\n}\\r\\n\' WHERE (`rubcodigo`=\'D4P\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\nif (($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\') && (campo(\\\'emptipoemple\\\')!=\\\'TEE\\\')&&(campo(\\\'decimo3\\\')==1)) {	\\r\\n	if (campo(\\\'empcargo\\\')!=\\\'FNXGERENTE\\\') {\\r\\n		$resultado=round((totalgravables()/12),2);	\\r\\n	}\\r\\n}\' WHERE (`rubcodigo`=\'DECIMEN3\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\nif (($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\') && (campo(\\\'emptipoemple\\\')!=\\\'TEE\\\')&&(campo(\\\'decimo3\\\')==1)) {	\\r\\n	if (campo(\\\'empcargo\\\')!=\\\'FNXGERENTE\\\') {			\\r\\n		$resultado=(valori(\\\'sbasico\\\')/valordf(\\\'DIAS\\\'))*campo(\\\'empdiastrab\\\');	\\r\\n	}\\r\\n}\\r\\n\' WHERE (`rubcodigo`=\'DECIMOMEN4\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\nif (($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\') ) {	\\r\\n		\\r\\n	$resultado=round((totalgravables()/24),2);\\r\\n		\\r\\n}\' WHERE (`rubcodigo`=\'VA\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$sumag=0;\\r\\n$sumai=0;\\r\\n$sumae=0;\\r\\n$sumap=0;\\r\\n$sumal=0;\\r\\n$sumab=0;\\r\\n$sumat=0;\\r\\n\\r\\n//GRABABLES\\r\\n$sumag= totalgravables();\\r\\nvalort($sumag,\\\'G\\\');\\r\\n//INGRESOS\\r\\n$sumai= totalrubrosingresos();\\r\\nvalort($sumai,\\\'I\\\');\\r\\n//EGRESOS\\r\\n$sumae=totalrubrosegresos();\\r\\nvalort($sumae,\\\'E\\\');\\r\\n//PROVISIONES\\r\\n$sumap=totalprovision();\\r\\nvalort($sumap,\\\'P\\\');\\r\\n//LIQUIDO\\r\\n$sumal=round(($sumai-$sumae),2);\\r\\nvalort($sumal,\\\'L\\\');\\r\\n//TOTAL\\r\\n$sumat=round(($sumai+valorn(\\\'APIESSP\\\')),2);\\r\\nvalort($sumat,\\\'T\\\');\\r\\n// GUARDA ANTICIPO QUINCENA\\r\\nif (date(\\\'d\\\', strtotime($onomina[\\\'fecha\\\']))>10 && date(\\\'d\\\', strtotime($onomina[\\\'fecha\\\']))<25) {\\r\\n	//Almaceno en anticipo\\r\\n	$ant1=valorn(\\\'PRIQUIN\\\');\\r\\n	grabarda1(\\\'AN_SU\\\', $ant1);\\r\\n}\\r\\n//BASE IR\\r\\n$sumab=round((valorn(\\\'SF\\\')+valorn(\\\'BR\\\')+valorn(\\\'OT\\\')+valorn(\\\'D3P\\\')+valorn(\\\'D3P\\\')-valorn(\\\'APIESS\\\')),2);\\r\\n$resultado=0;\' WHERE (`rubcodigo`=\'XTI\');\r\nUPDATE `tbfnxnrubrosinicial` SET `rubformula`=\'$resultado=0;\\r\\n$d