poname0!=\\\"\\\") {\\r\\n//      $res=mysqli_query($GLOBALS[\\\'conex\\\'], $query);\\r\\n// }\\r\\n// FIN CARCULAR LOS DIAS TRABAJADOS AUTOMATICAMENTE \\r\\n\\r\\n\\r\\n// CALCULAR FECAH A 30 DIAS\\r\\nif ($onomina[\\\'encroltipo1\\\']==\\\'MENSUAL\\\' ) {\\r\\n	$resultado=campo(\\\'empsalario\\\')/30*campo(\\\'empdiastrab\\\');\\r\\n}else{\\r\\n	//ANTICIPO\\r\\n  	$resultado=valorn(\\\'GR\\\')+valorn(\\\'BONO_ALIM\\\');\\r\\n  	grabarubro(\\\'SF\\\', \\\'ANTICIPO SUELDO 50%\\\');\\r\\n}\' WHERE (`rubcodigo`=\'SF\');\r\n\r\n\r\nINSERT INTO `tbfnxndatosfijos` (`dfcodigo`, `dfnombre`, `dfvalor`, `dfestado`) \r\nSELECT \'DIASF\', \'DIAS FONDOS DE RESERVA\', \'365\', \'1\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `dfcodigo` FROM `tbfnxndatosfijos` WHERE `tbfnxndatosfijos`.`dfcodigo` = \'DIASF\' );\r\n\r\nINSERT INTO `tbfnxnarchivostxt` (`codigo`, `descripcion`, `estado`, `ejecuta`, `tipo`) \r\nSELECT \'BPRODU\', \'EJEMPLO BANCO PRODUBANCO\', \'0\', \'gsRutaArchivoTxt is string=CompleteDir(fExeDir())\\r\\nsLcFecha is string=DateToString (EDT_Fecha,\\\"AAAAMMDD\\\")\\r\\nsLcFecha2 is string=DateToString (EDT_Fecha,\\\"AAMMDD\\\")\\r\\nFilName is string = \\\"C:\\\\fenix\\\\Docs\\\\ARCHIVOS_BANCO_TXT\\\\PRODU_\\\"+sLcFecha+\\\".txt\\\"		\\r\\nFileNum is int=FnxNCrearArchivo(FilName)\\r\\ndsFnxconsulta is Data Source\\r\\n\\r\\nIF FileNum <> -1 THEN		\\r\\n	sConsulta is string=[\\r\\n		SELECT\\r\\n		\\\'D\\\' AS tipoempleado,\\r\\n		LEFT(concat(\\\'CRIADERO SAN LUIS \\\',SPACE(40)),40) AS nombres,\\r\\n		RIGHT(CONCAT(REPLACE(SPACE(11),SPACE(1),0),TRIM(COALESCE(\\\'02005196422\\\',\\\'\\\'))),11)  AS empctaban,\\r\\n		RIGHT(CONCAT(REPLACE(SPACE(14),SPACE(1),0),FLOOR(ABS(SUM(empliquido))*100)),14) AS valor,\\r\\n		CONCAT(\\\'%2\\\',\\\'N1\\\') AS n1			\\r\\n		FROM\\r\\n		tbfnxnhisempleado\\r\\n		WHERE\\r\\n		(encrolcodigo = \\\'%1\\\' AND empliquido > 0) \\r\\n		AND (emptipocta = \\\'CTAAHO\\\' OR emptipocta = \\\'CTACOR\\\'  OR emptipocta = \\\'CTAVIR\\\' )\\r\\nUNION ALL\\r\