SE WHEN emptipocta = \\\"CTACOR\\\" THEN \\\"00\\\" ELSE \\\"10\\\" END				AS TipCta, \\r\\n		RIGHT(CONCAT(REPLACE(SPACE(10),SPACE(1),0),TRIM(COALESCE(empctaban,0))),10) AS NroCta,\\r\\n		total											AS Valor, \\r\\n		empcedula											AS Identi, \\r\\n		SPACE(20) AS secuencia,\\r\\n		\\\"CU\\\"												AS FormaPago, \\r\\n		\\\"USD\\\"												AS Moneda \\r\\n   FROM tbfnxnutilidad \\r\\n    WHERE\\r\\n		(anio = \\\'%1\\\' AND empnomban = \\\'10\\\' AND total> 0) \\r\\n		AND (emptipocta = \\\'CTAAHO\\\' OR emptipocta = \\\'CTACOR\\\'  OR emptipocta = \\\'CTAVIR\\\' )\\r\\n    ORDER BY empformapago,emptipocta, nombres		\\r\\n	]\\r\\n	\\r\\n	sConsultaExec is string =StringBuild(sConsulta,FAnio)\\r\\n	IF HExecuteSQLQuery(dsFnxconsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec) THEN\\r\\n		FnxNExportarExcel(dsFnxconsulta,FilName2)\\r\\n		\\r\\n	END\\r\\n	\\r\\n	IF ErrorOccurred THEN\\r\\n		Error(StringBuild(\\\"\\\",\\\"\\\"),ErrorInfo())\\r\\n	ELSE		\\r\\n		Info(\\\"Proceso concluido. Se genero el excel en: \\\"+CR+FilName2)		\\r\\n	END\\r\\n\\r\\n\\r\\n\\r\\n\', \'B\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `codigo` FROM `tbfnxnarchivostxt` WHERE `tbfnxnarchivostxt`.`codigo` = \'UTLIBPICH\' );\r\n\r\nINSERT INTO `tbfnxninit_varios` (`init_cod`, `init_form`, `init_desc`, `init_desc2`, `init_obs`, `init_tip`, `init_val`, `init_def`, `init_add`) \r\nSELECT \'BONOE\', \'ENCBONO\', \'CALCULO BONO\', \'\', \'\', \'C\', \'0\', \'\', \'\\r\\n\\r\\n\\r\\nsDatos =[\\r\\n		INSERT INTO tbfnxndetbono (codigobo,area,empcedula,personal,cargo,estadodescuen,bono,calbono,descuento,total,tipo) \\r\\n 		SELECT \\\'%1\\\',ca.codigoare,E.empcedula,E.emptipopersonal,E.empcargo,ca.estadodes,ca.valorbono,\\r\\n 		(COALESCE(ca.valorbono,0) * ((COALESCE(bo.cumplimientoc,0)/100) - (COALESCE(bo.cuarta,0)/100))) AS CalculoBono,\\r\\n 		(COALESCE((CASE WHEN ca.estadodes=1 THEN ((COALESCE(bo.valorrotura,0)*COALESCE(bo.roturamol,0))/(SELECT COUNT(em.empcedula) AS empdesc FROM tbfnxnempleado AS em LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=em.emptipopersonal AND ca.carcodigo=em.empcargo WHERE em.empactivo=\\\'Activo\\\' AND ca.estadodes=1  AND ca.codigoare=\\\'GUANTES\\\' )) END),0)) AS Descuento,\\r\\n 		(COALESCE(ca.valorbono,0) * ((COALESCE(bo.cumplimientoc,0)/100) - (COALESCE(bo.cuarta,0)/100)) - (COALESCE((CASE WHEN ca.estadodes=1 THEN ((COALESCE(bo.valorrotura,0)*COALESCE(bo.roturamol,0))/(SELECT COUNT(em.empcedula) AS empdesc FROM tbfnxnempleado AS em LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=em.emptipopersonal AND ca.carcodigo=em.empcargo WHERE em.empactivo=\\\'Activo\\\' AND ca.estadodes=1  AND ca.codigoare=\\\'GUANTES\\\' )) END),0))) AS Total,\\r\\n 		\\\'1\\\' AS tipo\\r\\n 		FROM tbfnxnempleado E\\r\\n 		LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=E.emptipopersonal AND ca.carcodigo=E.empcargo\\r\\n 		LEFT JOIN tbfnxnencbono AS bo ON bo.codigobo<>-1 AND bo.codigobo=\\\'%1\\\'\\r\\n 		WHERE E.empactivo=\\\'Activo\\\' AND ca.codigoare=\\\'GUANTES\\\'\\r\\n	]\\r\\n	\\r\\n	sDatos2 =[\\r\\n		INSERT INTO tbfnxndetbono (codigobo,area,empcedula,personal,cargo,estadodescuen,bono,calbono,descuento,total,tipo) \\r\\n 		SELECT \\\'%1\\\',ca.codigoare,E.empcedula,E.emptipopersonal,E.empcargo,ca.estadodes,ca.valorbono,\\r\\n 		(COALESCE(ca.valorbono,0) * ((COALESCE(bo.cumplimientoc2,0)/100) - (COALESCE(bo.cuarta2,0)/100))) AS CalculoBono,\\r\\n 		(COALESCE((CASE WHEN ca.estadodes=1 THEN ((COALESCE(bo.valorrotura2,0)*COALESCE(bo.roturamol2,0))/(SELECT COUNT(em.empcedula) AS empdesc FROM tbfnxnempleado AS em LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=em.emptipopersonal AND ca.carcodigo=em.empcargo WHERE em.empactivo=\\\'Activo\\\' AND ca.estadodes=1  AND ca.codigoare=\\\'TERMINADO\\\' )) END),0)) AS Descuento,\\r\\n 		(COALESCE(ca.valorbono,0) * ((COALESCE(bo.cumplimientoc2,0)/100) - (COALESCE(bo.cuarta2,0)/100)) - (COALESCE((CASE WHEN ca.estadodes=1 THEN ((COALESCE(bo.valorrotura2,0)*COALESCE(bo.roturamol2,0))/(SELECT COUNT(em.empcedula) AS empdesc FROM tbfnxnempleado AS em LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=em.emptipopersonal AND ca.carcodigo=em.empcargo WHERE em.empactivo=\\\'Activo\\\' AND ca.estadodes=1  AND ca.codigoare=\\\'TERMINADO\\\' )) END),0))) AS Total,\\r\\n 		\\\'2\\\' AS tipo\\r\\n 		FROM tbfnxnempleado E\\r\\n 		LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=E.emptipopersonal AND ca.carcodigo=E.empcargo\\r\\n 		LEFT JOIN tbfnxnencbono AS bo ON bo.codigobo<>-1 AND bo.codigobo=\\\'%1\\\'\\r\\n 		WHERE E.empactivo=\\\'Activo\\\' AND ca.codigoare=\\\'TERMINADO\\\'\\r\\n	]\\r\\n	\\r\\n	sDatos3 =[\\r\\n		INSERT INTO tbfnxndetbono (codigobo,area,empcedula,personal,cargo,estadodescuen,bono,calbono,descuento,total,tipo) \\r\\n 		SELECT \\\'%1\\\',ca.codigoare,E.empcedula,E.emptipopersonal,E.empcargo,ca.estadodes,ca.valorbono,\\r\\n 		(COALESCE(ca.valorbono,0) * (COALESCE(bo.cumplimientoc3,0)/100)) AS CalculoBono,\\r\\n		0	AS Descuento,\\r\\n		(COALESCE(ca.valorbono,0) * (COALESCE(bo.cumplimientoc3,0)/100)) AS Total,\\r\\n 		\\\'3\\\' AS tipo\\r\\n 		FROM tbfnxnempleado E\\r\\n 		LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=E.emptipopersonal AND ca.carcodigo=E.empcargo\\r\\n 		LEFT JOIN tbfnxnencbono AS bo ON bo.codigobo<>-1 AND bo.codigobo=\\\'%1\\\'\\r\\n 		WHERE E.empactivo=\\\'Activo\\\' AND ca.codigoare=\\\'LIGAS\\\'\\r\\n	]\\r\\n\\r\\n                 sDatos4 =[\\r\\n		INSERT INTO tbfnxndetbono (codigobo,area,empcedula,personal,cargo,estadodescuen,bono,calbono,descuento,total,tipo) \\r\\n 		SELECT \\\'%1\\\',ca.codigoare,E.empcedula,E.emptipopersonal,E.empcargo,ca.estadodes,ca.valorbono,\\r\\n 		(COALESCE(ca.valorbono,0) * (COALESCE(bo.cumplimientoc4,0)/100)) AS CalculoBono,\\r\\n		0	AS Descuento,\\r\\n		(COALESCE(ca.valorbono,0) * (COALESCE(bo.cumplimientoc4,0)/100)) AS Total,\\r\\n 		\\\'4\\\' AS tipo\\r\\n 		FROM tbfnxnempleado E\\r\\n 		LEFT JOIN tbfnxncargo AS ca  ON ca.percodigo=E.emptipopersonal AND ca.carcodigo=E.empcargo\\r\\n 		LEFT JOIN tbfnxnencbono AS bo ON bo.codigobo<>-1 AND bo.codigobo=\\\'%1\\\'\\r\\n 		WHERE E.empactivo=\\\'Activo\\\' AND ca.codigoare=\\\'MASTERBRIL\\\'\\r\\n	]\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `init_cod` FROM `tbfnxninit_varios` WHERE `tbfnxninit_varios`.`init_cod` = \'BONOE\' );\r\n\r\nINSERT INTO `tbfnxninit_varios` (`init_cod`, `init_form`, `init_desc`, `init_desc2`, `init_obs`, `init_tip`, `init_val`, `init_def`, `init_add`) \r\nSELECT \'CONTACOSTR\', \'CONTACOSTR\', \'CONTABILIZAR CENTRO DE COSTOS ROL\', \'\', \'\', \'E\', \'0\', \'\', \'bCorreto is boolean=False\\r\\ntrace(\\\"sin\\\")\\r\\nnParametroValor is int=FnxVerInitVariosConte(\\\"init_val\\\",\\\"CONTA_CC\\\")\\r\\nnParametroWhere is string=\\\"codcta LIKE \\\'7.%\\\'\\\"\\r\\ndsVerificar is Data Source\\r\\nsVerificaValoresExec is string\\r\\nsInsertarArenconExec is string\\r\\nHDeleteAll(tbfnxnarenconwdtiempo)\\r\\nsVerificaValores is string=[\\r\\n	SELECT tbfnxntiempocc.empcedula, SUM(valor) AS valort FROM tbfnxntiempocc,tbfnxnhisempleado\\r\\n	WHERE tbfnxnhisempleado.empcedula=tbfnxntiempocc.empcedula AND  tbfnxnhisempleado.encrolcodigo=\\\'%2\\\'\\r\\n	GROUP BY empcedula\\r\\n	HAVING valort<> %1\\r\\n]\\r\\nsVerificaValoresExec=StringBuild(sVerificaValores,nParametroValor,FnxNRolCont)\\r\\n\\r\\nsInsertarArencon is string=[\\r\\n	INSERT INTO tbfnxnarenconwdtiempo (codcta,refer,concepto,importe,origen,cod_orden,tipopce,procliemp,estado)\\r\\n	SELECT codcta,refer,concepto,importe,origen,cod_orden,tipopce,procliemp,0 AS estado FROM tbfnxnarenconwd\\r\\n	WHERE  %1\\r\\n]\\r\\n\\r\\nsInsertarArenconExec=StringBuild(sInsertarArencon,nParametroWhere)\\r\\n\\r\\nIF HExecuteSQLQuery(dsVerificar,MySqlNomina,hQueryWithoutCorrection,sVerificaValoresExec) THEN\\r\\n	IF HNbRec(dsVerificar)>0 THEN\\r\\n		info(\\\"EXISTEN EMPLEADOS SIN ASIGNAR % DE PARTICIPACION EN LOS PROYECTOS O NO SUMAN \\\"+nParametroValor)\\r\\n	ELSE\\r\\n		HFreeQuery(dsVerificar)\\r\\n		IF HExecuteSQLQuery(dsVerificar,MySqlNomina,hQueryWithoutCorrection,sInsertarArenconExec) THEN\\r\\n			\\r\\n			sSelectTiempoAren is string=[\\r\\n				SELECT codcta,refer,concepto,importe,origen,cod_orden,tipopce,procliemp\\r\\n				FROM tbfnxnarenconwdtiempo WHERE estado=0\\r\\n			]\\r\\n			\\r\\n			IF HExecuteSQLQuery(EjecucionTMP,MySqlNomina,hQueryWithoutCorrection,sSelectTiempoAren) THEN\\r\\n				HReadFirst(EjecucionTMP)				\\r\\n				WHILE HOut(EjecucionTMP) = False	\\r\\n					//Trace(EjecucionTMP.\\\"codcta\\\"+CR +\\\"SELECT coscodigo,empcedula,valor FROM tbfnxntiempocc WHERE empcedula=\\\'\\\"+EjecucionTMP.\\\"procliemp\\\"+\\\"\\\'\\\"	)					\\r\\n					IF HExecuteSQLQuery(gdsEjecucion,MySqlNomina,hQueryWithoutCorrection,\\\"SELECT coscodigo,empcedula,valor FROM tbfnxntiempocc WHERE valor<>0 AND empcedula=\\\'\\\"+EjecucionTMP.\\\"procliemp\\\"+\\\"\\\'\\\") THEN\\r\\n						HReadFirst(gdsEjecucion)				\\r\\n						WHILE HOut(gdsEjecucion) = False\\r\\n							sInsertarArenconTiempo is string=[\\r\\n								INSERT INTO tbfnxnarenconwdtiempo( codcta,refer,concepto,importe,origen,cod_orden,tipopce,procliemp,estado)\\r\\n								VALUES (\\\'%1\\\',\\\'%2\\\',\\\'%3\\\',\\\'%4\\\',\\\'%5\\\',\\\'%6\\\',\\\'%7\\\',\\\'%8\\\',%9)\\r\\n							]\\r\\n							xValor is numeric=(EjecucionTMP.\\\"importe\\\"*gdsEjecucion.\\\"valor\\\")/nParametroValor\\r\\n							\\r\\n							sInsertarArenconTiempoExec is string=StringBuild(sInsertarArenconTiempo,EjecucionTMP.\\\"codcta\\\",EjecucionTMP.\\\"refer\\\",EjecucionTMP.\\\"concepto\\\",xValor,EjecucionTMP.\\\"origen\\\",gdsEjecucion.\\\"coscodigo\\\",EjecucionTMP.\\\"tipopce\\\",EjecucionTMP.\\\"procliemp\\\",1)\\r\\n							HExecuteSQLQuery(\\\"Fnx\\\",MySqlNomina,hQueryWithoutCorrection,sInsertarArenconTiempoExec)\\r\\n							\\r\\n							HReadNext(gdsEjecucion)		\\r\\n						END\\r\\n					END\\r\\n					HReadNext(EjecucionTMP)						\\r\\n				END\\r\\n				trace(\\\"DELETE FROM tbfnxnarenconwd  WHERE \\\"+nParametroWhere)\\r\\n				HExecuteSQLQuery(\\\"Fnx\\\",MySqlNomina,hQueryWithoutCorrection,\\\"DELETE FROM tbfnxnarenconwd  WHERE \\\"+nParametroWhere)\\r\\n				sInsertarArencon2 is string=[\\r\\n					INSERT INTO tbfnxnarenconwd(codcta,refer,concepto,importe,origen,cod_orden,tipopce,procliemp)\\r\\n					SELECT codcta,refer,concepto,importe,origen,cod_orden,tipopce,procliemp AS estado FROM tbfnxnarenconwdtiempo\\r\\n					WHERE  estado=1\\r\\n				]\\r\\n				HExecuteSQLQuery(\\\"Fnx\\\",MySqlNomina,hQueryWithoutCorrection,sInsertarArencon2)\\r\\n				bCorreto=True\\r\\n			END\\r\\n			\\r\\n		END\\r\\n	END\\r\\nEND\\r\\n\\r\\nRESULT bCorreto\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `init_cod` FROM `tbfnxninit_varios` WHERE `tbfnxninit_varios`.`init_cod` = \'CONTACOSTR\' );\r\n\r\nINSERT INTO `tbfnxninit_varios` (`init_cod`, `init_form`, `init_desc`, `init_desc2`, `init_obs`, `init_tip`, `init_val`, `init_def`, `init_add`) \r\nSELECT \'CONTA_CC\', \'CONTA_CC\', \'VALOR PARA VALIDAR EL CENTRO COSTOS\', \'\', \'\', \'C\', \'100\', \'\', \'\');\r\nFROM DUAL WHERE NOT EXISTS (SELECT `init_cod` FROM `tbfnxninit_varios` WHERE `tbfnxninit_varios`.`init_cod` = \'CONTA_CC\');\r\n\r\nINSERT INTO `tbfnxninit_varios` (`init_cod`, `init_form`, `init_desc`, `init_desc2`, `init_obs`, `init_tip`, `init_val`, `init_def`, `init_add`) \r\nSELECT \'ROLCARGAR\', \'ROL_CARGAR\', \'COMANDO EJECUTA ANTES DE CARGAR ROL \', \'\', \'\', \'\', \'0\', \'\', \'\\r\\nbCorreto is boolean=True\\r\\nsLcFecha is string=DateToString (EDT_Fecha,\\\"AAAAMM\\\")\\r\\ndsFnxConsulta is Data Source\\r\\nsConsulta is string=[\\r\\n	UPDATE  tbfnxnempleado SET empdiastrab=30 WHERE tbfnxnempleado.empactivo=\\\'Activo\\\';\\r\\n	\\r\\n	UPDATE  tbfnxnempleado AS ee,\\r\\n	( SELECT e.empcedula,\\r\\nIF (MONTH(empfecentrada)=2,\\r\\nIF(DATE_FORMAT(e.empfecentrada,\\\'%Y%m\\\')=DATE_FORMAT(e.empfecsalant,\\\'%Y%m\\\'),\\r\\nIF(((DAY(e.empfecsalant)- DAY(e.empfecentrada))+1)>29,30,(DAY(e.empfecsalant)- DAY(e.empfecentrada))+1 ),\\r\\nIF(DAY(e.empfecsalant)>29,30,DAY(e.empfecsalant))) ,\\r\\n\\r\\nIF(DATE_FORMAT(e.empfecentrada,\\\'%Y%m\\\')=DATE_FORMAT(e.empfecsalant,\\\'%Y%m\\\'),\\r\\nIF(((DAY(e.empfecsalant)- DAY(e.empfecentrada))+1)>27,30,(DAY(e.empfecsalant)- DAY(e.empfecentrada))+1 ),\\r\\nIF(DAY(e.empfecsalant)>27,30,DAY(e.empfecsalant))))  AS diatrab\\r\\n\\r\\n\\r\\nFROM  tbfnxnempleado e\\r\\nWHERE DATE_FORMAT(e.empfecsalant,\\\'%Y%m%d\\\') <> DATE_FORMAT(e.empfecentrada,\\\'%Y%m%d\\\') \\r\\nAND DATE_FORMAT(e.empfecsalant,\\\'%Y%m\\\') =\\\'%1\\\';\\r\\n\\r\\nUNION ALL\\r\\n\\r\\nSELECT e1.empcedula,\\r\\nIF (MONTH(empfecentrada)=2,\\r\\nIF((30-DAY(e1.empfecentrada))>=29,30,(30-DAY(e1.empfecentrada))+1 ),\\r\\nIF((30-DAY(e1.empfecentrada))>=30,30,(30-DAY(e1.empfecentrada))+1 )\\r\\n) as diatrab,\\r\\nMONTH(empfecentrada)\\r\\nFROM  tbfnxnempleado e1\\r\\nWHERE \\r\\nDATE_FORMAT(e1.empfecentrada,\\\'%Y%m\\\') =\\\'%1\\\'  AND DATE_FORMAT(e1.empfecsalant,\\\'%Y%m\\\') =\\\'%1\\\'\\r\\n\\r\\n\\r\\n) AS et\\r\\n	\\r\\n	SET ee.empdiastrab=et.diatrab\\r\\n	WHERE ee.empactivo=\\\'Activo\\\' AND ee.empcedula=et.empcedula;\\r\\n]\\r\\n\\r\\nsConsultaExec is string=StringBuild(sConsulta,sLcFecha)\\r\\n//info(sConsultaExec)\\r\\nIF HExecuteSQLQuery(dsFnxConsulta,MySqlNomina,hQueryWithoutCorrection,sConsultaExec)=False THEN\\r\\n	pgUti_Mess(Error(ErrorInfo()),\\\"ADS Informa\\\",Detener)\\r\\n	bCorreto=False\\r\\nEND\\r\\n\\r\\nRESULT bCorreto\\r\\n\\r\\n\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT `init_cod` FROM `tbfnxninit_varios` WHERE `tbfnxninit_varios`.`init_cod` = \'ROLCARGAR\' );\r\n\r\n\r\n-- ----------------------------\r\n-- Procedure structure for Fnxsp_NGenerar\r\n-- ----------------------------\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NGenerar`;\r\nDELIMITER ;;\r\nCREATE DEFINER=``@`%` PROCEDURE `Fnxsp_NGenerar`(IN RolEncForm VARCHAR(10), IN Metodo INT, IN filtro TEXT)\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n\r\n	DROP VIEW IF EXISTS `v_fnxncurcont`;\r\n\r\n	SET @@group_concat_max_len = 15000;\r\n	SET @sql = NULL;\r\n\r\n\r\n	IF Metodo=1 THEN\r\n\r\n		SET @sql = CONCAT(\"CREATE SQL SECURITY  INVOKER VIEW v_fnxncurcont AS \",\r\n			\" SELECT tbfnxnhisempleado.encrolcodigo,tbfnxnhisempleado.empcedula,COALESCE(tbfnxnhisempleado.empcodcon,\'\') AS empcodcon,\",\r\n			\" COALESCE(empdepar,\'\') AS empdepar,COALESCE(depcodcon,\'\') AS depcodcon,COALESCE(depcodcon2,\'\') AS depcodcon2,COALESCE(tbfnxnhisempleado.coscodigo,\'\') AS coscodigo, \",\r\n			\" COALESCE(tbfnxnhisrubros.rubcodigo,\'\') AS rubcodigo,COALESCE(tbfnxnhisrubros.rubextension,\'\') AS rubextension,\" ,\r\n			\" COALESCE(tbfnxndetallerol.valor,0) AS valor,COALESCE(codcta1d,\'\') AS codctad,COALESCE(rubcodcta1d,\'\') AS rubcodctad,COALESCE(codcta1h,\'\') AS codctah,COALESCE(rubcodcta1h,\'\') AS rubcodctah \",\r\n			\" FROM tbfnxnhisempleado,tbfnxnhisrubros,tbfnxndetallerol,tbfnxndepartamento \",\r\n			\" WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula \",\r\n			\" AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo \",\r\n			\" AND tbfnxndetallerol.encrolcodigo=FnxNRol() \",\r\n			\" AND tbfnxnhisempleado.encrolcodigo=FnxNRol() \",\r\n			\" AND tbfnxnhisrubros.encrolcodigo=FnxNRol() \",\r\n			\" AND tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo \",\r\n			\" AND valor<>0 AND \", \r\n			filtro,\r\n			\" ORDER BY tbfnxnhisempleado.empcedula,tbfnxnhisrubros.rubtipo desc,tbfnxnhisrubros.ruborden ;\");\r\n	END IF;\r\n\r\nIF Metodo=2 THEN\r\n			SET @sql = CONCAT(\"CREATE SQL SECURITY  INVOKER VIEW v_fnxncurcont AS \",\r\n			\" SELECT tbfnxnhisempleado.encrolcodigo,tbfnxnhisempleado.empcedula,COALESCE(tbfnxnhisempleado.empcodcon,\'\') AS empcodcon,\" ,\r\n			\" COALESCE(empdepar,\'\') AS empdepar,COALESCE(depcodcon,\'\') AS depcodcon,COALESCE(depcodcon2,\'\') AS depcodcon2,COALESCE(tbfnxnhisempleado.coscodigo,\'\') AS coscodigo, \",\r\n			\" COALESCE(tbfnxnhisrubros.rubcodigo,\'\') AS rubcodigo,COALESCE(tbfnxnhisrubros.rubextension,\'\') AS rubextension, \",\r\n			\" COALESCE(tbfnxndetallerol.valor,0) AS valor,COALESCE(codcta2d,\'\') AS codctad,COALESCE(rubcodcta2d,\'\') AS rubcodctad,COALESCE(codcta2h,\'\') AS codctah,COALESCE(rubcodcta2h,\'\') AS rubcodctah \",\r\n			\" FROM tbfnxnhisempleado,tbfnxnhisrubros,tbfnxndetallerol,tbfnxndepartamento \",\r\n			\" WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula \",\r\n			\" AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo \",\r\n			\" AND tbfnxndetallerol.encrolcodigo=FnxNRol() \",\r\n			\" AND tbfnxnhisempleado.encrolcodigo=FnxNRol() \",\r\n			\" AND tbfnxnhisrubros.encrolcodigo=FnxNRol() \",\r\n			\" AND tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo \",\r\n			\" AND valor<>0 AND \",\r\n			filtro,\r\n			\" ORDER BY tbfnxnhisempleado.empcedula,tbfnxnhisrubros.rubtipo desc,tbfnxnhisrubros.ruborden ; \");\r\n\r\n	END IF;\r\n\r\n\r\n	PREPARE stmt1 FROM @sql;\r\n	EXECUTE stmt1;\r\n	DEALLOCATE PREPARE stmt1;\r\n\r\n CALL Fnxsp_NCrearCurCont(RolEncForm );\r\n	\r\n\r\nEND\r\n;;\r\nDELIMITER ;\r\n\r\n', '', 1, NULL, 'stravez', NULL, NULL, 'Nómina');
