(10, '2018-08-15', 'ESTRUCTURA 107', '/*Liberacion SRI 107 07-11-2017*/\r\nCREATE INDEX `ind_Fecha` ON `tbfnxndetallerol`(`fecha`) USING BTREE ;\r\nCREATE INDEX `ind_RubCod` ON `tbfnxndetallerol`(`rubcodigo`) USING BTREE ;\r\nCREATE INDEX `idx_codigo` ON `tbfnxndetallerol`(`ndetalle_id`) USING BTREE ;\r\n\r\n\r\nCREATE INDEX `idx_codigo` ON `tbfnxnempleado`(`empcodigo`) USING BTREE ;\r\n\r\nCREATE INDEX `idx_encrolcodigo` ON `tbfnxnencabezadorol`(`encrolcodigo`) USING BTREE ;\r\n\r\nCREATE INDEX `idx_cod` ON `tbfnxnglobal`(`Codigo`) USING BTREE ;\r\nCREATE INDEX `idx_periodo` ON `tbfnxnglobal`(`periodo`) USING BTREE ;\r\n\r\nCREATE INDEX `idx_encrolcodigo` ON `tbfnxnhisrubros`(`encrolcodigo`) USING BTREE ;\r\nCREATE INDEX `idx_rubcodigo` ON `tbfnxnhisrubros`(`rubcodigo`) USING BTREE ;\r\n\r\nALTER TABLE `tbfnxnhomologarubro` ADD COLUMN `periodo`  varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL AFTER `tipo`;\r\nCREATE INDEX `ind_periodo` ON `tbfnxnhomologarubro`(`periodo`) USING BTREE ;\r\nCREATE INDEX `ind_Cod` ON `tbfnxnhomologarubro`(`codigo`) USING BTREE ;\r\nCREATE INDEX `ind_CodHom` ON `tbfnxnhomologarubro`(`homologado`) USING BTREE ;\r\n\r\nALTER TABLE `tbfnxnsri107` ADD COLUMN `tipoDato`  int(11) NULL DEFAULT NULL AFTER `sriA`;\r\nALTER TABLE `tbfnxnsri107` MODIFY COLUMN `nsricodigo2`  varchar(45) CHARACTER SET utf8 COLLATE utf8_general_ci NULL DEFAULT NULL AFTER `operador`;\r\n\r\nCREATE OR REPLACE\r\nSQL SECURITY INVOKER \r\nVIEW `vfnxperiododecimo`AS \r\nSELECT\r\n	LEFT(codigo,2) AS codigo,\r\n	homologado,\r\n	CONCAT(\r\n		(\r\n			SELECT\r\n				`tbfnxnglobal`.`periodo`\r\n			FROM\r\n				`tbfnxnglobal`\r\n			WHERE\r\n				(`tbfnxnglobal`.`Codigo` = 1)\r\n		) - 1,\r\n		\'-\',\r\n		SUBSTR(periodo FROM 1 FOR 2),\r\n		\'-\',\r\n		SUBSTR(periodo FROM 3 FOR 2)\r\n	) AS desdeSierra,\r\n	CONCAT(\r\n		(\r\n			SELECT\r\n				`tbfnxnglobal`.`periodo`\r\n			FROM\r\n				`tbfnxnglobal`\r\n			WHERE\r\n				(`tbfnxnglobal`.`Codigo` = 1)\r\n		),\r\n		\'-\',\r\n		SUBSTR(periodo FROM 5 FOR 2),\r\n		\'-\',\r\n		SUBSTR(periodo FROM 7 FOR 2)\r\n	) AS hastaSierra,\r\n		IF(INSTR(periodo,\';\')> 0,CONCAT(\r\n		(\r\n			SELECT\r\n				`tbfnxnglobal`.`periodo`\r\n			FROM\r\n				`tbfnxnglobal`\r\n			WHERE\r\n				(`tbfnxnglobal`.`Codigo` = 1)\r\n		),\r\n		\'-\',\r\n		SUBSTR(periodo FROM 10 FOR 2),\r\n		\'-\',\r\n		SUBSTR(periodo FROM 12 FOR 2)\r\n	),NULL) AS desdeCosta,\r\n		IF(INSTR(periodo,\';\')> 0,CONCAT(\r\n		(\r\n			SELECT\r\n				`tbfnxnglobal`.`periodo`\r\n			FROM\r\n				`tbfnxnglobal`\r\n			WHERE\r\n				(`tbfnxnglobal`.`Codigo` = 1)\r\n		),\r\n		\'-\',\r\n		SUBSTR(periodo FROM 14 FOR 2),\r\n		\'-\',\r\n		SUBSTR(periodo FROM 16 FOR 2)\r\n	),NULL) AS hastaCosta\r\nFROM\r\n	tbfnxnhomologarubro HR\r\nWHERE\r\n	codigo IN(\'D4PP\',\'D3PP\',\'D4PM\',\'D3PM\') ;\r\n\r\n\r\nDROP PROCEDURE IF EXISTS `pivotCodigosEmpleado`;\r\n\r\nCREATE PROCEDURE `pivotCodigosEmpleado`(anio INT(6))\r\n    SQL SECURITY INVOKER\r\nBEGIN\r\n	\r\n	SET @@group_concat_max_len = 15000;\r\n	SET @sql = NULL;\r\n	SELECT\r\n		GROUP_CONCAT(DISTINCT\r\n			CONCAT(\r\n				\'MAX(IF(SRI.id = \'\'\',\r\n				SRI.id,\r\n				\'\'\', ROUND(XEM.sri107valor,2), 0)) AS \\\'_\',\r\n				SRI.nsricodigo,IF(SRI.sriA,\'A\',\'\'),\'\\\'\'\r\n			)\r\n		) INTO @sql\r\n	FROM tbfnxnsri107xempleado XEM INNER JOIN tbfnxnsri107 SRI ON XEM.sriCode = SRI.id\r\n	WHERE XEM.sri107periodo = anio;\r\n\r\n	SET @sql = CONCAT(\'SELECT XEM.sri107periodo, XEM.sri107empcedula, \', \r\n			@sql, \r\n			\' FROM tbfnxnsri107xempleado XEM INNER JOIN tbfnxnsri107 SRI ON XEM.sriCode = SRI.id WHERE XEM.sri107periodo = \\\'\',\r\n			anio,\r\n			\'\\\' GROUP BY XEM.sri107empcedula\');\r\n	\r\n	PREPARE stmt1 FROM @sql;\r\n	EXECUTE stmt1;\r\n	DEALLOCATE PREPARE stmt1;\r\n	 \r\nEND;', '', 1, NULL, 'wacurio', NULL, NULL, 'Nómina'),
