4P\', null, \'08010731;03010229\');\r\n\r\n\r\nDELETE FROM `tbfnxninit_varios` WHERE init_cod = \'QRY107\';\r\nINSERT INTO `tbfnxninit_varios` VALUES (\'QRY107\', \'SRI_107\', \'CONSULTA SRI 107\', null, null, null, \'1\', null, \'SELECT\\r\\n	EMP.sri107empcedula as idRet,\\r\\n	SRI.id AS idxml,\\r\\n	SRI.nsricodigo,\\r\\n	SRI.nsricodhml,\\r\\n	SRI.nsricodigo2,\\r\\n	SRI.operador,\\r\\n	SRI.tipoDato,\\r\\n	SEM.sri107id AS srixemid,\\r\\n	SRI.periodo AS periodo,\\r\\n	SEM.nsri107nominas AS nominas,\\r\\n	ROUND(SEM.sri107valor,2) AS valor,\\r\\n	IF (COALESCE(EMPL.decimo3,0)=0,\\\'NO\\\',\\\'SI\\\') AS benGalpg,\\r\\n	\\\'C\\\' AS tipIdRet,\\r\\n	CONCAT(TRIM(EMPL.empapeuno),\\\' \\\',TRIM(EMPL.empapedos)) as apellidoTrab,\\r\\n	CONCAT(TRIM(EMPL.empnomuno),\\\' \\\',TRIM(EMPL.empnomdos)) as nombreTrab,\\r\\n	IF(TRIM(EMPL.sucursal) <> \\\'\\\', EMPL.sucursal, \\\'001\\\') AS estab,\\r\\n	IF(TRIM(EMPL.dato4) <> \\\'\\\' AND EMPL.dato4 <> \\\'00\\\', EMPL.dato4, \\\'01\\\') AS residenciaTrab,\\r\\n	IF(EMPL.dato4 = \\\'01\\\', \\\'593\\\', IFNULL(EMPL.emppais,\\\'593\\\')) AS paisResidencia,\\r\\n	IF(EMPL.dato4 <> \\\'01\\\',IF(EMPL.dato4 = \\\'02\\\',EMPL.dato1,\\\'NA\\\'),\\\'NA\\\') AS aplicaConvenio,\\r\\n	IF(TRIM(EMPL.dato5) <> \\\'\\\' AND EMPL.dato5 <> \\\'00\\\', EMPL.dato5, \\\'01\\\') AS tipoTrabajDiscap,\\r\\n	IF(EMPL.fnxempdiscapacidad <> 0 ,EMPL.fnxempdiscapacidadgrado ,0 ) AS porcentajeDiscap,\\r\\n	IF(TRIM(EMPL.dato2) <> \\\'\\\' AND  EMPL.dato5 <> \\\'00\\\' AND EMPL.dato5 <> \\\'01\\\' AND  EMPL.dato5 <> \\\'02\\\', EMPL.dato2, \\\'N\\\') AS tipIdDiscap,\\r\\n	IF(SRI.periodo >= 2013 AND IF(TRIM(EMPL.dato5) <> \\\'\\\' AND EMPL.dato5 <> 00, EMPL.dato5, \\\'01\\\') <> \\\'01\\\'  AND IF(TRIM(EMPL.dato5) <> \\\'\\\' AND EMPL.dato5 <> \\\'00\\\', EMPL.dato5, \\\'01\\\') <>\\\'02\\\', IF((IF(TRIM(EMPL.dato5) <> \\\'\\\' AND EMPL.dato5 <> \\\'00\\\', EMPL.dato5, \\\'01\\\') = \\\'03\\\'  OR IF(TRIM(EMPL.dato5) <> \\\'\\\' AND EMPL.dato5 <> \\\'00\\\', EMPL.dato5, \\\'01\\\') = \\\'04\\\') AND EMPL.empcedula <> EMPL.dato3,IF(TRIM(EMPL.dato5) <> \\\'\\\' AND EMPL.dato5 <> \\\'00\\\', EMPL.dato5, \\\'01\\\'), 0) , \\\'999\\\') AS idDiscap,\\r\\n	COALESCE(EMPL.valor1,0) AS salarioDigno,\\r\\n	COALESCE(EMPL.valor2,1) AS sisSalNet\\r\\n	FROM\\r\\n	(\\r\\n	SELECT\\r\\n	XE.sri107empcedula,\\r\\n	XE.sri107periodo\\r\\n	FROM\\r\\n	tbfnxnsri107xempleado XE\\r\\n	WHERE \\r\\n	XE.sri107periodo = \\\'%1\\\' \\r\\n	GROUP BY\\r\\n	XE.sri107empcedula\\r\\n	\\r\\n	) EMP\\r\\n	CROSS JOIN tbfnxnsri107 SRI\\r\\n	LEFT JOIN tbfnxnsri107xempleado SEM ON EMP.sri107empcedula = SEM.sri107empcedula\\r\\n	AND SRI.id = SEM.sriCode\\r\\n	AND SRI.periodo = SEM.sri107periodo\\r\\n	AND SRI.periodo = EMP.sri107periodo\\r\\n	INNER JOIN tbfnxnempleado EMPL ON EMP.sri107empcedula = EMPL.empcedula\\r\\n	WHERE\\r\\n	SRI.periodo = \\\'%1\\\'\\r\\n	AND SRI.sriA = %2\\r\\n	ORDER BY\\r\\n	EMP.sri107empcedula,\\r\\n	SRI.operador ASC\');\r\n\r\nDELETE FROM `tbfnxnsri107formulas` WHERE sri107periodo = 2017;\r\nINSERT INTO `tbfnxnsri107formulas` VALUES (\'2017\', \'399=SRI._301+SRI._303+SRI._305+SRI._307-SRI._351-SRI._353-SRI._361-SRI._363-SRI._365-SRI._367-SRI._369-SRI._371-SRI._373+SRI._381\\r\\n349=SRI._301+SRI._303+SRI._305+SRI._381\', \'0\', \'\');\r\n', '', 1, NULL, 'wacurio', NULL, NULL, 'Nómina'),
