CT  tbfnxnhisempleado.empfecing,empfecsal,empdepar,empsalario,empdiastrab,emptiporemun as tipo_remuneracion,\\r\\n	encroldescrip,tbfnxndetallerol.encrolcodigo as rol,%2 as orden,\\r\\n	empapeuno,empapedos,empnomuno,empnomdos,tbfnxnhisempleado.empcedula,rubdescrip,empliquido,\\r\\n	emptotaling,emptotalegr,valor,tbfnxnhisrubros.rubtipo as tipo,tbfnxnhisrubros.rubcodigo,tbfnxnencabezadorol.fecha,tbfnxnhisempleado.empcodigo,\\r\\n	tbfnxndetallerol.encrolcodigo,%3 as grupo,tbfnxnhisempleado.nombres,tbfnxnhisrubros.rubtipo,tbfnxnhisrubros.ruborden,tbfnxnhisempleado.empmail\\r\\n	FROM  tbfnxndetallerol,tbfnxnhisrubros,tbfnxnhisempleado,tbfnxndepartamento,tbfnxnencabezadorol,tbfnxncurselemp, %5\\r\\n	WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\\r\\n	and tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\\r\\n	AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo\\r\\n	AND tbfnxnhisrubros.rubprovision=FALSE\\r\\n	AND tbfnxndetallerol.encrolcodigo= \\\'%1\\\'\\r\\n	AND tbfnxnhisrubros.encrolcodigo= \\\'%1\\\'\\r\\n	AND tbfnxnhisempleado.encrolcodigo= \\\'%1\\\'	\\r\\n	and tbfnxnencabezadorol.encrolcodigo= \\\'%1\\\'\\r\\n	AND valor <> 0\\r\\n	AND tbfnxndetallerol.rubcodigo <> \\\'SALARIO\\\'\\r\\n	%6\\r\\n	and tbfnxnhisempleado.empcedula = tbfnxncurselemp.empcedula\\r\\n	%4\\r\\n	ORDER BY tbfnxndetallerol.encrolcodigo,%3,%2 ,rubtipo desc,ruborden;\\r\\n]\\r\\nmyresul is string = StringBuild(sTxt,sCODIGOROL,sLcOrden,sLcGrupoCam,sLcGrupoRel,gsTablas,sValorextra)\\r\\ndsData is Data Source\\r\\n\\r\\ngsAux=myresul \\r\\nIF HExecuteSQLQuery(dsData,MySqlNomina,hQueryWithoutCorrection,myresul) THEN\\r\\n/// ABRIR REPORTE\\r\\n	sRuta is string = fExeDir() +\\\"\\\\Informes_Consulta\\\"\\r\\n	sReportCustom is string=\\\"RPT_FnxRptRol_liq_ser_pres\\\"//+sRuta\\r\\n	sReportNormal is string=\\\"RPT_FnxRptRol_liq_ser_pres\\\"\\r\\n	iPreview(ipvZoom100)\\r\\n	IF _DirectorioReportes(\\\"RPT_FnxRptRol_liq_ser_pres\\\") THEN\\r\\n		iPrintReport(sReportCustom)\\r\\n	ELSE\\r\\n		iPrintReport(sReportNormal,myresul)\\r\\n	END\\r\\nELSE\\r\\n	pgUti_Mess(Error(ErrorInfo()),\\\"ADS Informa\\\",Detener)\\r\\nEND\\r\\nRESULT bCorreto\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT * FROM tbfnxninit_varios WHERE tbfnxninit_varios.init_cod = \'RPTLSP\' );\r\n\r\nINSERT INTO tbfnxninit_varios (init_cod, init_form, init_desc, init_desc2, init_obs, init_tip, init_val, init_def, init_add) \r\nSELECT \'RPTNOV\', \'RPTEMPLE\', \'REPORTE DE NOVEDADES\', NULL, NULL, NULL, \'1\', NULL, \'bCorreto is boolean=False\\r\\nsRuta is string = fExeDir() +\\\"\\\\Informes_Consulta\\\"\\r\\nsReportCustom is string=\\\"\\\"\\r\\nsReportNormal is string=\\\"RPT_FnxRptNovedades\\\"\\r\\niPreview(ipvZoom100)\\r\\nIF _DirectorioReportes(sReportCustom) THEN\\r\\niPrintReport(sReportCustom,TPL_ROL.STC_Text,TPL_ROL.EDT_Busqueda)\\r\\nELSE\\r\\niPrintReport(sReportNormal,TPL_ROL.STC_Text,TPL_ROL.EDT_Busqueda)\\r\\nEND\\r\\nRESULT bCorreto\'\r\nFROM DUAL WHERE NOT EXISTS (SELECT * FROM tbfnxninit_varios WHERE tbfnxninit_varios.init_cod = \'RPTNOV\' );', '', 1, NULL, 'ctigse', NULL, NULL, 'Nómina'),
(17, '2018-10-23', 'VIESTAS DE REPORTES PARA NOMINA', 'CREATE OR REPLACE \r\nSQL SECURITY INVOKER \r\nVIEW vfnxrptrubrosprovisiones AS \r\nSELECT\r\n	tbfnxndetallerol.encrolcodigo AS encrolcodigo,\r\n	tbfnxndetallerol.rubcodigo AS rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip AS rubdescrip,\r\n	tbfnxnhisrubros.rubtipo AS rubtipo,\r\n	tbfnxnhisrubros.rubnomcor AS rubnomcor,\r\n	tbfnxnhisrubros.rubprovision AS rubprovision,\r\n	sum(tbfnxndetallerol.valor) AS valor,\r\n	tbfnxnhisempleado.coscodigo AS orden\r\nFROM\r\n	(\r\n		(\r\n			(\r\n				(\r\n					(\r\n						(\r\n							tbfnxndetallerol\r\n							JOIN tbfnxnhisrubros ON (\r\n								(\r\n									(\r\n										tbfnxndetallerol.encrolcodigo = tbfnxnhisrubros.encrolcodigo\r\n									)\r\n									AND (\r\n										tbfnxndetallerol.rubcodigo = tbfnxnhisrubros.rubcodigo\r\n									)\r\n								)\r\n							)\r\n						)\r\n						JOIN tbfnxnhisempleado ON (\r\n							(\r\n								(\r\n									tbfnxnhisempleado.encrolcodigo = tbfnxndetallerol.encrolcodigo\r\n								)\r\n								AND (\r\n									tbfnxnhisempleado.empcedula = tbfnxndetallerol.empcedula\r\n								)\r\n							)\r\n						)\r\n					)\r\n					JOIN tbfnxndepartamento ON (\r\n						(\r\n							tbfnxnhisempleado.empdepar = tbfnxndepartamento.depcodigo\r\n						)\r\n					)\r\n				)\r\n				JOIN tbfnxnpersonal ON (\r\n					(\r\n						tbfnxnhisempleado.emptipopersonal = tbfnxnpersonal.percodigo\r\n					)\r\n				)\r\n			)\r\n			JOIN tbfnxntipoempleado ON (\r\n				(\r\n					tbfnxnhisempleado.emptipoemple = tbfnxntipoempleado.tecodigo\r\n				)\r\n			)\r\n		)\r\n		JOIN tbfnxntiporemuner ON (\r\n			(\r\n				tbfnxnhisempleado.emptiporemun = tbfnxntiporemuner.trcodigo\r\n			)\r\n		)\r\n	)\r\nWHERE\r\n	(\r\n		(\r\n			tbfnxnhisrubros.rubtipo = \'E\'\r\n		)\r\n		AND (\r\n			tbfnxndetallerol.valor <> 0\r\n		)\r\n		AND (\r\n			tbfnxnhisrubros.rubprovision = 1\r\n		)\r\n		AND (\r\n			tbfnxndetallerol.encrolcodigo = \'0000000205\'\r\n		)\r\n		AND (\r\n			tbfnxnhisempleado.encrolcodigo = \'0000000205\'\r\n		)\r\n		AND (\r\n			tbfnxnhisrubros.encrolcodigo = \'0000000205\'\r\n		)\r\n	)\r\nGROUP BY\r\n	tbfnxndetallerol.encrolcodigo,\r\n	tbfnxndetallerol.rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.rubnomcor,\r\n	tbfnxnhisrubros.rubprovision,\r\n	tbfnxnhisempleado.coscodigo\r\nORDER BY\r\n	tbfnxnhisempleado.coscodigo ;\r\n	\r\n-- ----\r\n\r\n-- ----\r\nCREATE OR REPLACE\r\nSQL SECURITY INVOKER \r\nVIEW vfnxrptrubrosegresos AS \r\nSELECT\r\n	tbfnxndetallerol.encrolcodigo AS encrolcodigo,\r\n	tbfnxndetallerol.rubcodigo AS rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip AS rubdescrip,\r\n	tbfnxnhisrubros.rubtipo AS rubtipo,\r\n	tbfnxnhisrubros.rubnomcor AS rubnomcor,\r\n	tbfnxnhisrubros.rubprovision AS rubprovision,\r\n	sum(tbfnxndetallerol.valor) AS valor,\r\n	tbfnxnhisempleado.coscodigo AS orden\r\nFROM\r\n	(\r\n		(\r\n			(\r\n				(\r\n					(\r\n						(\r\n							tbfnxndetallerol\r\n							JOIN tbfnxnhisrubros ON (\r\n								(\r\n									(\r\n										tbfnxndetallerol.encrolcodigo = tbfnxnhisrubros.encrolcodigo\r\n									)\r\n									AND (\r\n										tbfnxndetallerol.rubcodigo = tbfnxnhisrubros.rubcodigo\r\n									)\r\n								)\r\n							)\r\n						)\r\n						JOIN tbfnxnhisempleado ON (\r\n							(\r\n								(\r\n									tbfnxnhisempleado.encrolcodigo = tbfnxndetallerol.encrolcodigo\r\n								)\r\n								AND (\r\n									tbfnxnhisempleado.empcedula = tbfnxndetallerol.empcedula\r\n								)\r\n							)\r\n						)\r\n					)\r\n					JOIN tbfnxndepartamento ON (\r\n						(\r\n							tbfnxnhisempleado.empdepar = tbfnxndepartamento.depcodigo\r\n						)\r\n					)\r\n				)\r\n				JOIN tbfnxnpersonal ON (\r\n					(\r\n						tbfnxnhisempleado.emptipopersonal = tbfnxnpersonal.percodigo\r\n					)\r\n				)\r\n			)\r\n			JOIN tbfnxntipoempleado ON (\r\n				(\r\n					tbfnxnhisempleado.emptipoemple = tbfnxntipoempleado.tecodigo\r\n				)\r\n			)\r\n		)\r\n		JOIN tbfnxntiporemuner ON (\r\n			(\r\n				tbfnxnhisempleado.emptiporemun = tbfnxntiporemuner.trcodigo\r\n			)\r\n		)\r\n	)\r\nWHERE\r\n	(\r\n		(\r\n			tbfnxnhisrubros.rubtipo = \'E\'\r\n		)\r\n		AND (\r\n			tbfnxndetallerol.valor <> 0\r\n		)\r\n		AND (\r\n			tbfnxnhisrubros.rubprovision <> 1\r\n		)\r\n		AND (\r\n			tbfnxndetallerol.encrolcodigo = \'0000000205\'\r\n		)\r\n		AND (\r\n			tbfnxnhisempleado.encrolcodigo = \'0000000205\'\r\n		)\r\n		AND (\r\n			tbfnxnhisrubros.encrolcodigo = \'0000000205\'\r\n		)\r\n	)\r\nGROUP BY\r\n	tbfnxndetallerol.encrolcodigo,\r\n	tbfnxndetallerol.rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.rubnomcor,\r\n	tbfnxnhisrubros.rubprovision,\r\n	tbfnxnhisempleado.coscodigo\r\nORDER BY\r\n	tbfnxnhisempleado.coscodigo ;\r\n\r\n-- ----\r\n\r\n-- ----\r\nCREATE OR REPLACE\r\nSQL SECURITY INVOKER \r\nVIEW vfnxrptvaloresperiodos AS \r\nSELECT  \r\n	tbfnxndetallerol.empcedula AS empcedula,\r\n	sum(tbfnxndetallerol.valor) AS valor,\r\n	YEAR (tbfnxndetallerol.fecha) AS periodo\r\nFROM\r\n	(\r\n		tbfnxndetallerol\r\n		JOIN tbfnxnrubros ON (\r\n			(\r\n				tbfnxndetallerol.rubcodigo = tbfnxnrubros.rubcodigo\r\n			)\r\n		)\r\n	)\r\nWHERE\r\n	(\r\n		(\r\n			tbfnxnrubros.rubtipo = \'I\'\r\n		)\r\n		AND (\r\n			(\r\n				tbfnxndetallerol.rubcodigo = \'FRES_ING\'\r\n			)\r\n			OR (\r\n				tbfnxndetallerol.rubcodigo = \'CDC\'\r\n			)\r\n			OR (\r\n				tbfnxndetallerol.rubcodigo = \'CDTS\'\r\n			)\r\n		)\r\n	)\r\nGROUP BY\r\n	YEAR (tbfnxndetallerol.fecha),\r\n	tbfnxndetallerol.empcedula ;\r\n\r\n-- ----\r\n\r\n-- ----\r\nCREATE OR REPLACE\r\nSQL SECURITY INVOKER \r\nVIEW vfnxrptrubroaingreso AS \r\nSELECT  \r\n	tbfnxndetallerol.encrolcodigo AS encrolcodigo,\r\n	tbfnxndetallerol.rubcodigo AS rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip AS rubdescrip,\r\n	tbfnxnhisrubros.rubtipo AS rubtipo,\r\n	tbfnxnhisrubros.rubnomcor AS rubnomcor,\r\n	tbfnxnhisrubros.rubprovision AS rubprovision,\r\n	sum(tbfnxndetallerol.valor) AS valor,\r\n	tbfnxnhisempleado.coscodigo AS orden\r\nFROM\r\n	(\r\n		(\r\n			(\r\n				(\r\n					(\r\n						(\r\n							tbfnxndetallerol\r\n							JOIN tbfnxnhisrubros ON (\r\n								(\r\n									(\r\n										tbfnxndetallerol.encrolcodigo = tbfnxnhisrubros.encrolcodigo\r\n									)\r\n									AND (\r\n										tbfnxndetallerol.rubcodigo = tbfnxnhisrubros.rubcodigo\r\n									)\r\n								)\r\n							)\r\n						)\r\n						JOIN tbfnxnhisempleado ON (\r\n							(\r\n								(\r\n									tbfnxnhisempleado.encrolcodigo = tbfnxndetallerol.encrolcodigo\r\n								)\r\n								AND (\r\n									tbfnxnhisempleado.empcedula = tbfnxndetallerol.empcedula\r\n								)\r\n							)\r\n						)\r\n					)\r\n					JOIN tbfnxndepartamento ON (\r\n						(\r\n							tbfnxnhisempleado.empdepar = tbfnxndepartamento.depcodigo\r\n						)\r\n					)\r\n				)\r\n				JOIN tbfnxnpersonal ON (\r\n					(\r\n						tbfnxnhisempleado.emptipopersonal = tbfnxnpersonal.percodigo\r\n					)\r\n				)\r\n			)\r\n			JOIN tbfnxntipoempleado ON (\r\n				(\r\n					tbfnxnhisempleado.emptipoemple = tbfnxntipoempleado.tecodigo\r\n				)\r\n			)\r\n		)\r\n		JOIN tbfnxntiporemuner ON (\r\n			(\r\n				tbfnxnhisempleado.emptiporemun = tbfnxntiporemuner.trcodigo\r\n			)\r\n		)\r\n	)\r\nWHERE\r\n	(\r\n		(\r\n			tbfnxnhisrubros.rubtipo = \'I\'\r\n		)\r\n		AND (\r\n			tbfnxndetallerol.valor <> 0\r\n		)\r\n		AND (\r\n			tbfnxndetallerol.encrolcodigo = \'0000000205\'\r\n		)\r\n		AND (\r\n			tbfnxnhisempleado.encrolcodigo = \'0000000205\'\r\n		)\r\n		AND (\r\n			tbfnxnhisrubros.encrolcodigo = \'0000000205\'\r\n		)\r\n	)\r\nGROUP BY\r\n	tbfnxndetallerol.encrolcodigo,\r\n	tbfnxndetallerol.rubcodigo,\r\n	tbfnxnhisrubros.rubdescrip,\r\n	tbfnxnhisrubros.rubtipo,\r\n	tbfnxnhisrubros.rubnomcor,\r\n	tbfnxnhisrubros.rubprovision,\r\n	tbfnxnhisempleado.coscodigo\r\nORDER BY\r\n	tbfnxnhisempleado.coscodigo ;\r\n-- ----\r\n\r\n-- ----\r\nCREATE OR REPLACE\r\nSQL SECURITY INVOKER \r\nVIEW vfnxrptdatosvacaciones AS \r\nSELECT  \r\n	sum(\r\n		tbfnxnhisempleado.emptotaling\r\n	) AS totalingresos,\r\n	YEAR (\r\n		tbfnxnencabezadorol.fecha\r\n	) AS periodos,\r\n\r\nIF (\r\n	(\r\n		YEAR (\r\n			tbfnxnencabezadorol.fecha\r\n		) = YEAR ((now() + INTERVAL -(1) YEAR))\r\n	),\r\n	(\r\n		sum(\r\n			tbfnxnhisempleado.emptotaling\r\n		) - vfnxrptvaloresperiodos.valor\r\n	),\r\n	0\r\n) AS vperido,\r\n\r\nIF (\r\n	(\r\n		YEAR (\r\n			tbfnxnencabezadorol.fecha\r\n		) = YEAR ((now() + INTERVAL -(1) YEAR))\r\n	),\r\n	(\r\n		sum(\r\n			tbfnxnhisempleado.emptotaling\r\n		) - vfnxrptvaloresperiodos.valor\r\n	),\r\n	0\r\n) AS vvalor,\r\n tbfnxnencabezadorol.encroltipo1 AS encroltipo1,\r\n vfnxrptvaloresperiodos.valor AS valor,\r\n tbfnxnempleado.empcedula AS empcedula,\r\n tbfnxnempleado.empfecing AS empfecing,\r\n tbfnxnempleado.empdepar AS empdepar,\r\n tbfnxnempleado.nombres AS nombres,\r\n tbfnxnempleado.coscodigo AS coscodigo,\r\n tbfnxncostos.cosnombre AS cosnombre\r\nFROM\r\n	(\r\n		(\r\n			(\r\n				(\r\n					tbfnxnencabezadorol\r\n					JOIN tbfnxnhisempleado ON (\r\n						(\r\n							tbfnxnencabezadorol.encrolcodigo = tbfnxnhisempleado.encrolcodigo\r\n						)\r\n					)\r\n				)\r\n				JOIN vfnxrptvaloresperiodos ON (\r\n					(\r\n						tbfnxnhisempleado.empcedula = vfnxrptvaloresperiodos.empcedula\r\n					)\r\n				)\r\n			)\r\n			JOIN tbfnxnempleado ON (\r\n				(\r\n					tbfnxnhisempleado.empcedula = tbfnxnempleado.empcedula\r\n				)\r\n			)\r\n		)\r\n		JOIN tbfnxncostos ON (\r\n			(\r\n				tbfnxnempleado.coscodigo = tbfnxncostos.coscodigo\r\n			)\r\n		)\r\n	)\r\nWHERE\r\n	(\r\n		(\r\n			YEAR (\r\n				tbfnxnencabezadorol.fecha\r\n			) = vfnxrptvaloresperiodos.periodo\r\n		)\r\n		AND (\r\n			tbfnxnencabezadorol.encroltipo1 = \'MENSUAL\'\r\n		)\r\n		AND (\r\n			tbfnxnempleado.empactivo = \'Activo\'\r\n		)\r\n	)\r\nGROUP BY\r\n	YEAR (\r\n		tbfnxnencabezadorol.fecha\r\n	),\r\n	tbfnxnempleado.empcedula,\r\n	tbfnxnempleado.empfecing,\r\n	tbfnxnempleado.nombres,\r\n	tbfnxnempleado.coscodigo,\r\n	tbfnxnempleado.empdepar,\r\n	tbfnxnencabezadorol.encroltipo1 ;\r\n\r\n-- ----\r\n\r\n-- ----\r\n\r\nCREATE OR REPLACE\r\nSQL SECURITY INVOKER \r\nVIEW v_fnx_reporte AS \r\nSELECT \r\n	ER.encrolcodigo,\r\n	ER.encroldescrip,\r\n	EM.nombres,\r\n	DR.valor,\r\n	DR.empcedula,\r\n	RU.rubcodigo,\r\n	RU.rubdescrip,\r\n	SRI.nsridescrip,\r\n	SRI.nsricodigo,\r\n	SRI.nsrieditable\r\nFROM\r\n	tbfnxndetallerol DR\r\nINNER JOIN tbfnxnrubros RU ON DR.rubcodigo = RU.rubcodigo\r\nINNER JOIN tbfnxnsri107 SRI ON RU.rubsri = SRI.nsricodigo\r\nINNER JOIN tbfnxnencabezadorol ER ON DR.encrolcodigo = ER.encrolcodigo\r\nINNER JOIN tbfnxnempleado EM ON DR.empcedula = EM.empcedula\r\nWHERE\r\n	YEAR (DR.fecha) = \'2016\'\r\nAND (\r\n	(ER.encroltipo1 = \'MENSUAL\')\r\n	OR (\r\n		ER.encroltipo1 = \'PPROVICION\'\r\n		AND DR.rubcodigo = (\r\n			SELECT\r\n				HOM.homologado\r\n			FROM\r\n				tbfnxnhomologarubro HOM\r\n			WHERE\r\n				HOM.codigo = \'FNXD4\'\r\n		)\r\n	)\r\n) ;\r\n\r\n-- ----\r\n\r\n-- ----\r\n\r\nCREATE OR REPLACE\r\nSQL SECURITY INVOKER \r\nVIEW v_fnxrptcargaporedad AS \r\nSELECT \r\n	tbfnxnempleado.nombres AS NOMBRES,\r\n	tbfnxnempleado.empestcivil AS EMPESTCIVIL,\r\n	tbfnxnempleado.empactivo AS EMPACTIVO,\r\n	tbfnxnempleado.coscodigo AS COSCODIGO,\r\n	tbfnxnempleado.empfecing AS EMPFECING,\r\n	tbfnxnempleado.empcategoria AS EMPCATEGORIA,\r\n	tbfnxnempleado.empcargas AS EMPCARGAS,\r\n	tbfnxnempleado.empcedula AS EMPCEDULA,\r\n	tbfnxnempleado.emphijos AS EMPHIJOS,\r\n	tbfnxnfamilia.famcodigo AS FAMCODIGO,\r\n	tbfnxnfamilia.famparentesco AS famparentesco,\r\n	tbfnxnfamilia.famnombre AS famnombre,\r\n	tbfnxnfamilia.famcarga AS famcarga,\r\n	tbfnxnfamilia.famfecnac AS famFECNAC,\r\n	tbfnxnfamilia.famdomicilio AS famdomicilio,\r\n	tbfnxnfamilia.famocupacion AS famocupacion,\r\n	tbfnxnfamilia.famtelf AS famtelf,\r\n	tbfnxndepartamento.depcodigo AS depcodigo,\r\n	tbfnxndepartamento.depnombre AS depnombre,\r\n	(\r\n		YEAR (now()) - YEAR (\r\n			tbfnxnfamilia.famfecnac\r\n		)\r\n	) AS edad,\r\n	(\r\n		MONTH (now()) - MONTH (\r\n			tbfnxnfamilia.famfecnac\r\n		)\r\n	) AS mes,\r\n\r\nIF (\r\n	(\r\n		tbfnxnfamilia.famparentesco = \'Hijo\'\r\n	),\r\n	1,\r\n	0\r\n) AS hijos,\r\n\r\nIF (\r\n	(\r\n		tbfnxnfamilia.famparentesco = \'Hija\'\r\n	),\r\n	1,\r\n	0\r\n) AS hijas,\r\n tbfnxncostos.cosnombre AS cosnombre\r\nFROM\r\n	(\r\n		(\r\n			(\r\n				tbfnxnempleado\r\n				JOIN tbfnxnfamilia ON (\r\n					(\r\n						tbfnxnempleado.empcedula = tbfnxnfamilia.empcedula\r\n					)\r\n				)\r\n			)\r\n			JOIN tbfnxndepartamento ON (\r\n				(\r\n					tbfnxnempleado.empdepar = tbfnxndepartamento.depcodigo\r\n				)\r\n			)\r\n		)\r\n		JOIN tbfnxncostos ON (\r\n			(\r\n				tbfnxnempleado.coscodigo = tbfnxncostos.coscodigo\r\n			)\r\n		)\r\n	)\r\nORDER BY\r\n	tbfnxnempleado.coscodigo,\r\n	tbfnxnempleado.nombres,\r\n	tbfnxnempleado.empcedula ;\r\n-- ----------------\r\n-- ----------------\r\nCREATE OR REPLACE \r\nSQL SECURITY INVOKER \r\nVIEW v_fnxfechasreingresoempleados AS \r\nSELECT \r\n	tbfnxnhistorialingreso.empcedula AS empcedula,\r\n	tbfnxnhistorialingreso.hicodigo AS hicodigo,\r\n	tbfnxnhistorialingreso.hifecingemp AS hifecingemp,\r\n	tbfnxnhistorialingreso.hifecsalemp AS hifecsalemp,\r\n	tbfnxnhistorialingreso.himotivosalemp AS himotivosalemp,\r\n	tbfnxnhistorialingreso.hientroiess AS hientroiess,\r\n	min(\r\n		tbfnxnhistorialingreso.hifechaing\r\n	) AS fechaingreso,\r\n	min(\r\n		tbfnxnhistorialingreso.hifechasal\r\n	) AS fechasalida,\r\n	max(\r\n		tbfnxnhistorialingreso.hifechaing\r\n	) AS fechareingreso,\r\n	max(\r\n		tbfnxnhistorialingreso.hifechasal\r\n	) AS fechasali2,\r\n	tbfnxnhistorialingreso.himotivo AS himotivo,\r\n	tbfnxnhistorialingreso.hidias AS hidias,\r\n	tbfnxnhistorialingreso.hidiasacum AS hidiasacum\r\nFROM\r\n	tbfnxnhistorialingreso\r\nGROUP BY\r\n	tbfnxnhistorialingreso.empcedula\r\nORDER BY\r\n	tbfnxnhistorialingreso.hifecingemp ;\r\n\r\n', '', 1, NULL, 'ctigse', NULL, NULL, 'Nómina');
