mina,hQueryWithoutCorrection,\\\"call Fnxsp_NReporteParametrizable (\\\'\\\"+lsTxtvista+\\\"\\\')\\\") = False THEN\\r\\n				pgUti_Mess(Error(ErrorInfo()),\\\"ADS Informa\\\",Detener)\\r\\n			END\\r\\n			sRuta is string = fExeDir() +\\\"\\\\Informes_Consulta\\\"\\r\\n			sReportCustom is string=\\\"\\\"//+sRuta\\r\\n			sReportNormal is string=\\\"RPT_FnxRptRolGeneral\\\"\\r\\n			iPreview(ipvZoom100)\\r\\n			IF _DirectorioReportes(sReportCustom) THEN\\r\\n				iPrintReport(sReportCustom,sCODIGOROL)\\r\\n			ELSE\\r\\n				iPrintReport(sReportNormal,sCODIGOROL)\\r\\n			END\\r\\n			sListaI=\\\"\\\"\\r\\n			sListaE=\\\"\\\"\\r\\n			sCode =\\\"\\\"\\r\\n			sCodeE=\\\"\\\"\\r\\nEND\\r\\n\\r\\n\\r\\nRESULT bCorreto\' WHERE (init_cod=\'RPTCODI1RP\');\r\nUPDATE tbfnxninit_varios SET init_def=\'VFP\', init_obs=\'bCorreto is boolean=False\\r\\nLsTxtinser is string = [\\r\\n	Insert INTO tbfnxnrolparametrizable (encroldescrip ,rol ,grupo ,orden ,empapeuno ,empapedos ,empnomuno ,empnomdos ,empcodigo ,empcedula ,\\r\\n	%1	%2 campoi0,campoe0 ,campol0)\\r\\n]\\r\\ninfo(sListaI,sListaE)\\r\\nLsTxtinser = StringBuild(LsTxtinser,sListaI,sListaE)\\r\\nLsTxtvista is string = [\\r\\n	%6\\r\\n	SELECT\\r\\n	tbfnxnencabezadorol.encroldescrip ,\\r\\n	tbfnxndetallerol.encrolcodigo as rol,\\r\\n	%1 as grupo,\\r\\n	%2 as orden, \\r\\n	tbfnxnhisempleado.empapeuno as empapeuno,\\r\\n	tbfnxnhisempleado.empapedos as empapedos,\\r\\n	tbfnxnhisempleado.empnomuno as empnomuno,\\r\\n	tbfnxnhisempleado.empnomdos as empnomdos,\\r\\n	tbfnxnhisempleado.empcodigo as empcodigo,\\r\\n	tbfnxnhisempleado.empcedula as empcedula,\\r\\n	%7\\r\\n	%8\\r\\n	tbfnxnhisempleado.emptotaling as campoi0,\\r\\n	tbfnxnhisempleado.emptotalegr as campoe0,\\r\\n	tbfnxnhisempleado.empliquido as campol0\\r\\n	FROM\\r\\n	tbfnxndetallerol,tbfnxnencabezadorol,tbfnxnhisempleado, tbfnxncurselemp %3\\r\\n	WHERE 	tbfnxndetallerol.encrolcodigo=\\\'\\\'%4\\\'\\\'\\r\\n	AND 	tbfnxndetallerol.encrolcodigo=tbfnxnencabezadorol.encrolcodigo\\r\\n	A