\\nOTHER CASE\\r\\n		IF HExecuteSQLQuery(dsData,MySqlNomina,hQueryWithoutCorrection,lsTxt) THEN\\r\\n		sRuta is string = fExeDir() +\\\"\\\\Informes_Consulta\\\"\\r\\n		sReportCustom is string=\\\"RPT_FnxRptRolIndividualSinCopia\\\"//+sRuta\\r\\n		sReportNormal is string=\\\"RPT_FnxRptRolIndividualSinCopia\\\"\\r\\n		iPreview(ipvZoom100)\\r\\n		IF _DirectorioReportes(\\\"RPT_FnxRptRolIndividualSinCopia\\\") THEN\\r\\n			iPrintReport(sReportCustom)\\r\\n		ELSE\\r\\n			iPrintReport(sReportNormal,myresul)\\r\\n		END\\r\\n	ELSE\\r\\n		pgUti_Mess(Error(ErrorInfo()),\\\"ADS Informa\\\",Detener)\\r\\n	END\\r\\nEND\\r\\n\\r\\nRESULT bCorreto\' WHERE (init_cod=\'RPTRISC\');\r\nUPDATE tbfnxninit_varios SET init_def=\'VFP\', init_obs=\'bCorreto is boolean=False\\r\\nlsInfEmpresa is string = [\\r\\n	SELECT nombre, direccion, telefono, ruc, logo from tbfnxninit ;\\r\\n]\\r\\nFnxCursorVFP(lsInfEmpresa,\\\"nempresa\\\") \\r\\n\\r\\nLsTxt is string = [\\r\\n	SELECT DISTINCT  tbfnxnhisempleado.empfecing,empfecsal,empdepar,empsalario,empdiastrab,emptiporemun as tipo_remuneracion,\\r\\n	encroldescrip,tbfnxndetallerol.encrolcodigo as rol,%2 as orden,\\r\\n	empapeuno,empapedos,empnomuno,empnomdos,tbfnxnhisempleado.empcedula,rubdescrip,empliquido,\\r\\n	emptotaling,emptotalegr,valor,tbfnxnhisrubros.rubtipo as tipo,tbfnxnhisrubros.rubcodigo,tbfnxnencabezadorol.fecha,tbfnxnhisempleado.empcodigo,\\r\\n	tbfnxndetallerol.encrolcodigo,%3 as grupo,tbfnxnhisempleado.nombres,tbfnxnhisrubros.rubtipo,tbfnxnhisrubros.ruborden,tbfnxnhisempleado.empmail\\r\\n	FROM  tbfnxndetallerol,tbfnxnhisrubros,tbfnxnhisempleado,tbfnxndepartamento,tbfnxnencabezadorol,tbfnxncurselemp, %5\\r\\n	WHERE tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\\r\\n	and tbfnxnhisempleado.empdepar=tbfnxndepartamento.depcodigo\\r\\n	AND tbfnxndetallerol.rubcodigo=tbfnxnhisrubros.rubcodigo\\r\\n	AND tbfnxnhisrubros.rubprovision=FALSE\\r\\n	AND tbfnxndetallerol.encrolcodigo= \\\'%1\\\'\\r\\n	AND tbfnxnhisrubros.encrolcodigo= \\\'%1\\\'\\r\\n	AND tbfnxnhisempleado.encrolcodigo= \\\'%1\\\'	\\r\\n	and tbfnxnencabezadorol.encrolcodigo= \\\'%1\\\'\\r\\n	AND valor <> 0\\r\\n	AND tbfnxndetallerol.rubcodigo <> \\\'SALARIO\\\'\\r\\n	%6\\r\\n	and tbfnxnhisempleado.empcedula = tbfnxncurselemp.empcedula\\r\\n	%4\\r\\n	ORDER BY tbfnxndetallerol.encrolcodigo,%3,%2 ,rubtipo desc,ruborden;\\r\\n]\\r\\nLsTxt = StringBuild(LsTxt,sCODIGOROL,sLcOrden,sLcGrupoCam,sLcGrupoRel,gsTablas,sValorextra)\\r\\ndsData is Data Source\\r\\nSWITCH NoSpace(gsRptvfp)\\r\\nCASE \\\"VFP\\\"\\r\\n	///////////////////////////////////////////////////////////////////////////////////////////////////\\r\\n	// Codigo de visualizacion de reporte en VFP																										 //\\r\\n	///////////////////////////////////////////////////////////////////////////////////////////////////\\r\\n	FnxCursorVFP(LsTxt,\\\"nrolindividualvarios\\\") \\r\\n	FnxPreview()\\r\\n	FnxReportVFP(*,\\\"RolIndividualVarios.frx\\\",\\\"nrolindividualvarios.dbf\\\")\\r\\n\\r\\nOTHER CASE\\r\\n		/// abrir reporte\\r\\n		sRuta is string = fExeDir() +\\\"\\\\Informes_Consulta\\\"\\r\\n		sReportCustom is string=\\\"\\\"//+sRuta\\r\\n		sReportNormal is string=\\\"RPT_FnxRptRolIndividualVarias\\\"\\r\\n		iPreview(ipvZoom100)\\r\\n		IF _DirectorioReportes(sReportCustom) THEN\\r\\n			iPrintReport(sReportCustom)\\r\\n		ELSE\\r\\n			iPrintReport(sReportNormal,LsTxt)\\r\\n		END\\r\\nEND\\r\\nRESULT bCorreto\' WHERE (init_cod=\'RPTRIV\');\r\nUPDATE tbfnxninit_varios SET init_def=\'VFP\', init_obs=\'bCorreto is boolean=False\\r\\nLsTxtinser is string = [\\r\\n	Insert INTO tbfnxnrolparametrizable (encroldescrip ,rol ,grupo ,orden ,empapeuno ,empapedos ,empnomuno ,empnomdos ,empcodigo ,empcedula ,\\r\\n	%1	%2 campoi0,campoe0 ,campol0)\\r\\n]\\r\\nLsTxtinser= StringBuild(LsTxtinser,sListaI,sListaE)\\r\\nLsTxtvista is string = [\\r\\n	%6\\r\\n	SELECT\\r\\n	tbfnxnencabezadorol.encroldescrip ,\\r\\n	tbfnxndetallerol.encrolcodigo as rol,\\r\\n	%1 as grupo,\\r\\n	%2 as orden, \\r\\n	tbfnxnhisempleado.empapeuno as empapeuno,\\r\\n	tbfnxnhisempleado.empapedos as empapedos,\\r\\n	tbfnxnhisempleado.empnomuno as empnomuno,\\r\\n	tbfnxnhisempleado.empnomdos as empnomdos,\\r\\n	tbfnxnhisempleado.empcodigo as empcodigo,\\r\\n	tbfnxnhisempleado.empcedula as empcedula,\\r\\n	%7\\r\\n	%8\\r\\n	tbfnxnhisempleado.emptotaling as campoi0,\\r\\n	tbfnxnhisempleado.emptotalegr as campoe0,\\r\\n	tbfnxnhisempleado.empliquido as campol0\\r\\n	FROM\\r\\n	tbfnxndetallerol,tbfnxnencabezadorol,tbfnxnhisempleado, tbfnxncurselemp %3\\r\\n	WHERE 	tbfnxndetallerol.encrolcodigo=\\\'\\\'%4\\\'\\\'\\r\\n	AND 	tbfnxndetallerol.encrolcodigo=tbfnxnencabezadorol.encrolcodigo\\r\\n	AND 	tbfnxnencabezadorol.encrolcodigo=tbfnxnhisempleado.encrolcodigo\\r\\n	AND  	tbfnxndetallerol.empcedula=tbfnxnhisempleado.empcedula\\r\\n	%5\\r\\n	AND tbfnxnhisempleado.empcedula = tbfnxncurselemp.Empcedula\\r\\n	GROUP   by tbfnxnhisempleado.empcedula\\r\\n	order by %2;\\r\\n]\\r\\nSWITCH NoSpace(gsRptvfp)\\r\\nCASE \\\"VFP\\\"\\r\\n///////////////////////////////////////////////////////////////////////////////////////////////////\\r\\n// Codigo de visualizacion de reporte en VFP																										 //\\r\\n//////////////////////////////////////////////////////////////////////////////////////////////////\\r\\ninfo(gsRptvfp)\\r\\nLsTxtvista = [\\r\\n	SELECT\\r\\ntbfnxnencabezadorol.encrolcodigo as rol,\\r\\ntbfnxnencabezadorol.encroldescrip,\\r\\ntbfnxnhisempleado.emptotaling AS emptotaling,\\r\\ntbfnxnhisempleado.emptotalegr AS emptotalegr,\\r\\ntbfnxnhisempleado.empliquido AS empliquido\\r\\nFROM\\r\\ntbfnxnencabezadorol, tbfnxnhisempleado\\r\\nWHERE\\r\\ntbfnxnencabezadorol.encrolcodigo = tbfnxnhisempleado.encrolcodigo\\r\\nand  tbfnxnencabezadorol.encrolcodigo=\\\'%1\\\'\\r\\nGROUP   by 	tbfnxnencabezadorol.encrolcodigo\\r\\norder by tbfnxnhisempleado.nombres\\r\\n]\\r\\nlsingresos is string =[\\r\\n				SELECT \\r\\n					tbfnxndetallerol.encrolcodigo AS encrolcodigo,	\\r\\n					tbfnxndetallerol.rubcodigo AS rubcodigo,	\\r\\n					v_fnxhistoricosrubros.rubdescrip AS rubdescrip,	\\r\\n					v_fnxhistoricosrubros.rubtipo AS rubtipo,	\\r\\n					v_fnxhistoricosrubros.rubnomcor AS rubnomcor,	\\r\\n					v_fnxhistoricosrubros.rubprovision AS rubprovision,	\\r\\n					SUM(tbfnxndetallerol.valor) AS valor\\r\\n				FROM \\r\\n					tbfnxndetallerol,	\\r\\n					v_fnxhistoricosrubros\\r\\n				WHERE \\r\\n					tbfnxndetallerol.encrolcodigo = v_fnxhistoricosrubros.encrolcodigo\\r\\n					AND	tbfnxndetallerol.rubcodigo = v_fnxhistoricosrubros.rubcodigo\\r\\n					AND\\r\\n					(\\r\\n						v_fnxhistoricosrubros.rubtipo = \\\'I\\\'\\r\\n					 AND	tbfnxndetallerol.valor <> 0\\r\\n						AND	tbfnxndetallerol.encrolcodigo = \\\'%1\\\'\\r\\n						AND tbfnxndetallerol.rubcodigo<>\\\'SALARIO\\\'\\r\\n						\\r\\n					)\\r\\n				GROUP BY \\r\\n					tbfnxndetallerol.encrolcodigo,	\\r\\n					tbfnxndetallerol.rubcodigo,	\\r\\n					v_fnxhistoricosrubros.rubdescrip,	\\r\\n					v_fnxhistoricosrubros.rubtipo,	\\r\\n					v_fnxhistoricosrubros.rubnomcor,	\\r\\n					v_fnxhistoricosrubros.rubprovision\\r\\n\\r\\n\\r\\n]\\r\\nlsingresos=StringBuild(lsingresos,sCODIGOROL)\\r\\n\\r\\nlsegresos is string =[\\r\\n				SELECT \\r\\n					tbfnxndetallerol.encrolcodigo AS encrolcodigo,	\\r\\n					tbfnxndetallerol.rubcodigo AS rubcodigo,	\\r\\n					v_fnxhistoricosrubros.rubdescrip AS rubdescrip,	\\r\\n					v_fnxhistoricosrubros.rubtipo AS rubtipo,	\\r\\n					v_fnxhistoricosrubros.rubnomcor AS rubnomcor,	\\r\\n					SUM(tbfnxndetallerol.valor) AS valor\\r\\n				FROM \\r\\n					tbfnxndetallerol,	\\r\\n					v_fnxhistoricosrubros\\r\\n				WHERE \\r\\n					tbfnxndetallerol.encrolcodigo = v_fnxhistoricosrubros.encrolcodigo\\r\\n					AND	tbfnxndetallerol.rubcodigo = v_fnxhistoricosrubros.rubcodigo\\r\\n					AND\\r\\n					(\\r\\n						tbfnxndetallerol.encrolcodigo = \\\'%1\\\'\\r\\n						AND	v_fnxhistoricosrubros.rubprovision <> 1\\r\\n						AND	tbfnxndetallerol.valor <> 0\\r\\n						AND	v_fnxhistoricosrubros.rubtipo = \\\'E\\\'\\r\\n					)\\r\\n				GROUP BY \\r\\n					tbfnxndetallerol.encrolcodigo,	\\r\\n					tbfnxndetallerol.rubcodigo,	\\r\\n					v_fnxhistoricosrubros.rubdescrip,	\\r\\n					v_fnxhistoricosrubros.rubtipo,	\\r\\n					v_fnxhistoricosrubros.rubnomcor\\r\\n\\r\\n\\r\\n]\\r\\nlsegresos=StringBuild(lsegresos,sCODIGOROL)\\r\\n\\r\\nlsprovision is string =[\\r\\n			SELECT \\r\\n				tbfnxndetallerol.encrolcodigo AS encrolcodigo,	\\r\\n				tbfnxndetallerol.rubcodigo AS rubcodigo,	\\r\\n				v_fnxhistoricosrubros.rubdescrip AS rubdescrip,	\\r\\n				v_fnxhistoricosrubros.rubtipo AS rubtipo,	\\r\\n				v_fnxhistoricosrubros.rubnomcor AS rubnomcor,	\\r\\n				v_fnxhistoricosrubros.rubprovision AS rubprovision,	\\r\\n				SUM(tbfnxndetallerol.valor) AS valor\\r\\n			FROM \\r\\n				tbfnxndetallerol,	\\r\\n				v_fnxhistoricosrubros\\r\\n			WHERE \\r\\n				tbfnxndetallerol.encrolcodigo = v_fnxhistoricosrubros.encrolcodigo\\r\\n				AND	tbfnxndetallerol.rubcodigo = v_fnxhistoricosrubros.rubcodigo\\r\\n				AND\\r\\n				(\\r\\n					tbfnxndetallerol.valor <> 0\\r\\n					AND	v_fnxhistoricosrubros.rubprovision  = 1\\r\\n					AND	tbfnxndetallerol.encrolcodigo = \\\'%1\\\'\\r\\n				)\\r\\n			GROUP BY \\r\\n				tbfnxndetallerol.encrolcodigo,	\\r\\n				tbfnxndetallerol.rubcodigo,	\\r\\n				v_fnxhistoricosrubros.rubdescrip,	\\r\\n				v_fnxhistoricosrubros.rubtipo,	\\r\\n				v_fnxhistoricosrubros.rubnomcor,	\\r\\n				v_fnxhistoricosrubros.rubprovision\\r\\n\\r\\n\\r\\n]\\r\\nlsprovision=StringBuild(lsprovision,sCODIGOROL)\\r\\n\\r\\nFnxCursorVFP(lsingresos,\\\"nrolRubrosAgruIng\\\") \\r\\nFnxCursorVFP(lsegresos,\\\"nrolRubrosAgruEgre\\\") \\r\\nFnxCursorVFP(lsprovision,\\\"nrolRubrosAgruProv\\\") \\r\\n\\r\\nLsTxtvista = StringBuild(LsTxtvista,sCODIGOROL)\\r\\n			FnxCursorVFP(LsTxtvista,\\\"nrolRubrosAgrupados\\\") \\r\\n			FnxPreview()\\r\\n			FnxReportVFP(*,\\\"RolTotalRubros.frx\\\",\\\"nrolRubrosAgrupados.dbf\\\")\\r\\n\\r\\nOTHER CASE\\r\\n\\r\\nLsTxtvista = StringBuild(LsTxtvista,sLcGrupoCam,sLcOrden,gsTablas,sCODIGOROL,sLcGrupoRel,LsTxtinser,sCode , sCodeE )\\r\\ninfo(LsTxtvista)\\r\\nIF HExecuteSQLQuery(\\\"dsData\\\",MySqlNomina,hQueryWithoutCorrection,\\\"call Fnxsp_NReporteParametrizable (\\\'\\\"+LsTxtvista+\\\"\\\')\\\") = False THEN\\r\\n	pgUti_Mess(Error(ErrorInfo()),\\\"ADS Informa\\\",Detener)\\r\\nEND\\r\\n//abrir el reporte\\r\\nsRuta is string = fExeDir() +\\\"\\\\Informes_Consulta\\\"\\r\\nsReportCustom is string=\\\"\\\"//+sRuta\\r\\nsReportNormal is string=\\\"RPT_FnxRptRoldePagosTotalRubros\\\"\\r\\niPreview(ipvZoom100)\\r\\nIF _DirectorioReportes(sReportCustom) THEN\\r\\n	iPrintReport(sReportCustom)\\r\\nELSE\\r\\n	iPrintReport(sReportNormal,sCODIGOROL,TABLE_ListRoles.COL_encroldescrip)\\r\\nEND\\r\\n	\\r\\nEND\\r\\n\\r\\n// Limpiarvariables()\\r\\n\\r\\nRESULT bCorreto\' WHERE (init_cod=\'RPTTR\');\r\n', 'Verificar si se encuentra el ejecutable m_reporte en la siguiente direcion : C:\\Fenix\\AdsFenix\\Modulos', 1, NULL, 'ctige', NULL, NULL, 'Nómina');
