(22, '2019-11-25', 'FENIXTIME - NOMINA INTEGRACION', 'SET FOREIGN_KEY_CHECKS=0;\r\n\r\nDROP TABLE IF EXISTS `tbfnxnencbiometrico`;\r\nCREATE TABLE `tbfnxnencbiometrico` (\r\n  `codigobi` varchar(10) NOT NULL,\r\n  `encrolcodigo` varchar(10) DEFAULT NULL,\r\n  `fechainicio` date DEFAULT NULL,\r\n  `fechafin` date DEFAULT NULL,\r\n  `descripcion` varchar(255) DEFAULT NULL,\r\n  `exporto` varchar(255) DEFAULT NULL,\r\n  PRIMARY KEY (`codigobi`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\r\n\r\nDROP TABLE IF EXISTS `tbfnxndetbiometrico`;\r\nCREATE TABLE `tbfnxndetbiometrico` (\r\n  `codigobid` int(10) NOT NULL AUTO_INCREMENT,\r\n  `codigobi` varchar(10) NOT NULL,\r\n  `empcedula` varchar(10) DEFAULT NULL,\r\n  `estado` int(4) DEFAULT NULL,\r\n  `dias` decimal(10,5) DEFAULT NULL,\r\n  `vacaciones` decimal(10,5) DEFAULT NULL,\r\n  `atrasos` decimal(10,5) DEFAULT NULL,\r\n  `he25` decimal(10,5) DEFAULT NULL,\r\n  `he50` decimal(10,5) DEFAULT NULL,\r\n  `he100` decimal(10,5) DEFAULT NULL,\r\n  `encrolcodigo` varchar(10) DEFAULT NULL,\r\n  `diaatraso` decimal(10,5) DEFAULT NULL,\r\n  `faltas` decimal(10,5) DEFAULT NULL,\r\n  `sincronizado` int(4) DEFAULT NULL,\r\n  PRIMARY KEY (`codigobid`),\r\n  KEY `codigobi` (`codigobi`),\r\n  KEY `empcedula` (`empcedula`),\r\n  KEY `encrolcodigo` (`encrolcodigo`),\r\n  CONSTRAINT `tbfnxndetbiometrico_ibfk_1` FOREIGN KEY (`codigobi`) REFERENCES `tbfnxnencbiometrico` (`codigobi`),\r\n  CONSTRAINT `tbfnxndetbiometrico_ibfk_2` FOREIGN KEY (`empcedula`) REFERENCES `tbfnxnempleado` (`empcedula`),\r\n  CONSTRAINT `tbfnxndetbiometrico_ibfk_3` FOREIGN KEY (`encrolcodigo`) REFERENCES `tbfnxnencabezadorol` (`encrolcodigo`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\r\n\r\nDROP TABLE IF EXISTS `tbfnxnadibiometrico`;\r\nCREATE TABLE `tbfnxnadibiometrico` (\r\n  `biometrico` varchar(25) DEFAULT NULL,\r\n  `adicionales` varchar(25) DEFAULT NULL,\r\n  `tablaref` varchar(50) DEFAULT NULL,\r\n  `observacion` varchar(255) DEFAULT NULL\r\n) ENGINE=InnoDB DEFAUL