o = \\\'I\\\'\\r\\n					 AND	tbfnxndetallerol.valor <> 0\\r\\n						AND	tbfnxndetallerol.encrolcodigo = \\\'<<oAppPrm.frmprm.Codigorol>>\\\'\\r\\n						AND tbfnxndetallerol.rubcodigo<>\\\'SALARIO\\\'\\r\\n						\\r\\n					)\\r\\n				GROUP BY \\r\\n					tbfnxndetallerol.encrolcodigo,	\\r\\n					tbfnxndetallerol.rubcodigo,	\\r\\n					v_fnxhistoricosrubros.rubdescrip,	\\r\\n					v_fnxhistoricosrubros.rubtipo,	\\r\\n					v_fnxhistoricosrubros.rubnomcor,	\\r\\n					v_fnxhistoricosrubros.rubprovision\\r\\nENDTEXT\\r\\n\\r\\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\\\"nrolRubrosAgruIng\\\")\\r\\n\\r\\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\\r\\n	SELECT \\r\\n					tbfnxndetallerol.encrolcodigo AS encrolcodigo,	\\r\\n					tbfnxndetallerol.rubcodigo AS rubcodigo,	\\r\\n					v_fnxhistoricosrubros.rubdescrip AS rubdescrip,	\\r\\n					v_fnxhistoricosrubros.rubtipo AS rubtipo,	\\r\\n					v_fnxhistoricosrubros.rubnomcor AS rubnomcor,	\\r\\n					SUM(tbfnxndetallerol.valor) AS valor\\r\\n				FROM \\r\\n					tbfnxndetallerol,	\\r\\n					v_fnxhistoricosrubros\\r\\n				WHERE \\r\\n					tbfnxndetallerol.encrolcodigo = v_fnxhistoricosrubros.encrolcodigo\\r\\n					AND	tbfnxndetallerol.rubcodigo = v_fnxhistoricosrubros.rubcodigo\\r\\n					AND\\r\\n					(\\r\\n						tbfnxndetallerol.encrolcodigo = \\\'<<oAppPrm.frmprm.Codigorol>>\\\'\\r\\n						AND	v_fnxhistoricosrubros.rubprovision <> 1\\r\\n						AND	tbfnxndetallerol.valor <> 0\\r\\n						AND	v_fnxhistoricosrubros.rubtipo = \\\'E\\\'\\r\\n					)\\r\\n				GROUP BY \\r\\n					tbfnxndetallerol.encrolcodigo,	\\r\\n					tbfnxndetallerol.rubcodigo,	\\r\\n					v_fnxhistoricosrubros.rubdescrip,	\\r\\n					v_fnxhistoricosrubros.rubtipo,	\\r\\n					v_fnxhistoricosrubros.rubnomcor\\r\\nENDTEXT\\r\\n\\r\\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\\\"nrolRubrosAgruEgre\\\")\\r\\n\\r\\n\\r\\nTEXT TO lcSQLcommand NOSHOW TEXT PRETEXT 7\\r\\n		SELECT \\r\\n				tbfnxndetallerol.encrolcodigo AS encrolcodigo,	\\r\\n				tbfnxndetallerol.rubcodigo AS rubcodigo,	\\r\\n				v_fnxhistoricosrubros.rubdescrip AS rubdescrip,	\\r\\n				v_fnxhistoricosrubros.rubtipo AS rubtipo,	\\r\\n				v_fnxhistoricosrubros.rubnomcor AS rubnomcor,	\\r\\n				v_fnxhistoricosrubros.rubprovision AS rubprovision,	\\r\\n				SUM(tbfnxndetallerol.valor) AS valor\\r\\n			FROM \\r\\n				tbfnxndetallerol,	\\r\\n				v_fnxhistoricosrubros\\r\\n			WHERE \\r\\n				tbfnxndetallerol.encrolcodigo = v_fnxhistoricosrubros.encrolcodigo\\r\\n				AND	tbfnxndetallerol.rubcodigo = v_fnxhistoricosrubros.rubcodigo\\r\\n				AND\\r\\n				(\\r\\n					tbfnxndetallerol.valor <> 0\\r\\n					AND	v_fnxhistoricosrubros.rubprovision  = 1\\r\\n					AND	tbfnxndetallerol.encrolcodigo = \\\'<<oAppPrm.frmprm.Codigorol>>\\\'\\r\\n				)\\r\\n			GROUP BY \\r\\n				tbfnxndetallerol.encrolcodigo,	\\r\\n				tbfnxndetallerol.rubcodigo,	\\r\\n				v_fnxhistoricosrubros.rubdescrip,	\\r\\n				v_fnxhistoricosrubros.rubtipo,	\\r\\n				v_fnxhistoricosrubros.rubnomcor,	\\r\\n				v_fnxhistoricosrubros.rubprovision\\r\\n\\r\\nENDTEXT\\r\\n\\r\\ncmd = SQLEXEC(cnConnection,lcSQLcommand,\\\"nrolRubrosAgruProv\\\")\\r\\n	SET ENGINEBEHAVIOR 70\\r\\n\\r\\nUseTable(\\\"tbfnxninit\\\") \\r\\nSELECT nombre, direccion, telefono, ruc, logo from tbfnxninit   INTO CURSOR qemp\\r\\n\\r\\nSELECT * from qResultnrolRubrosAgrupados  INTO CURSOR qCurRol READWRITE \\r\\n\\r\\nCREATE CURSOR cur1(encrolcodi c(10),rubcodigoi c(10),rubdescripi c(30),valori n(20,4),rubcodigoe c(10),rubdescripe c(30),valore n(20,4),ordenrub n(5))\\r\\nCREATE CURSOR curol(encrolcodi c(10),rubcodigoi c(10),rubdescripi c(30),valori n(20,4),rubcodigoe c(10),rubdescripe c(30),valore n(20,4),rubcodigop c(10),rubdescripp c(30),valorp n(20,4),ordenrub n(5))\\r\\ncampo=0\\r\\nj=1\\r\\n	SELECT * from nrolRubrosAgruIng  INTO CURSOR CIng\\r\\n	camposing=reccount(\\\"CIng\\\")\\r\\n	SELECT * from nrolRubrosAgruEgre INTO CURSOR CEgr	\\r\\n	camposegr=reccount(\\\"CEgr\\\")\\r\\n	campo=j\\r\\n	*************************************************\\r\\n	IF camposing>camposegr\\r\\n		SELECT Cing\\r\\n		SCAN \\r\\n			SELECT cur1\\r\\n			APPEND BLANK \\r\\n			REPLACE;\\r\\n			 rubcodigoi		WITH Cing.rubcodigo,;\\r\\n			 rubdescripi	WITH Cing.rubdescrip,;\\r\\n			 encrolcodi	WITH Cing.encrolcodigo,;\\r\\n			 ordenrub		WITH j,;\\r\\n			 valori			WITH Cing.valor IN cur1			 \\r\\n			 j=j+1\\r\\n			 SELECT Cing\\r\\n		ENDSCAN\\r\\n		SELECT cur1		\\r\\n		IF j>campo		\\r\\n			GO campo				\\r\\n		ELSE\\r\\n			campo=j\\r\\n		ENDIF\\r\\n		lcCont=1\\r\\n		SELECT CEgr\\r\\n		SCAN \\r\\n			SELECT cur1			\\r\\n			REPLACE;\\r\\n			 rubcodigoe		WITH CEgr.rubcodigo,;\\r\\n			 rubdescripe	WITH CEgr.rubdescrip,;\\r\\n			 ordenrub		WITH lcCont,;\\r\\n			 valore			WITH CEgr.valor IN cur1			 \\r\\n			 SKIP 1\\r\\n			 lcCont=lcCont+1\\r\\n			 SELECT CEgr\\r\\n		ENDSCAN				\\r\\n	ELSE \\r\\n\\r\\n	*/////////////////////si egresos es mayor a ingresos\\r\\n		lcCont=1\\r\\n		SELECT CEgr\\r\\n		SCAN \\r\\n			SELECT cur1\\r\\n			APPEND BLANK \\r\\n			REPLACE;\\r\\n			 rubcodigoe		WITH CEgr.rubcodigo,;\\r\\n			 rubdescripe	WITH CEgr.rubdescrip,;\\r\\n			 encrolcodi	    WITH CEgr.encrolcodigo,;\\r\\n			 ordenrub		WITH lcCont,;\\r\\n			 valore			WITH CEgr.valor IN cur1\\r\\n			 j=j+1\\r\\n			 lcCont=lcCont+1\\r\\n			 SELECT CEgr			 \\r\\n		ENDSCAN				\\r\\n		SELECT cur1\\r\\n		IF j>campo		\\r\\n			GO campo				\\r\\n		ELSE\\r\\n			campo=j\\r\\n		ENDIF\\r\\n 		lcCont=1\\r\\n		SELECT Cing\\r\\n		SCAN \\r\\n			SELECT cur1		\\r\\n			REPLACE;\\r\\n			 rubcodigoi		WITH Cing.rubcodigo,;\\r\\n			 rubdescripi	WITH Cing.rubdescrip,;\\r\\n			 ordenrub		WITH lcCont,;\\r\\n			 valori			WITH Cing.valor IN cur1						 \\r\\n			 SKIP 1\\r\\n			 lcCont=lcCont+1\\r\\n			 SELECT Cing\\r\\n		ENDSCAN		\\r\\n	ENDIF\\r\\n			SELECT cur1	\\r\\n*//////////////////////////////////\\r\\ncampo=0\\r\\nj=1\\r\\n	camposrubros=reccount(\\\"cur1\\\")\\r\\n	SELECT * from nrolRubrosAgruProv INTO CURSOR CProv\\r\\n	campospro=reccount(\\\"CProv\\\")\\r\\n	campo=j\\r\\n	*************************************************\\r\\n	IF camposrubros>campospro\\r\\n		SELECT cur1 \\r\\n		SCAN \\r\\n			SELECT curol\\r\\n			APPEND BLANK \\r\\n			REPLACE;\\r\\n			 encrolcodi		WITH cur1.encrolcodi,;\\r\\n			 rubcodigoi		WITH cur1.rubcodigoi,;\\r\\n			 rubdescripi	WITH cur1.rubdescripi,;\\r\\n			 valori			WITH cur1.valori ,;			 \\r\\n			 rubcodigoe		WITH cur1.rubcodigoe,;\\r\\n			 rubdescripe	WITH cur1.rubdescripe,;\\r\\n			 ordenrub		WITH j,;\\r\\n			 valore			WITH cur1.valore IN curol			 \\r\\n			 j=j+1\\r\\n			 SELECT cur1 \\r\\n		ENDSCAN\\r\\n		\\r\\n		SELECT curol\\r\\n		\\r\\n		GO TOP 		\\r\\n		\\r\\n		IF j>campo		\\r\\n			GO campo				\\r\\n		ELSE\\r\\n			campo=j\\r\\n		ENDIF\\r\\n		lcCont=1\\r\\n		SELECT CProv\\r\\n		SCAN \\r\\n			SELECT curol			\\r\\n			REPLACE;\\r\\n			 rubcodigop		WITH CProv.rubcodigo,;\\r\\n			 rubdescripp	WITH CProv.rubdescrip,;\\r\\n			 ordenrub		WITH lcCont,;\\r\\n			 valorp			WITH CProv.valor IN curol			 \\r\\n			 SKIP 1\\r\\n			 lcCont=lcCont+1\\r\\n			 SELECT CProv\\r\\n		ENDSCAN				\\r\\n	ELSE \\r\\n	****\\r\\n	ENDIF\\r\\nSELECT curol	\\r\\nSELECT * FROM qCurRol, curol WHERE  ALLTRIM(qcurrol.rol) = ALLTRIM(curol.encrolcodi)  INTO CURSOR qCurRol \\r\\n\\r\\n\\r\\n*imprmir los reportes\\r\\nPrintViewReport(\\\"C:\\\\Fenix\\\\AdsFenix\\\\Modulos\\\\MenuWD\\\\ReportesVFP\\\\RolTotalRubros.frx\\\")\\r\\n\\r\\n\\r\\n\\r\\n\'\r\nWHERE\r\n	init_cod = \'RPTTR\' ;', '', 1, NULL, 'echango', NULL, NULL, 'Nómina');
