(30, '2021-06-15', 'ACTA DE FINIQUITO VALORES', '\r\nSET FOREIGN_KEY_CHECKS=0;\r\n\r\n-- ----------------------------\r\n-- Table structure for tbfnxnactafiniquitovalores\r\n-- ----------------------------\r\nDROP TABLE IF EXISTS `tbfnxnactafiniquitovalores`;\r\nCREATE TABLE `tbfnxnactafiniquitovalores` (\r\n  `id` int(10) NOT NULL AUTO_INCREMENT,\r\n  `empcedula` varchar(10) DEFAULT NULL,\r\n  `actRubro` varchar(50) DEFAULT NULL,\r\n  `valor` decimal(20,8) DEFAULT NULL,\r\n  `valorNuevo` decimal(20,8) DEFAULT NULL,\r\n  PRIMARY KEY (`id`),\r\n  KEY `empcedula` (`empcedula`),\r\n  CONSTRAINT `tbfnxnactafiniquitovalores_ibfk_1` FOREIGN KEY (`empcedula`) REFERENCES `tbfnxnempleado` (`empcedula`) ON DELETE CASCADE ON UPDATE CASCADE\r\n) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;\r\n\r\nALTER TABLE tbfnxnactafiniquitoreglas ADD actRubro VARCHAR(50); \r\n\r\nINSERT INTO tbfnxnactafiniquitoreglas (actRegla, actRubro) VALUES (\'//// HE50\\r\\nlnHE50 is numeric = Round(gnSueldo / 240 * 1.5 * Val(EDT_HE50..Value), 2)\\r\\nEDT_HE50_Calculadas = lnHE50\', \'HE50\');\r\nINSERT INTO tbfnxnactafiniquitoreglas (actRegla, actRubro) VALUES (\'//// HE100\\r\\nlnHE100 is numeric = Round(gnSueldo / 240 * 2 * Val(EDT_HE100..Value), 2)\\r\\nEDT_HE100_Calculadas = lnHE100\', \'HE100\');\r\nINSERT INTO tbfnxnactafiniquitoreglas (actRegla, actRubro) VALUES (\'/// Almuerzos\\r\\nEDT_Almuerzos_Calculadas = EDT_Almuerzos\', \'ALMUERZOS\');\r\nINSERT INTO tbfnxnactafiniquitoreglas (actRegla, actRubro) VALUES (\'//// OTROS DESCUENTOS\\r\\nEDT_Otros_Calculadas = EDT_Otros\', \'OTROS\');\r\nINSERT INTO tbfnxnactafiniquitoreglas (actRegla, actRubro) VALUES (\'//// FALTAS\\r\\nlnFaltas is numeric = Round(gnSueldo / 240 * 2 * Val(EDT_Faltas..Value), 2)\\r\\nEDT_Faltas_Calculadas = lnFaltas\', \'FALTAS\');\r\nINSERT INTO tbfnxnactafiniquitoreglas (actRegla, actRubro) VALUES (\'//// Permisos\\r\\nlnPermisos is numeric = Round(gnSueldo / 240 * Val(EDT_Permisos..Value), 2)\\r\\nEDT_Permisos_Calculadas = lnPermisos \', \'PERMISOS\');\r\nINSERT INTO tbfnxnactafiniquitoreglas (actRegla, actRubro) VALUES (\'/// Prestamos\\r\\nlnSaldo is numeric = 0\\r\\ndsPrestamo is Data Source\\r\\nlsQuery is string = \\\"SELECT SUM(ppimporte) as saldo FROM tbfnxnpagoprestamo WHERE empcedula = \\\'\\\" + gsCedula + \\\"\\\'\\\"\\r\\nIF HExecuteSQLQuery(dsPrestamo,MySqlNomina,hQueryWithoutCorrection,lsQuery) THEN\\r\\n	IF HNbRec(dsPrestamo)>0 THEN\\r\\n		HReadFirst(dsPrestamo)\\r\\n		lnSaldo = Round(dsPrestamo.saldo, 2)\\r\\n	END\\r\\nEND\\r\\nEDT_Prestamo_Calculadas = lnSaldo \', \'PRESTAMOS\');\r\n', NULL, 1, NULL, 'dfalconi', NULL, '', 'Nómina'),
