(37, '2021-12-29', 'CONTABILIZACION ACTA DE FINIQUITO', 'SET FOREIGN_KEY_CHECKS=0;\r\n\r\n-- ----------------------------\r\n-- Table structure for tbfnxnactafiniquitorubros\r\n-- ----------------------------\r\nDROP TABLE IF EXISTS `tbfnxnactafiniquitorubros`;\r\nCREATE TABLE `tbfnxnactafiniquitorubros` (\r\n  `codigo` varchar(10) NOT NULL,\r\n  `nombre` varchar(100) DEFAULT NULL,\r\n  `tipo` tinyint(4) DEFAULT \'1\' COMMENT \'1: ingreso, 2: gasto, 3: fijo\',\r\n  `codigoctadebe` varchar(100) DEFAULT NULL,\r\n  `codigoctahaber` varchar(100) DEFAULT NULL,\r\n  PRIMARY KEY (`codigo`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\r\n\r\n-- ----------------------------\r\n-- Records of tbfnxnactafiniquitorubros\r\n-- ----------------------------\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000001\', \'EMBARAZO\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000002\', \'BONIFICACION 25\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000003\', \'ENFERMEDAD NO PROFESIONAL\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000004\', \'DISCAPACIDAD\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000005\', \'DECIMO TERCERO\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000006\', \'DECIMO CUARTO\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000007\', \'DIRIGENTE SINDICAL\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000008\', \'VACACIONES\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000009\', \'DESPIDO INTEMPESTIVO\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000010\', \'DISCRIMINACION\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000011\', \'TERMINACION\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000012\', \'UTILIDADES\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000013\', \'SUELDO PENDIENTE\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000014\', \'HORA EXTRA\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000015\', \'HORA SUPLEMENTARIA\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000016\', \'RECARGO JORNADA NOCTURNA\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000017\', \'OTROS RUBROS\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000018\', \'FONDOS DE RESERVA\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000019\', \'APORTE IESS\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000020\', \'TOTAL LIQUIDACION\', \'3\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000021\', \'PRESTAMOS\', \'2\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000022\', \'ALMUERZOS\', \'2\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000023\', \'FALTAS\', \'2\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000024\', \'PERMISOS\', \'2\', \'\', \'\');\r\nINSERT INTO `tbfnxnactafiniquitorubros` VALUES (\'0000000025\', \'OTROS DESCUENTOS\', \'2\', \'\', \'\');\r\n\r\n-- ----------------------------\r\n-- Sec of tbfnxnactafiniquitorubros\r\n-- ----------------------------\r\nINSERT INTO `tbfnxnids` VALUES (\'Código de Acta Finiquito Rubros\', \'25\', \'tbfnxNACTAFINIQUITORUBROS\', \'1\', \'1.00000000\');\r\n\r\n-- ----------------------------\r\n-- Table structure for tbfnxnactafiniquitocontabilidad\r\n-- ----------------------------\r\nDROP TABLE IF EXISTS `tbfnxnactafiniquitocontabilidad`;\r\nCREATE TABLE `tbfnxnactafiniquitocontabilidad` (\r\n  `id` int(11) NOT NULL AUTO_INCREMENT,\r\n  `actcodigo` varchar(10) DEFAULT NULL,\r\n  `empcedula` varchar(10) DEFAULT NULL,\r\n  `rubcodigo` varchar(10) DEFAULT NULL,\r\n  `descripcion` varchar(100) DEFAULT NULL,\r\n  `valor` decimal(20,8) DEFAULT NULL,\r\n  PRIMARY KEY (`id`),\r\n  KEY `tbfnxnactafiniquitocontabilidad_ibfk_1` (`rubcodigo`),\r\n  CONSTRAINT `tbfnxnactafiniquitocontabilidad_ibfk_1` FOREIGN KEY (`rubcodigo`) REFERENCES `tbfnxnactafiniquitorubros` (`codigo`)\r\n) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;\r\n\r\n-- ----------------------------\r\n-- Table structure for tbfnxnactafiniquitorencon\r\n-- ----------------------------\r\nDROP TABLE IF EXISTS `tbfnxnactafiniquitorencon`;\r\nCREATE TABLE `tbfnxnactafiniquitorencon` (\r\n  `actcodigo` varchar(10) DEFAULT NULL,\r\n  `empcedula` varchar(10) DEFAULT NULL,\r\n  `codigocuenta` varchar(100) DEFAULT NULL,\r\n  `descripcion` varchar(100) DEFAULT NULL,\r\n  `importe` decimal(20,8) DEFAULT NULL\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\r\n\r\n-- ----------------------------\r\n-- Add columns to tables\r\n-- ----------------------------\r\nALTER TABLE `tbfnxnactafiniquito` ADD COLUMN `actContabilizado` tinyint(4) DEFAULT \'0\';\r\nALTER TABLE `tbfnxnactafiniquito` ADD COLUMN `actCodAsi` varchar(10) NULL DEFAULT \'\';\r\nALTER TABLE `tbfnxnactafiniquitocalculos` ADD COLUMN `actRubro` varchar(100) NULL DEFAULT \'\';\r\n\r\n-- ----------------------------\r\n-- Update data\r\n-- ----------------------------\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"DECIMO TERCERO\" WHERE actTabla = \"TABLE_Decimo3\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"DECIMO TERCERO\" WHERE actTabla = \"TABLE_Decimo3Anteriores\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"DECIMO CUARTO\" WHERE actTabla = \"TABLE_Decimo4Anteriores\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = UPPER(actDescripcion) WHERE actTabla = \"TABLE_Descuentos\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"FONDOS DE RESERVA\" WHERE actTabla = \"TABLE_OtrosIngresos\" AND actDescripcion LIKE \'F. R. pendientes mes de%\';\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = UPPER(actDescripcion) WHERE actTabla = \"TABLE_OtrosIngresos\" AND actDescripcion NOT LIKE \'F. R. pendientes mes de%\';\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = UPPER(actDescripcion) WHERE actTabla = \"TABLE_Remuneracion\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"UTILIDADES\" WHERE actTabla = \"TABLE_Utilidades\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"VACACIONES\" WHERE actTabla = \"TABLE_Vacaciones\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"APORTE IESS\" WHERE actTabla = \"TOTAL_APIESS\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"DECIMO TERCERO\" WHERE actTabla = \"TOTAL_Decimo3\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"DECIMO CUARTO\" WHERE actTabla = \"TOTAL_Decimo4\";\r\nUPDATE tbfnxnactafiniquitocalculos SET actRubro = \"VACACIONES\" WHERE actTabla = \"TOTAL_Vacaciones\";\r\n\r\n-- ----------------------------\r\n-- Create Procedure\r\n-- ----------------------------\r\nDROP PROCEDURE IF EXISTS `Fnxsp_NContabilizarActaFiniquito`;\r\n\r\nCREATE PROCEDURE `Fnxsp_NContabilizarActaFiniquito`(IN `codigoacta` varchar(10))\r\nBEGIN\r\n		DELETE FROM tbfnxnactafiniquitorencon;\r\n\r\n		INSERT INTO tbfnxnactafiniquitorencon\r\n		SELECT\r\n			actcodigo, \r\n			empcedula,\r\n			codigoctahaber  AS codigocuenta,\r\n			nombre,\r\n			SUM(VALOR) * -1 AS SUMA\r\n		FROM\r\n			tbfnxnactafiniquitocontabilidad c\r\n		INNER JOIN tbfnxnactafiniquitorubros r ON r.codigo = c.rubcodigo\r\n		WHERE codigoctahaber <> \"\"\r\n		AND actcodigo = codigoacta\r\n		GROUP BY codigoctahaber; \r\n	\r\n		INSERT INTO tbfnxnactafiniquitorencon\r\n		SELECT\r\n			actcodigo, \r\n			empcedula,\r\n			codigoctadebe AS codigocuenta,\r\n			nombre, SUM(VALOR) AS SUMA\r\n		FROM\r\n			tbfnxnactafiniquitocontabilidad c\r\n		INNER JOIN tbfnxnactafiniquitorubros r ON r.codigo = c.rubcodigo\r\n		WHERE codigoctadebe <> \"\"\r\n		AND actcodigo = codigoacta\r\n		GROUP BY codigoctadebe;\r\n		\r\n		SELECT * FROM tbfnxnactafiniquitorencon;\r\nEND ;\r\n\r\n', 'Activar el formulario WIN_Acta_Finiquito_Contabilizacion', 1, NULL, 'dfalconi', NULL, '', 'Nómina'),
(38, '2022-01-07', 'CONTROL DE PERMISOS POR DIA', 'SET FOREIGN_KEY_CHECKS=0;\r\n\r\n-- ----------------------------\r\n-- Table structure for tbfnxnpermisosbitacora\r\n-- ----------------------------\r\nDROP TABLE IF EXISTS `tbfnxnpermisosbitacora`;\r\nCREATE TABLE `tbfnxnpermisosbitacora` (\r\n  `codigo` varchar(10) NOT NULL,\r\n  `empcedula` varchar(10) DEFAULT NULL,\r\n  `fechasalida` date DEFAULT NULL,\r\n  `fechareingreso` date DEFAULT NULL,\r\n  `numerodias` decimal(20,8) DEFAULT NULL,\r\n  `recuperable` varchar(4) DEFAULT NULL,\r\n  `motivo` varchar(255) DEFAULT NULL,\r\n  `observacion` varchar(255) DEFAULT NULL,\r\n  `referencia` varchar(10) DEFAULT NULL,\r\n  PRIMARY KEY (`codigo`),\r\n  KEY `empcedula` (`empcedula`),\r\n  CONSTRAINT `tbfnxnpermisosbitacora_ibfk_1` FOREIGN KEY (`empcedula`) REFERENCES `tbfnxnempleado` (`empcedula`)\r\n) ENGINE=InnoDB DEFAULT CHARSET=utf8;\r\n\r\n-- ----------------------------\r\n-- Sec of tbfnxnpermisosbitacora\r\n-- ----------------------------\r\nDELETE FROM `tbfnxnids` WHERE `table` = \'tbfnxNPERMISOSBITACORA\';\r\nINSERT INTO `tbfnxnids` (`descrip`, `nextid`, `table`, `activo`, `reiniciar`) VALUES (\'Código de Permisos Bitacora\', \'1\', \'tbfnxNPERMISOSBITACORA\', \'1\', \'1.00000000\');\r\n\r\n', NULL, 1, NULL, 'dfalconi', NULL, '', 'Nómina'),
(39, '2022-01-14', 'CONTABILIZACION POR CUENTAS DE CENTROS DE COSTOS', 'DROP PROCEDURE IF EXISTS `Fnxsp_NCrearCurCont`;\r\n\r\nCREATE DEFINER=`root`@`localhost` PROCEDURE `Fnxsp_NCrearCurCont`(IN RolEncForm VARCHAR(10))\r\n    SQL SECURITY INVOKER\r\n    BEGIN\r\n		DECLARE EncrolcodigoV VARCHAR(10);\r\n		DECLARE empcedulaV VARCHAR(10);\r\n		DECLARE	empcodconV VARCHAR(30);\r\n		DECLARE empdeparV VARCHAR(10);\r\n		DECLARE depcodigoV VARCHAR(10);\r\n		DECLARE depcodconV VARCHAR(30);\r\n		DECLARE depcodcon2V VARCHAR(30);\r\n		DECLARE coscodigoV  VARCHAR(10);			 \r\n		DECLARE rubcodigoV VARCHAR(10);\r\n		DECLARE rubextensionV VARCHAR(10);\r\n    	DECLARE	valorV DECIMAL(23,8);		 \r\n		DECLARE codctadV VARCHAR(2);\r\n		DECLARE rubcodctadV VARCHAR(30);\r\n		DECLARE codctahV VARCHAR(2);\r\n		DECLARE rubcodctahV	VARCHAR(30);	\r\n		DECLARE coscodconV	VARCHAR(50);\r\n		DECLARE coscodcon2V	VARCHAR(50);\r\n		DECLARE CuentaD	VARCHAR(30);\r\n		DECLARE CuentaH	VARCHAR(30);		\r\n		DECLARE d INT DEFAULT 0;\r\n\r\n        DECLARE CurCont CURSOR FOR SELECT s.encrolcodigo, s.empcedula,s.empcodcon,s.empdepar , s.depcodcon , s.depcodcon2 ,s.coscodigo , \r\n		    s.rubcodigo ,s.rubextension,s.valor, s.codctad , s.rubcodctad , s.codctah , s.rubcodctah ,COALESCE(tbfnxncostos.coscodcon,\'\'),COALESCE(tbfnxncostos.coscodcon2,\'\') \r\n        FROM (SELECT @iFnxValorRol:=RolEncForm  p) param,v_fnxncurcont s \r\n        LEFT JOIN tbfnxncostos ON tbfnxncostos.coscodigo=s.coscodigo;\r\n\r\n		DECLARE CONTINUE HANDLER FOR SQLSTATE \'02000\' SET d = 1;\r\n    \r\n        SET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;\r\n		START TRANSACTION;\r\n\r\n		TRUNCATE TABLE tbfnxnasiento;  \r\n		OPEN CurCont;\r\n        FETCH CurCont INTO EncrolcodigoV,empcedulaV,empcodconV,empdeparV,depcodconV,depcodcon2V,coscodigoV,rubcodigoV,rubextensionV,valorV,codctadV,rubcodctadV,codctahV,rubcodctahV,coscodconV,coscodcon2V;\r\n\r\n        WHILE NOT d DO\r\n            SET CuentaD=\'\';\r\n            SET CuentaH=\'\';\r\n\r\n            IF codctadV=\'C\' THEN SET CuentaD = rubcodctadV; END IF;\r\n            IF codctadV=\'D\' THEN SET CuentaD = empdeparV; END IF;\r\n            IF codctadV=\'T\' THEN SET CuentaD = coscodconV; END IF;\r\n            IF codctadV=\'E\' THEN SET CuentaD = empcodconV; END IF;\r\n            IF codctadV=\'EE\' THEN SET CuentaD = CONCAT(RubcodctadV,\'.\',empcodconV); END IF;\r\n            IF codctadV=\'DE\' THEN SET CuentaD = CONCAT(depcodconV,\'.\',rubextensionV); END IF;\r\n            IF codctadV=\'TE\' THEN SET CuentaD = CONCAT(coscodconV,\'.\',rubextensionV); END IF;\r\n            IF codctadV=\'D2\' THEN SET CuentaD = CONCAT(depcodcon2V,\'.\',rubextensionV); END IF;\r\n            IF codctadV=\'T2\' THEN SET CuentaD = CONCAT(coscodcon2V,\'.\',rubextensionV); END IF;\r\n            IF codctadV=\'DI\' THEN SET CuentaD = \'\'; END IF;\r\n            IF codctadV=\'CI\' THEN SET CuentaD = (SELECT COALESCE(crctactble, \'\') AS crctactble FROM tbfnxnctasrubros where rubcodigo = rubcodigoV and crcendep = coscodigoV); END IF;\r\n\r\n            INSERT INTO tbfnxnAsiento(Encrolcodigo,empcedula,rubcodigo,valor,tipo,empdepar,coscodigo,codigocon) \r\n            VALUES (EncrolcodigoV,empcedulaV,rubcodigoV,valorV,\'D\',empdeparV,coscodigoV,CuentaD);\r\n\r\n            IF codctahV=\'C\' THEN SET CuentaH = rubcodctahV; END IF;\r\n            IF codctahV=\'D\' THEN SET CuentaH = empdeparV; END IF;\r\n            IF codctahV=\'T\' THEN SET CuentaH = coscodconV; END IF;\r\n            IF codctahV=\'E\' THEN SET CuentaH = empcodconV; END IF;\r\n            IF codctahV=\'EE\' THEN SET CuentaH = CONCAT(RubcodctahV,\'.\',empcodconV); END IF;\r\n            IF codctahV=\'DE\' THEN SET CuentaH = CONCAT(depcodconV,\'.\',rubextensionV); END IF;\r\n            IF codctahV=\'TE\' THEN SET CuentaH = CONCAT(coscodconV,\'.\',rubextensionV); END IF;\r\n            IF codctahV=\'D2\' THEN SET CuentaH = CONCAT(depcodcon2V,\'.\',rubextensionV); END IF;\r\n            IF codctahV=\'T2\' THEN SET CuentaH = CONCAT(coscodcon2V,\'.\',rubextensionV); END IF;\r\n            IF codctahV=\'DI\' THEN SET CuentaH = \'\'; END IF;\r\n            IF codctahV=\'CI\' THEN SET CuentaH = (SELECT COALESCE(crctactble, \'\') AS crctactble FROM tbfnxnctasrubros where rubcodigo = rubcodigoV and crcendep = coscodigoV); END IF;\r\n\r\n            INSERT INTO tbfnxnAsiento(Encrolcodigo,empcedula,rubcodigo,valor,tipo,empdepar,coscodigo,codigocon) \r\n            VALUES (EncrolcodigoV,empcedulaV,rubcodigoV,valorV*(-1),\'H\',empdeparV,coscodigoV,CuentaH);\r\n\r\n            FETCH CurCont INTO EncrolcodigoV,empcedulaV,empcodconV,empdeparV,depcodconV,depcodcon2V,coscodigoV,rubcodigoV,rubextensionV,valorV,codctadV,rubcodctadV,codctahV,rubcodctahV,coscodconV,coscodcon2V;\r\n		END WHILE; \r\n\r\n		CLOSE CurCont;\r\n		COMMIT;\r\n\r\n    	SELECT nombres,rubdescrip,valor as valor,tbfnxnhisrubros.rubcodigo,codigocon\r\n        FROM tbfnxnasiento,tbfnxnhisrubros,tbfnxnhisempleado\r\n        WHERE tbfnxnhisrubros.rubcodigo=tbfnxnasiento.rubcodigo AND LENGTH(TRIM(tbfnxnAsiento.codigocon))=0\r\n        AND tbfnxnhisempleado.empcedula=tbfnxnasiento.empcedula\r\n        AND tbfnxnhisempleado.encrolcodigo=RolEncForm \r\n		AND tbfnxnhisrubros.encrolcodigo=RolEncForm \r\n        AND tbfnxnhisrubros.encrolcodigo=tbfnxnhisempleado.encrolcodigo\r\n        ORDER BY nombres, valor DESC;\r\n\r\nEND ;', NULL, 1, NULL, 'dfalconi', NULL, '', 'Nómina'),
