CREATE DEFINER=`ads`@`localhost` PROCEDURE `Fnxsp_NCalcularImpuesto` (IN `periodo` VARCHAR(4))  SQL SECURITY INVOKER BEGIN
	
declare fBasica decimal(23,8);
declare rIMPFRACBASICA DECIMAL(23,8);
DECLARE rIMPFRACXCEDENTE decimal(23,8);
DECLARE rCALFRACEXE decimal(23,8);
DECLARE rIMPUESTXCENTAJE decimal(23,8);
declare imtotal decimal(23,8);
