			WHERE tbfnxnsri107xempleado.sri107empcedula= CEDULA AND tbfnxnsri107xempleado.sri107codigo=codigo and  tbfnxnsri107xempleado.sri107periodo=periodo;
		FETCH C_MAXIMPUESTO INTO cedula,valor;
		END WHILE;
	CLOSE C_MAXIMPUESTO;
END$$

