CREATE DEFINER=`root`@`localhost` PROCEDURE `Fnxsp_NCargarAnticipos` (IN `RolEncForm` VARCHAR(10))  SQL SECURITY INVOKER BEGIN			 
	DECLARE rubroV VARCHAR(10);
	DECLARE d INT DEFAULT 0;

	DECLARE CurCont CURSOR FOR SELECT rubro FROM tbfnxnanticipo
	WHERE estado=1 AND (encrolcodigo=RolEncForm OR Length(TRIM(COALESCE(encrolcodigo,'')))=0 )
	GROUP by rubro;

		DECLARE CONTINUE HANDLER FOR SQLSTATE '02000' SET d = 1;
    SET SESSION TRANSACTION ISOLATION LEVEL SERIALIZABLE;
		START TRANSACTION;
		
	OPEN CurCont;
    FETCH CurCont INTO rubroV;
	
    WHILE NOT d DO
	
		UPDATE tbfnxndetallerol SET valor=0 WHERE encrolcodigo=RolEncForm AND rubcodigo=rubroV;	
		FETCH CurCont INTO rubroV;		
	END WHILE; 
	
	CLOSE CurCont;
	
	UPDATE tbfnxnanticipo SET encrolcodigo='' WHERE encrolcodigo=RolEncForm;

	UPDATE tbfnxndetallerol AS dr, tbfnxnanticipo AS an 
	SET dr.valor=(SELECT Sum(valor)*(-1) AS valor
	FROM tbfnxnanticipo AS an 
	WHERE estado=1 AND (encrolcodigo=RolEncForm OR Length(TRIM(COALESCE(encrolcodigo,'')))=0 ) 
	AND an.cedula= dr.empcedula
	GROUP by cedula,encrolcodigo,rubro)
	WHERE dr.encrolcodigo=RolEncForm AND an.rubro= dr.rubcodigo;

	UPDATE tbfnxnanticipo SET encrolcodigo=RolEncForm WHERE estado=1 AND LENGTH(COALESCE(encrolcodigo))=0 ; 
	
	COMMIT;

		
END$$

