	DECLARE da,di,ma,mi,aa,ai,d, m,na int;

	 SET da =DAY(PdFecharol);
	 SET di=DAY(PdFechaing);
	 SET ma =MONTH(PdFecharol);
	 SET mi=MONTH(PdFechaing);
	 SET aa=YEAR(PdFecharol);
	 SET ai=YEAR(PdFechaing);
	
	IF da<di THEN	
		SET	ma=ma-1;
	END IF;	
	IF ma<mi THEN	
